INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (54763, 0, 0, 'I don\'t know who you are, but you have my gratitude.', 0, 12, 0, 100, 0, 0, 0, 53145, 'Raufen - Nectarbreeze Farmer Rescue talk');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (54763, 0, 1, 'I never thought something like this could happen here...', 0, 12, 0, 100, 0, 0, 0, 58475, 'Raufen - Nectarbreeze Farmer Rescue talk');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (54763, 0, 2, 'I thought I was done for!', 0, 12, 0, 100, 0, 0, 0, 53546, 'Raufen - Nectarbreeze Farmer Rescue talk');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (54763, 0, 3, 'You don\'t have to tell me twice!', 0, 12, 0, 100, 0, 0, 0, 53543, 'Raufen - Nectarbreeze Farmer Rescue talk');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (54915, 0, 0, '$n, you have no idea how glad I am to see you. Supervising is hungry work.', 0, 12, 0, 100, 0, 0, 0, 53413, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (54917, 0, 0, 'Join the students in the block formation below. Then follow the leader, as they do.', 0, 12, 0, 100, 0, 0, 0, 53581, 'Instructor Xann');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (54918, 0, 0, '', 0, 12, 0, 100, 22, 0, 0, 53579, '');
