INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (54925, 0, 0, 'Very well!', 0, 14, 0, 100, 0, 0, 0, 53212, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (54925, 1, 0, 'I knew I would lose. The rivers told me so.', 0, 12, 0, 100, 0, 0, 0, 53213, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (54926, 0, 0, 'Fine, then. It\'s time for an open-faced $R sandwich!', 0, 14, 0, 100, 0, 0, 0, 53219, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (54926, 1, 0, 'You win... this time. I\'d better get back to my size training.', 0, 12, 0, 100, 0, 0, 0, 53220, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (54960, 0, 0, 'Stranger, have you ever seen a stone fall into a calm lake? Every action creates a ripple, even accepting a pledge of friendship. I shall think upon your request.', 0, 12, 0, 100, 0, 0, 0, 58066, 'Elder Lusshan to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55009, 0, 0, 'Over here, $n!', 0, 12, 0, 100, 0, 0, 0, 54614, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55084, 0, 0, 'Mogu ambushers descend from the trees. Look out!', 0, 42, 0, 100, 0, 0, 37666, 53394, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55084, 1, 0, 'Look at what we have here! Another straggler from the orchard...', 0, 12, 0, 100, 0, 0, 0, 53395, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55092, 0, 0, '', 0, 12, 0, 100, 0, 0, 0, 53420, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55196, 0, 0, 'Your friend put up quite the fight. He nearly escaped, but now he is a prisoner, just like the rest of us.', 0, 12, 0, 100, 0, 0, 30637, 57950, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55201, 0, 0, 'Are you friends with Admiral Taylor? If so, then I must test your strength in battle later.', 0, 12, 0, 100, 0, 0, 0, 53948, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55201, 0, 1, 'The water spirits have answered my prayers. Thank you.', 0, 12, 0, 100, 0, 0, 0, 53949, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55201, 0, 2, 'I had given up hope, friend. Thank you.', 0, 12, 0, 100, 0, 0, 0, 53950, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55201, 0, 3, 'Do my eyes deceive me? Am I truly free?', 0, 12, 0, 100, 0, 0, 0, 53951, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55201, 0, 4, 'I live another day, thanks to you.', 0, 12, 0, 100, 0, 0, 0, 53952, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55267, 0, 0, 'I tried to boot a jade monster in the head but hurt my paw.', 0, 12, 0, 100, 2, 0, 0, 0, 'Scared Pandaren Cub to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55267, 0, 1, 'Thank you for saving me!', 0, 12, 0, 100, 2, 0, 0, 0, 'Scared Pandaren Cub to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55267, 0, 2, 'It was An! She dared Shin to go knock on the Jade Witch\'s door.', 0, 12, 0, 100, 2, 0, 0, 0, 'Scared Pandaren Cub to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55368, 0, 0, 'You should join our family, preserved forever in jade.', 0, 12, 0, 100, 1, 0, 0, 0, 'Widow Greenpaw to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55490, 0, 0, 'Are you part of the rescue team? Are we saved?', 0, 12, 0, 100, 0, 0, 0, 53953, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55490, 0, 1, 'I knew someone would come for us. Thank you, $c!', 0, 12, 0, 100, 0, 0, 0, 53954, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55490, 0, 2, 'Thank you, $c. I can get back to camp on my own.', 0, 12, 0, 100, 0, 0, 0, 53955, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55490, 0, 3, 'They\'ve got the Admiral locked up like an animal. Please save him too.', 0, 12, 0, 100, 0, 0, 0, 53956, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55490, 0, 4, 'The hozen revel in torturing their prisoners. Thank you, $c, for saving me from a dire fate.', 0, 12, 0, 100, 0, 0, 0, 53957, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55597, 0, 0, 'Na Lek imbues $p with gratitude and returns to the waters of the pool.', 0, 16, 0, 100, 0, 0, 0, 56967, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (55768, 0, 0, 'Ah, a visitor! This is an incredible time for Pandaria - the mists have parted, and we rejoin the world. You come in peace, I hope?', 0, 12, 0, 100, 0, 0, 30064, 62621, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56114, 0, 0, 'Yeah, I tried to hop through that thing too.', 0, 12, 0, 100, 0, 0, 0, 58026, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56114, 0, 1, 'Once the trap is sprung no one gets in or out until the people trapped are completely drained of life. Mogu are evil.', 0, 12, 0, 100, 0, 0, 0, 58027, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56114, 0, 2, 'Careful. Get stuck in that and it will drain your life to power itself.', 0, 12, 0, 100, 0, 0, 0, 58028, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56114, 0, 3, 'I\'ve punched it, kicked it, smacked it, thwacked it. Nothing seems to break this oubliette.', 0, 12, 0, 100, 0, 0, 0, 58030, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56133, 0, 0, 'Very well, $n. I will need your help making this beer.', 0, 12, 0, 100, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56133, 1, 0, 'When I yell out the names of ingredients, you throw them in the pot...', 0, 12, 0, 100, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56133, 2, 0, 'I am kidding!', 0, 14, 0, 100, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56133, 3, 0, 'I am the best brewer across three continents! I think I can handle this part.', 0, 12, 0, 100, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56133, 4, 0, 'If you wouldn\'t mind waiting, $n... this might take a long time.', 0, 12, 0, 100, 0, 0, 0, 0, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56439, 0, 0, 'Die or surrender. You cannot defeat me.', 0, 14, 0, 100, 0, 0, 28273, 62908, 'Sha of Doubt');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56439, 1, 0, 'You cannot escape me. I am... in... every...  breath...', 0, 14, 0, 100, 0, 0, 28274, 62912, 'Sha of Doubt');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56439, 2, 0, 'See how effortlessly you become unraveled.', 0, 14, 0, 100, 0, 0, 28275, 62913, 'Sha of Doubt');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56439, 2, 1, 'Succumb to the darkness inside your soul.', 0, 14, 0, 100, 0, 0, 28276, 62914, 'Sha of Doubt');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56439, 3, 0, 'All is lost.', 0, 14, 0, 100, 0, 0, 28277, 62909, 'Sha of Doubt');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56439, 4, 0, 'Abandon all hope.', 0, 14, 0, 100, 0, 0, 28278, 62910, 'Sha of Doubt');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56439, 4, 1, 'Embrace your despair.', 0, 14, 0, 100, 0, 0, 28279, 62911, 'Sha of Doubt');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56448, 0, 0, 'The water reacts violently to your touch!', 0, 41, 0, 100, 0, 0, 0, 59470, 'Wise Mari');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56448, 1, 0, 'You dare to disturb these waters? You will drown!', 0, 12, 0, 100, 0, 0, 28257, 55118, 'Wise Mari');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56448, 2, 0, 'Rise!', 0, 12, 0, 100, 0, 0, 28260, 62958, 'Wise Mari');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56448, 2, 1, 'Spring to life!', 0, 12, 0, 100, 0, 0, 28258, 55606, 'Wise Mari');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56448, 2, 2, 'Living water, bring them death!', 0, 12, 0, 100, 0, 0, 28261, 62960, 'Wise Mari');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56448, 2, 3, 'Do not run, it only delays the inevitable.', 0, 12, 0, 100, 0, 0, 28269, 59474, 'Wise Mari');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56448, 2, 4, 'Come in, the water\'s fine!', 0, 12, 0, 100, 0, 0, 28270, 59475, 'Wise Mari');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56448, 2, 5, 'Let death wash over you!', 0, 12, 0, 100, 0, 0, 28271, 59476, 'Wise Mari');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56448, 3, 0, 'Behold the power of torrential waters!', 0, 12, 0, 100, 0, 0, 28266, 55605, 'Wise Mari');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56448, 4, 0, '%s draws from the surrounding water!', 0, 41, 0, 100, 0, 0, 0, 59456, 'Wise Mari');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56448, 5, 0, 'The darkness clears...', 0, 12, 0, 100, 0, 0, 28262, 55719, 'Wise Mari');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56448, 6, 0, 'I peer into these waters and my reflection is empty...', 0, 12, 0, 100, 0, 0, 28263, 55720, 'Wise Mari');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56448, 7, 0, 'Waters... claim me...', 0, 12, 0, 100, 0, 0, 28264, 55721, 'Wise Mari');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56448, 8, 0, 'Your cause is hopeless.', 0, 12, 0, 100, 0, 0, 28268, 62989, 'Wise Mari');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56448, 8, 1, 'As the waters foretold.', 0, 12, 0, 100, 0, 0, 28267, 62988, 'Wise Mari');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56473, 0, 0, '', 0, 41, 0, 100, 0, 0, 0, 65524, 'Flying Snow');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56485, 0, 0, 'I am sorry, but I cannot go back just yet.', 0, 12, 0, 100, 0, 0, 27573, 55159, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56485, 1, 0, 'I\'ve decided to search for the Vale. I must find the Sacred Pools and study their healing powers.', 0, 12, 0, 100, 0, 0, 27574, 55160, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56485, 2, 0, 'I\'m sorry, but I won\'t. I\'m sure you understand my reasons.', 0, 12, 0, 100, 0, 0, 27575, 55163, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56485, 3, 0, 'Come on, let\'s go.', 0, 12, 0, 100, 0, 0, 27576, 55165, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56488, 0, 0, 'Prince Anduin, we\'ve come to take ye home.', 0, 12, 0, 100, 0, 0, 27841, 55161, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56488, 1, 0, 'Come quietly, now.', 0, 12, 0, 100, 0, 0, 27842, 55162, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56488, 2, 0, 'Eh? Oh...uh...you\'re absolutely right, Prince Anduin. You are free to go as you please.', 0, 12, 0, 100, 0, 0, 27843, 55164, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56488, 3, 0, 'Eh? What happened?', 0, 12, 0, 100, 0, 0, 27844, 55167, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56488, 4, 0, 'Whaaa? I can\'t believe he did that ta me!', 0, 12, 0, 100, 0, 0, 27845, 55170, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56488, 5, 0, 'Cheeky little bugger! Didn\'t think he had it in him!', 0, 12, 0, 100, 0, 0, 27846, 58214, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56503, 0, 0, 'Master Shang has trained you well. Thank you, friend!', 0, 12, 0, 100, 2, 0, 0, 53265, 'Min Dimwind');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56503, 1, 0, 'I couldn\'t have fought them off alone. Now, if you\'ll excuse me, I should go find my cart.', 0, 12, 0, 100, 1, 0, 0, 56452, 'Min Dimwind');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56503, 2, 0, 'Cart!', 0, 14, 0, 100, 22, 0, 0, 56453, 'Min Dimwind');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56503, 3, 0, 'Hello, cart. Still upside-downed, I see.', 0, 12, 0, 100, 1, 0, 0, 56454, 'Min Dimwind');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56511, 0, 0, '%s rises from the fountain!', 0, 41, 0, 100, 0, 0, 0, 55607, 'Corrupt Living Water');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56541, 0, 0, 'You!  You have allowed the Sha to reawaken after all these years!', 0, 14, 0, 100, 0, 0, 27626, 55307, 'Master Snowdrift');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56541, 1, 0, 'If you truly wish to undo what your kind have wrought upon our land you must first prove yourselves here in our school.', 0, 14, 0, 100, 0, 0, 27627, 55308, 'Master Snowdrift');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56541, 2, 0, 'First, you will face our initiates...', 0, 14, 0, 100, 0, 0, 27632, 55309, 'Master Snowdrift');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56541, 3, 0, 'You have bested our most junior of students.  Now you will face two of my most senior.', 0, 14, 0, 100, 0, 0, 27631, 55312, 'Master Snowdrift');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56541, 4, 0, 'Flying Snow!  Fragrant Lotus!  Step forward!', 0, 14, 0, 100, 0, 0, 27636, 55313, 'Master Snowdrift');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56541, 5, 0, 'You have bested my prize students. Perhaps you can be of use to us after all...', 0, 14, 0, 100, 0, 0, 27633, 55314, 'Master Snowdrift');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56541, 6, 0, 'As my master once said, "You cannot truly know someone until you fight them."', 0, 14, 0, 100, 0, 0, 27634, 55315, 'Master Snowdrift');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56541, 7, 0, 'Come forward, so that we may be properly introduced.', 0, 14, 0, 100, 0, 0, 27635, 59404, 'Master Snowdrift');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56541, 8, 0, 'Very well then, outsiders. Let us see your true strength.', 0, 14, 0, 100, 0, 0, 27628, 55828, 'Master Snowdrift');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56541, 9, 0, 'I am bested.  Give me a moment and we will venture forth together to face the Sha.', 0, 14, 0, 100, 0, 0, 27623, 55832, 'Master Snowdrift');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56541, 10, 0, 'You need more training!', 0, 14, 0, 100, 0, 0, 27637, 63335, 'Master Snowdrift');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56541, 10, 1, 'Your technique needs work.', 0, 14, 0, 100, 0, 0, 27638, 63336, 'Master Snowdrift');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56541, 11, 0, 'Skill with a weapon is but a physical manifestation of one\'s martial strength. Let me show you what spiritual power is!', 0, 14, 0, 100, 0, 0, 27629, 55829, 'Master Snowdrift');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56541, 12, 0, 'When I was but a cub I could scarcely throw a punch, but after years of training I can do so much more!', 0, 14, 0, 100, 0, 0, 27630, 55830, 'Master Snowdrift');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56541, 13, 0, 'Where one falls, another will rise to take their place.', 0, 14, 0, 100, 0, 0, 27624, 55310, 'Master Snowdrift');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56541, 13, 1, 'You have much to learn if you wish to defend your people from the Sha initiate.  Stand aside for your fellow students!', 0, 14, 0, 100, 0, 0, 27625, 55311, 'Master Snowdrift');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56589, 0, 0, 'Keep those archers off the wall. Raigonn must reach the gate.', 0, 14, 0, 0, 0, 0, 29377, 66014, 'boss_striker_gadok');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56589, 1, 0, 'I\'ll hurl your corpses from this tower.', 0, 14, 0, 0, 0, 0, 29375, 62888, 'boss_striker_gadok');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56589, 2, 0, 'Our numbers... endless! You... cannot win...', 0, 14, 0, 0, 0, 0, 29376, 62889, 'boss_striker_gadok');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56589, 3, 0, 'You joined a battle already lost.', 0, 14, 0, 0, 0, 0, 29378, 62899, 'boss_striker_gadok');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56589, 3, 1, 'Air superiority.', 0, 14, 0, 0, 0, 0, 29379, 62902, 'boss_striker_gadok');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56589, 3, 2, 'As expected.', 0, 14, 0, 0, 0, 0, 29380, 62903, 'boss_striker_gadok');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56636, 0, 0, 'There will be no escaping me.', 0, 14, 0, 0, 0, 0, 29074, 62883, 'boss_commander_rimok');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56636, 1, 0, 'Empress... forgive me!', 0, 14, 0, 0, 0, 0, 29075, 62884, 'boss_commander_rimok');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56636, 2, 0, 'The gates are about to fall. Quickly! Slaughter these meddlers!', 0, 14, 0, 0, 0, 0, 29076, 59340, 'boss_commander_rimok');
