INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56637, 4, 0, 'Ook! Oooook!!', 0, 14, 0, 100, 0, 0, 28797, 59253, 'Ook-Ook to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56660, 6, 0, 'And those aren\'t pandaren down there.  They\'ve got no fur.', 0, 12, 0, 100, 5, 0, 27303, 55579, 'Ji Firepaw');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56660, 5, 0, 'Are you seeing what I\'m seeing?!  Is that a boat?!', 0, 12, 0, 100, 5, 0, 27302, 55577, 'Ji Firepaw');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56660, 3, 0, 'A thorn?  And I left my tweezers at home.', 0, 12, 0, 100, 6, 0, 27300, 55574, 'Ji Firepaw');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56660, 4, 0, 'How could such a thing cause pain to something so large?', 0, 12, 0, 100, 6, 0, 27301, 55575, 'Ji Firepaw');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56660, 2, 0, 'When am I not respectful?  You hurt me, Aysa.', 0, 12, 0, 100, 6, 0, 27299, 55561, 'Ji Firepaw');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56660, 1, 0, 'Bah, let a pandaren hope, would you?  I\'m going to miss the old man.', 0, 12, 0, 100, 1, 0, 27298, 55556, 'Ji Firepaw');
