INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56719, 0, 0, 'I will not be caged again. These Shado-Pan could not stop me. Neither shall you!', 0, 14, 0, 100, 0, 0, 28346, 63337, 'Sha of Violence');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56719, 1, 0, 'So long as violence lurks in your hearts... I... will... return...', 0, 14, 0, 100, 0, 0, 28347, 63338, 'Sha of Violence');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56719, 2, 0, 'Another violent death...', 0, 14, 0, 100, 0, 0, 28348, 63339, 'Sha of Violence');
