INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56742, 0, 0, 'We\'ve got to light that signal fire. Without reinforcements, they\'ll destroy the gate!', 0, 12, 0, 0, 5, 0, 29068, 55755, 'Serpent\'s Spine Defender');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56743, 0, 0, 'But the mantid have overrun the area. There\'s no clear path!', 0, 12, 0, 0, 1, 0, 29071, 55756, 'Serpent\'s Spine Defender');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56745, 0, 0, 'Don\'t worry! I can make it!', 0, 12, 0, 0, 0, 0, 27692, 55757, 'Serpent\'s Spine Trainee');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56747, 0, 0, 'Let me show you my power.', 0, 12, 0, 100, 0, 0, 28317, 59297, 'Gu Cloudstrike');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56747, 1, 0, 'Come to me, serpent!  Together we will destroy these intruders.', 0, 12, 0, 100, 0, 0, 28318, 59298, 'Gu Cloudstrike');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56747, 2, 0, 'Even together... we failed...', 0, 14, 0, 100, 0, 0, 28319, 66226, 'Gu Cloudstrike');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56747, 3, 0, 'Their energy is strong!', 0, 14, 0, 100, 0, 0, 28320, 58683, 'Gu Cloudstrike');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56747, 4, 0, 'No, no! Do not fail me!', 0, 14, 0, 100, 0, 0, 28321, 58684, 'Gu Cloudstrike');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56747, 5, 0, 'Weaklings... let me make room for our guests.', 0, 14, 0, 100, 0, 0, 28322, 59289, 'Gu Cloudstrike');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56747, 6, 0, 'Approach... your death awaits.', 0, 14, 0, 100, 0, 0, 28323, 59290, 'Gu Cloudstrike');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56747, 7, 0, 'Serpent, charge me with your power!', 0, 12, 0, 100, 0, 0, 28324, 58682, 'Gu Cloudstrike');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56747, 8, 0, 'Weakling!', 0, 14, 0, 100, 0, 0, 28325, 66227, 'Gu Cloudstrike');
