INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56762, 0, 0, 'Be free of doubt, priestess...', 0, 14, 0, 100, 0, 0, 28559, 55909, 'Yu\'lon');
