INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56782, 0, 0, 'Hey, stranger! Pssst! Come over here!', 0, 12, 0, 100, 0, 0, 0, 0, 'Rain-Zhu to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56838, 0, 0, '', 0, 12, 0, 100, 396, 0, 0, 59397, 'Shokia');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56840, 0, 0, 'Nazgrim gonna be real happy with Riko! Here! Report is done!', 0, 12, 0, 100, 396, 0, 0, 59398, 'Riko');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56841, 0, 0, '', 0, 12, 0, 100, 397, 0, 0, 59392, 'Shademaster Kiryn');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56843, 0, 0, 'You there! In the rafters! My sacred library has become possessed by the Sha!', 0, 14, 0, 100, 0, 0, 30887, 57464, 'Lorewalker Stonestep');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56843, 1, 0, 'See how they pour from every letter of these sacred scrolls... corrupting them! Defiling them!', 0, 14, 0, 100, 0, 0, 30888, 57465, 'Lorewalker Stonestep');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56843, 2, 0, 'I beseech you, kind strangers. Cleanse this place of their foul presence! Lay our stories to rest!', 0, 14, 0, 100, 0, 0, 30889, 57604, 'Lorewalker Stonestep');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56843, 3, 0, 'You must destroy the source of the corruption! Quickly, before our cherished history is erased forever!', 0, 14, 0, 100, 0, 0, 30890, 57467, 'Lorewalker Stonestep');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56843, 4, 0, 'Oh, my. If I am not mistaken, it appears that the tale of Zao Sunseeker has come to life before us.', 0, 14, 0, 100, 0, 0, 30901, 57605, 'Lorewalker Stonestep');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56843, 5, 0, 'According to the legend, long ago, there were five suns that burned brightly in the sky. When the winds blew, their intense heat caused great wildfires.', 0, 14, 0, 100, 0, 0, 30902, 57607, 'Lorewalker Stonestep');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56843, 6, 0, 'Crops wilted, villages burned, and families wandered endlessly to seek refuge from the flames...', 0, 14, 0, 100, 0, 0, 30903, 57608, 'Lorewalker Stonestep');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56843, 7, 0, 'The fires, they are burning the library! Please, heroes! Purge this evil before the temple crumbles to ash!', 0, 14, 0, 100, 0, 0, 30904, 57610, 'Lorewalker Stonestep');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56843, 8, 0, 'Zao has become corrupted too! Please, fight him with dignity...', 0, 14, 0, 100, 0, 0, 30906, 57746, 'Lorewalker Stonestep');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56843, 9, 0, 'Ah, it is not yet over. From what I see, we face the trial of the yaungol. Let me shed some light...', 0, 14, 0, 100, 0, 0, 30896, 57614, 'Lorewalker Stonestep');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56843, 10, 0, 'As the tale goes, the yaungol was traveling across the Kun\'lai plains when suddenly he was ambushed by two strange creatures!', 0, 14, 0, 100, 0, 0, 30897, 57986, 'Lorewalker Stonestep');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56843, 11, 0, 'The yaungol attacked the beasts with great determination and ferocity. Yet, the more he attacked, the more fearsome the beasts grew!', 0, 14, 0, 100, 0, 0, 30898, 57988, 'Lorewalker Stonestep');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56843, 12, 0, 'However, when he let it alone, the beast shrank to near nothingness.', 0, 14, 0, 100, 0, 0, 30899, 57989, 'Lorewalker Stonestep');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56843, 13, 0, 'For you see, strife feeds on conflict. When you have peace in your heart, strife will find somewhere else to roam.', 0, 14, 0, 100, 0, 0, 30900, 57990, 'Lorewalker Stonestep');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56877, 0, 0, '%s\'s |cFFFF0000Weak Spot|r is exposed! Attack it to break his Impervious Carapace!', 0, 41, 0, 100, 0, 0, 0, 57628, 'boss_raigonn');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56877, 1, 0, 'The |cFFFE9A2EKrik\'thik Swarm Bringer|r reinforcements join the battle!', 0, 41, 0, 100, 0, 0, 0, 57603, 'boss_raigonn');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56877, 2, 0, 'The |cFFFE9A2EKrik\'thik Engulfer|r reinforcements appear above!', 0, 41, 0, 100, 0, 0, 0, 57602, 'boss_raigonn');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56877, 3, 0, 'The |cFFFE9A2EKrik\'thik Protectorate|r reinforcements appear!', 0, 41, 0, 100, 0, 0, 0, 57601, 'boss_raigonn');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56884, 0, 0, 'Taran Zhu and the Shado-Pan are MINE. Come forward and die by my hand, as all who oppose me shall in the days to come!', 0, 14, 0, 100, 0, 0, 28334, 63341, 'Corrupted Taran Zhu');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56884, 1, 0, 'Hatred will consume and conquer all!', 0, 14, 0, 100, 0, 0, 28332, 63342, 'Corrupted Taran Zhu');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56884, 2, 0, 'No... NO!', 0, 14, 0, 100, 0, 0, 28333, 63343, 'Corrupted Taran Zhu');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56884, 3, 0, 'Rage at your defeat.', 0, 14, 0, 100, 0, 0, 28335, 63344, 'Corrupted Taran Zhu');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56884, 4, 0, 'The Sha of Hatred has fled my body... and the monastery, as well. I must thank you, strangers. The Shado-Pan are in your debt. Now, there is much work to be done...', 0, 14, 0, 100, 0, 0, 28423, 63340, 'Corrupted Taran Zhu');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56906, 0, 0, 'Gate?! I\'m going to bring this whole wall down!', 0, 14, 0, 0, 0, 0, 29341, 55896, 'boss_saboteur_kiptilak - SAY_INTRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56906, 1, 0, 'I brought enough bombs... for all of you!', 0, 14, 0, 0, 0, 0, 29339, 63323, 'boss_saboteur_kiptilak - SAY_AGGRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56906, 2, 0, 'BOOM shakalaka!', 0, 14, 0, 0, 0, 0, 29342, 55902, 'boss_saboteur_kiptilak - SAY_SLAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56906, 2, 1, 'I\'m the bomber man...tid!', 0, 14, 0, 0, 0, 0, 29343, 63329, 'boss_saboteur_kiptilak - SAY_SLAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56906, 2, 2, 'You blew it!', 0, 14, 0, 0, 0, 0, 29344, 63330, 'boss_saboteur_kiptilak - SAY_SLAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56906, 3, 0, 'I... blew it...', 0, 14, 0, 0, 0, 0, 29340, 63325, 'boss_saboteur_kiptilak - SAY_DEATH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56906, 4, 0, 'You\'re the bomb!', 0, 14, 0, 0, 0, 0, 29345, 55899, 'boss_saboteur_kiptilak - SAY_SPECIAL');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56906, 4, 1, 'This will be the bomb.', 0, 14, 0, 0, 0, 0, 29346, 55897, 'boss_saboteur_kiptilak - SAY_SPECIAL');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56906, 4, 2, 'Things that make you go... BOOM!', 0, 14, 0, 0, 0, 0, 29347, 55900, 'boss_saboteur_kiptilak - SAY_SPECIAL');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56906, 4, 3, 'BOOM! Explosion! Overpowering!', 0, 14, 0, 0, 0, 0, 29348, 55901, 'boss_saboteur_kiptilak - SAY_SPECIAL');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (56906, 4, 4, 'There goes the BOOM!', 0, 14, 0, 0, 0, 0, 29349, 56894, 'boss_saboteur_kiptilak - SAY_SPECIAL');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57120, 0, 0, 'Oh, I don\'t know. I am old, and I have always...', 0, 12, 0, 100, 0, 0, 0, 55924, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57120, 1, 0, 'Look out! Mantid!', 0, 14, 0, 100, 0, 0, 0, 55925, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57120, 2, 0, 'I\'ve reconsidered... it\'s too dangerous to stay here. No piece of land is worth losing your life over. Let me see those evacuation orders.', 0, 12, 0, 100, 0, 0, 0, 55952, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57121, 0, 0, 'That\'s right. Now please, $r, leave and let us defend our property.', 0, 12, 0, 100, 0, 0, 0, 55931, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57122, 0, 0, '', 0, 12, 0, 100, 0, 0, 0, 55929, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57122, 1, 0, '', 0, 12, 0, 100, 0, 0, 0, 55930, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57155, 0, 0, 'I feared that this day would come. I will pack up my things.', 0, 12, 0, 100, 0, 0, 0, 55926, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57161, 0, 0, 'I see. I\'d hoped that we could wait out this invasion, but if the authorities want us to leave, we will leave.', 0, 12, 0, 100, 0, 0, 0, 55934, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57161, 1, 0, 'Ying, Huizhong... gather your things. We\'re leaving the farm until it\'s safe.', 0, 12, 0, 100, 0, 0, 0, 55935, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57161, 2, 0, 'I don\'t know, dear.', 0, 12, 0, 100, 0, 0, 0, 55938, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57161, 3, 0, 'Are you?', 0, 12, 0, 100, 0, 0, 0, 55939, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57162, 0, 0, '', 0, 12, 0, 100, 0, 0, 0, 55936, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57163, 0, 0, '', 0, 12, 0, 100, 0, 0, 0, 55937, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57183, 0, 0, '%s sighs.', 0, 16, 0, 100, 0, 0, 0, 55942, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57183, 1, 0, 'I am a farmer, $c. It\'s all I know.', 0, 12, 0, 100, 0, 0, 0, 55943, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57183, 2, 0, 'All we have is our land. And that commander wants us to give it up to the mantid?', 0, 12, 0, 100, 0, 0, 0, 55944, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57183, 3, 0, 'I will not do it. My grandfather knew what he was getting into when he first planted seed in the shadow of the Serpent\'s Spine.', 0, 12, 0, 100, 0, 0, 0, 55945, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57183, 4, 0, 'I have my wife, two strong daughters, and two strong sons. Thank you, but we can protect this farm ourselves.', 0, 12, 0, 100, 0, 0, 0, 55946, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57298, 0, 0, 'A radish for sweet little Ella? Well, I sure can spare one. Here you go.', 0, 12, 0, 100, 0, 0, 0, 65907, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57319, 0, 0, 'I keep hearing voices in my head. Should I be worried?', 0, 15, 0, 100, 0, 0, 0, 56762, 'Emulates player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57319, 0, 1, 'Why do I try so hard? It\'s all meaningless in the end...', 0, 15, 0, 100, 0, 0, 0, 56766, 'Emulates player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57319, 0, 2, 'Does this armor look a little tight on me?', 0, 15, 0, 100, 0, 0, 0, 56759, 'Emulates player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57319, 0, 3, 'Everyone around me only pretends to like me.', 0, 15, 0, 100, 0, 0, 0, 56758, 'Emulates player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57319, 0, 4, 'Nothing is ever going to change.', 0, 15, 0, 100, 0, 0, 0, 56763, 'Emulates player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57319, 0, 5, 'Of course my worst fears are going to come true.', 0, 15, 0, 100, 0, 0, 0, 56760, 'Emulates player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57319, 0, 6, 'What are people saying about me?', 0, 15, 0, 100, 0, 0, 0, 56761, 'Emulates player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57319, 0, 7, 'Why do I always feel so alone? Is it because...I\'m alone?', 0, 15, 0, 100, 0, 0, 0, 56767, 'Emulates player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57385, 0, 0, 'Would that I could, $c, but I have a bit of a weed problem. Say... you wouldn\'t be willing to help out, would you?', 0, 12, 0, 100, 0, 0, 0, 56248, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57402, 0, 0, 'Little Ella\'s trying her hand at brewing? Well, I\'ll be! I wish her luck. Brewing isn\'t something a novice can just take up, you know. Anyhow, I can spare a Lakemelon for her.', 0, 12, 0, 100, 0, 0, 0, 65908, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57638, 1, 0, 'Many have tested my claim, and I yet stand proven.', 0, 12, 0, 100, 2, 0, 0, 56334, 'Jojo Ironbrow');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57638, 0, 0, 'The reeds of the Singing Pools are the hardest in all of the land, but they are as air before my mighty brow.', 0, 12, 0, 100, 1, 0, 0, 56333, 'Jojo Ironbrow');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57669, 0, 0, 'Our crafters build the finest wooden planks.  They would weather the most brutal of storms.  But they are as water before the storm of my crushing skull.', 0, 12, 0, 100, 1, 0, 0, 56343, 'Jojo Ironbrow');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57669, 1, 0, 'Nothing made by pandaren hands can withstand me.', 0, 12, 0, 100, 2, 0, 0, 56344, 'Jojo Ironbrow');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57670, 1, 0, 'Any who yet doubt my strength are simply foolish.', 0, 12, 0, 100, 2, 0, 0, 56356, 'Jojo Ironbrow');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57670, 0, 0, 'These are the densest stones across all of Shen-zin Su, yet they are as nothing before my mighty head.', 0, 12, 0, 100, 1, 0, 0, 56355, 'Jojo Ironbrow');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57681, 0, 0, 'You have no idea how long I was in there.', 0, 12, 0, 100, 0, 0, 0, 56349, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57681, 0, 1, 'Thanks!', 0, 12, 0, 100, 0, 0, 0, 56347, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57681, 0, 2, 'Better get out of here before he comes by again!', 0, 12, 0, 100, 0, 0, 0, 56351, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57681, 0, 3, 'I suppose there are worse fates than being stuck forever inside a beer barrel.', 0, 12, 0, 100, 0, 0, 0, 56354, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57681, 0, 4, 'Ach! The light... it hurts our eyes...', 0, 12, 0, 100, 0, 0, 0, 56350, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57681, 0, 5, 'Finally!', 0, 12, 0, 100, 0, 0, 0, 56352, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57692, 0, 0, 'This is magnificent!  I\'ve never seen craftsmanship so fine.', 0, 12, 0, 100, 1, 0, 0, 56363, 'Jojo Ironbrow');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57692, 1, 0, 'There is only one thing to do.  I must smash it!  Once and for all, all shall know the power of my mighty brow.', 0, 12, 0, 100, 1, 0, 0, 56361, 'Jojo Ironbrow');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57692, 2, 0, 'Oh... my head... it hurts so badly!  I\'ve... the pillar must contain enormous power... perhaps stronger even than my brow...', 0, 12, 0, 100, 18, 0, 0, 56362, 'Jojo Ironbrow');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (57692, 3, 0, 'I\'m... I\'m just going to lie down a while... until this headache goes away.', 0, 12, 0, 100, 0, 0, 0, 56364, 'Jojo Ironbrow');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58202, 0, 0, 'Help!', 0, 14, 0, 100, 0, 0, 0, 485, 'Serpent\'s Spine Defender');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58202, 0, 1, 'Mmmphhh', 0, 14, 0, 100, 0, 0, 0, 66519, 'Serpent\'s Spine Defender');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58202, 0, 2, 'The mantid are coming over the wall!', 0, 14, 0, 100, 0, 0, 0, 66521, 'Serpent\'s Spine Defender');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58206, 0, 0, 'Look at that. The little guy left you some purified water.', 0, 12, 0, 100, 0, 0, 0, 56971, '');
