INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58816, 5, 0, 'There we go. Should you pass through Silvermoon City, come and say hello, hmmm? Al diel shala...', 0, 12, 0, 100, 0, 0, 31480, 64890, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58826, 0, 0, 'I can allow this no longer!', 0, 14, 0, 100, 0, 0, 27747, 57748, 'Zao Sunseeker');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58826, 1, 0, 'The blistering heat must have dulled my senses... mirages! All of you!', 0, 14, 0, 100, 0, 0, 27748, 57623, 'Zao Sunseeker');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58833, 0, 0, '', 0, 12, 0, 100, 0, 0, 0, 57918, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58840, 0, 0, '%s draws his sword and shield.', 0, 41, 0, 100, 0, 0, 0, 58795, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58840, 1, 0, '%s drops his shield and draws a dagger.', 0, 41, 0, 100, 0, 0, 0, 58797, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58870, 0, 0, 'The unthinkable has happened: Our siege on Theramore has failed.', 0, 12, 0, 100, 5, 0, 30642, 64678, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58870, 1, 0, 'You\'re the contingency plan. Your mission is to infiltrate Theramore Isle.', 0, 12, 0, 100, 1, 0, 30643, 64684, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58870, 2, 0, 'Burn their ships. Butcher their flight master. Sabotage their war machines. And rescue our spy!', 0, 12, 0, 100, 25, 0, 30644, 64826, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58875, 0, 0, 'My dear, it appears we have visitors!  Friends of yours, Voss?', 0, 14, 0, 100, 0, 0, 27479, 58193, 'GANDLING_TALK_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58875, 1, 0, 'Their deaths only serve to empower my summoners further! Behold!', 0, 14, 0, 100, 0, 0, 27480, 58194, 'GANDLING_TALK_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58875, 2, 0, 'What\'s the matter, Voss, getting tired?', 0, 14, 0, 100, 0, 0, 27481, 58195, 'GANDLING_TALK_3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58875, 3, 0, 'Isn\'t it beautiful? The weaving of flesh and bone and soul.', 0, 14, 0, 100, 0, 0, 27482, 58196, 'GANDLING_TALK_4');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58875, 4, 0, 'You, too, are beautiful, Voss. Have you looked in a mirror recently?', 0, 14, 0, 100, 0, 0, 27483, 58197, 'GANDLING_TALK_5');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58875, 5, 0, 'My dear, you should embrace this gift that has been bestowed upon you.', 0, 14, 0, 100, 0, 0, 27484, 58198, 'GANDLING_TALK_6');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58875, 6, 0, 'Come, Voss, your fate awaits you.', 0, 14, 0, 100, 0, 0, 27485, 58199, 'GANDLING_TALK_7');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58918, 0, 0, 'You found...', 0, 41, 0, 100, 0, 0, 0, 58910, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58918, 1, 0, 'A Lost Hen! Bring her back to a chicken coop.', 0, 41, 0, 100, 0, 0, 0, 58912, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58918, 2, 0, 'A Wild Prairie Chicken! Kick him out of here!', 0, 41, 0, 100, 0, 0, 0, 58914, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58918, 3, 0, 'A Marmot Disguised in Chicken Feathers! Expose him for the sham he really is!', 0, 41, 0, 100, 0, 0, 0, 58915, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58918, 4, 0, 'An Amorous Rooster! Dip him in the water to cool his passions.', 0, 41, 0, 100, 0, 0, 0, 58913, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58918, 5, 0, 'Old Hillpaw\'s prize-winning chicken, Mother Clucks!', 0, 41, 0, 100, 0, 0, 0, 58916, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58918, 5, 1, 'Old Hillpaw\'s prize-winning chicken, Madam Specklybottom!', 0, 41, 0, 100, 0, 0, 0, 58918, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58918, 5, 2, 'Old Hillpaw\'s prize-winning chicken, Thunderpeck!', 0, 41, 0, 100, 0, 0, 0, 58919, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58918, 5, 3, 'Old Hillpaw\'s prize-winning chicken, Mantidsbane!', 0, 41, 0, 100, 0, 0, 0, 58920, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58918, 5, 4, 'Old Hillpaw\'s prize-winning chicken, Rumblefeather!', 0, 41, 0, 100, 0, 0, 0, 58922, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58918, 5, 5, 'Old Hillpaw\'s prize-winning chicken, Egga!', 0, 41, 0, 100, 0, 0, 0, 58923, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58918, 5, 6, 'Old Hillpaw\'s prize-winning chicken, Stumbleflutter!', 0, 41, 0, 100, 0, 0, 0, 58925, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58918, 5, 7, 'Old Hillpaw\'s prize-winning chicken, Buck-Bucka Betty!', 0, 41, 0, 100, 0, 0, 0, 58926, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58930, 0, 0, 'I was barely inside their outer patrols when they caught me. I didn\'t find anything.', 0, 12, 0, 100, 1, 0, 0, 57799, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58930, 1, 0, 'I saw their leader. He was heading further up the wall. I\'ll mark it on your map.', 0, 12, 0, 100, 1, 0, 0, 57800, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58930, 2, 0, 'Thank you, friend. I didn\'t uncover anything before they caught me.', 0, 12, 0, 100, 1, 0, 0, 57801, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58930, 3, 0, 'I... I failed. Thank you for coming for me.', 0, 12, 0, 100, 1, 0, 0, 57802, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58955, 0, 2, 'Thank you, hero.', 0, 12, 0, 100, 0, 0, 0, 57489, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58955, 0, 1, 'I must get to Stoneplow! The mantid will attack soon.', 0, 12, 0, 100, 0, 0, 0, 57490, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58955, 0, 0, 'The mogu ambushed us while we were trying to retreat from the mantid.', 0, 12, 0, 100, 0, 0, 0, 57491, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58955, 0, 3, 'Thank you, stranger.', 0, 12, 0, 100, 0, 0, 0, 57476, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58970, 0, 0, 'I spent so long trying to find a way to extend my life that I forgot to live it.', 0, 12, 0, 100, 0, 0, 0, 57760, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58970, 1, 0, 'And now this... folly... would cost my daughter her life.', 0, 12, 0, 100, 0, 0, 0, 57762, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58970, 2, 0, 'The water may not create life, but at least it will let me transfer it.', 0, 12, 0, 100, 0, 0, 0, 57764, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58970, 3, 0, 'I love you, my little Lyalia.', 0, 12, 0, 100, 0, 0, 0, 57763, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58975, 0, 0, 'What now, Sunwalker?', 0, 12, 0, 100, 0, 0, 0, 58095, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58975, 1, 0, 'Agreed.', 0, 12, 0, 100, 0, 0, 0, 58096, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (58975, 2, 0, 'Lyalia! I am so sorry, my child.', 0, 12, 0, 100, 0, 0, 0, 57631, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59074, 0, 0, 'Yes yes yes! So close! Just a pinch of...oops...', 0, 14, 0, 100, 0, 0, 28290, 59302, 'Uncle Gao to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59074, 1, 0, 'Too bitter! Just a drop of honey, and a cane of sugar, and maybe some corn? Yes, more corn!', 0, 14, 0, 100, 0, 0, 28291, 59303, 'Uncle Gao');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59074, 2, 0, 'Yes, yes, yes...no, no, no no no! Yes! No! Peppers!', 0, 14, 0, 100, 0, 0, 28292, 59304, 'Uncle Gao');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59074, 3, 0, 'Ahh! Help! What is that thing!? It doesn\'t matter, nothing will stop me now, not when I\'m so close! Maybe if I don\'t look at it...?', 0, 14, 0, 100, 0, 0, 28289, 59301, 'Uncle Gao');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59074, 4, 0, 'Is it...can it be???', 0, 12, 0, 100, 0, 0, 28293, 59305, 'Uncle Gao to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59074, 5, 0, 'Such harmony of flavor, such heady aroma! It is...the perfect brew!', 0, 12, 0, 100, 0, 0, 28294, 59306, 'Uncle Gao to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59074, 6, 0, 'The name of Stormstout will be sung once again throughout the hills!', 0, 14, 0, 100, 0, 0, 28295, 59307, 'Uncle Gao to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59074, 7, 0, 'You again! Don\'t you see? I have made a name for myself at last: I have brewed perfection!', 0, 14, 0, 100, 0, 0, 31999, 59308, 'Uncle Gao to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59074, 8, 0, 'Details, details.', 0, 12, 0, 100, 0, 0, 32000, 61472, 'Uncle Gao to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59074, 9, 0, 'Look, "Chen Stormstout," we all can\'t be heroes, running from our responsibilities, tromping around the Dread Wastes, saving the world. Some of us are "Artists."', 0, 12, 0, 100, 0, 0, 32001, 61473, 'Uncle Gao to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59074, 10, 0, 'Yes, yes. That happens.', 0, 12, 0, 100, 0, 0, 32002, 61474, 'Uncle Gao to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59080, 0, 0, 'School is in session!', 0, 14, 0, 100, 0, 0, 27477, 58099, 'grandmaster gandling - TALK_AGGRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59080, 1, 0, 'Dunce.', 0, 14, 0, 100, 0, 0, 27497, 58100, 'grandmaster gandling - TALK_SLAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59080, 2, 0, 'Class... dismissed.', 0, 14, 0, 100, 0, 0, 27478, 58102, 'grandmaster gandling - TALK_DEATH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59080, 3, 0, '|TInterface\\Icons\\inv_misc_book_01.blp:20|t%s assigns $n a |cFFFF0000|Hspell:113395|h[Harsh Lesson]|h|r!', 0, 41, 0, 100, 0, 0, 0, 58103, 'grandmaster gandling - TALK_HARSH_LESSON');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59143, 0, 0, 'I am in your debt.', 0, 12, 0, 100, 2, 0, 0, 58086, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59143, 0, 1, 'I must rejoin the battle to save Binan Village.', 0, 12, 0, 100, 2, 0, 0, 58090, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59143, 0, 2, 'Shaohao must have sent you.', 0, 12, 0, 100, 2, 0, 0, 58093, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59143, 0, 3, 'We must drive the yaungol back to the Townlong Steppes.', 0, 12, 0, 100, 2, 0, 0, 58091, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59143, 0, 4, 'You have great honor, stranger. Please join us.', 0, 12, 0, 100, 2, 0, 0, 58092, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59143, 0, 5, 'Thank you. You have saved my life.', 0, 12, 0, 100, 2, 0, 0, 58089, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59143, 0, 6, 'Join us, $c. We could use you in the fight.', 0, 12, 0, 100, 2, 0, 0, 58088, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59143, 0, 7, 'That is powerful healing!', 0, 12, 0, 100, 0, 0, 0, 58087, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59150, 0, 0, 'Everything must burn. None shall know of the Scarlet Crusade\'s shame!', 0, 14, 0, 100, 0, 0, 29524, 63259, 'KOEGLER_INTO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59150, 1, 0, 'You, too, shall be charred to ash!', 0, 14, 0, 100, 0, 0, 29522, 63260, 'KOEGLER_AGGRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59150, 2, 0, 'Purged by fire!', 0, 14, 0, 100, 0, 0, 29528, 66472, 'KOEGLER_DRAGON_BREATH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59150, 4, 0, '|TInterface\\Icons\\inv_misc_head_dragon_01.blp:20|t%s begins to cast |cFFFF0000|Hspell:113653|h[Greater Dragon\'s Breath]|h|r!', 0, 41, 0, 100, 0, 0, 0, 58168, 'KOEGLER_DRAGON_BREATH_ANN');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59150, 3, 0, '|TInterface\\Icons\\ability_warlock_inferno.blp:20|t%s begins to cast |cFFFF0000|Hspell:113364|h[Book Burner]|h|r!', 0, 41, 0, 100, 0, 0, 0, 58133, 'KOEGLER_BOOK_BURNER_ANN');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59150, 5, 0, 'My fire... has gone out..', 0, 14, 0, 100, 0, 0, 29523, 63261, 'KOEGLER_DEATH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59153, 0, 0, 'RATTLEGORE!', 0, 14, 0, 100, 0, 0, 0, 58323, 'rattlegore - TALK_SUMMONED');
