INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (59704, 2, 0, 'Where are the other Stormstouts? Why are hozen all over the brewery?', 0, 12, 0, 100, 0, 0, 29768, 59248, 'Chen #4');
