INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61140, 0, 1, 'But it ain\'t no ting. Take your trinkets; you\'ll be needin\' dem.', 0, 14, 0, 100, 0, 0, 29083, 60654, 'GARAJAL_DEATH_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61140, 0, 2, 'I be goin\' now; time ta find a new home for me soul.', 0, 14, 0, 100, 0, 0, 29084, 60655, 'GARAJAL_DEATH_3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61140, 0, 3, 'Death be a doorway, an\' time a window; I\'ll be back! AH-HAHAHAHAHAH!', 0, 14, 0, 100, 0, 0, 29085, 60656, 'GARAJAL_DEATH_4');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61156, 0, 0, 'Flee, craven villagers! This land belongs to nature alone once more!', 0, 14, 0, 100, 0, 0, 0, 60741, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61156, 1, 0, 'Vengeful Hui has appeared in the Mason\'s Quarter, north of Greenstone Village. Challenge him!', 0, 41, 0, 100, 0, 0, 0, 60742, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61203, 0, 0, 'Many thanks... I couldn\'t have held out much longer.', 0, 12, 0, 100, 0, 0, 0, 60930, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61203, 1, 0, 'Alas, I\'m in no condition to go on. If only our entire supply of Greenstone Brew hadn\'t been stolen!', 0, 12, 0, 100, 0, 0, 0, 62401, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61203, 2, 0, 'If you can venture into the surrounding forest and return any stolen barrels you find, I\'ll concoct a concentrated draft of marvelous power for you...', 0, 12, 0, 100, 0, 0, 0, 66255, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61203, 3, 0, 'An adventurer saves the village, but a HERO returns the brew!', 0, 14, 0, 100, 0, 0, 0, 66253, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61203, 3, 1, 'That\'ll teach those pests to pilfer our dojo\'s prized product!', 0, 14, 0, 100, 0, 0, 0, 66251, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61203, 3, 2, 'Ah, back where it belongs. Thank you!', 0, 14, 0, 100, 0, 0, 0, 66252, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61203, 3, 3, 'A precious barrel of Greenstone brew! Hurrah!', 0, 14, 0, 100, 0, 0, 0, 66250, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61203, 4, 0, 'Ah, you have returned! One thousand thanks. Please, take this, our most potent creation: Volatile Greenstone Brew.', 0, 12, 0, 100, 0, 0, 0, 60931, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61203, 5, 0, 'You\'ve received Volatile Greenstone Brew! Use it in combat to help defeat multiple enemies.', 0, 41, 0, 100, 0, 0, 0, 60915, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61243, 0, 0, 'I hear something...', 0, 14, 0, 0, 0, 0, 32495, 63254, 'Gekkan intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61243, 1, 0, 'Search his corpse!', 0, 14, 0, 0, 0, 0, 32496, 63257, 'Gekkan kill target');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61243, 2, 0, 'Slay them!', 0, 14, 0, 0, 0, 0, 32497, 63258, 'Gekkan spell cast');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61243, 3, 0, 'Stop them!', 0, 14, 0, 0, 0, 0, 32488, 63255, 'Gekkan aggro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61243, 4, 0, 'Such a waste...', 0, 14, 0, 0, 0, 0, 32494, 63256, 'Gekkan death');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61305, 0, 0, 'LOREWALKER! You have something of mine.', 0, 14, 0, 100, 0, 0, 0, 60715, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61315, 0, 0, 'The binding should only take a moment, but I\'ll be completely entranced.', 0, 12, 0, 100, 0, 0, 0, 60713, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61315, 1, 0, 'Watch my back - I trust you can handle any interruptions.', 0, 12, 0, 100, 0, 0, 0, 60714, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61315, 2, 0, 'All done. Did I miss anything?', 0, 12, 0, 100, 0, 0, 0, 60718, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61342, 0, 0, 'Away, you fiends! Help!', 0, 14, 0, 100, 20, 0, 0, 60979, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61342, 1, 0, 'There\'s hope for Greenstone! Thank you!', 0, 12, 0, 100, 0, 0, 0, 60965, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61346, 0, 0, 'Away, you fiends! Help!', 0, 14, 0, 100, 20, 0, 0, 60979, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61346, 1, 0, 'Many thanks!', 0, 12, 0, 100, 0, 0, 0, 60684, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 0, 0, 'The ancient vaults of the mogu, untouched by time for thousands of years, who knows what secrets await?', 0, 12, 0, 100, 0, 0, 30198, 65168, 'LOREWALKER_CHO_INTRO_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 1, 0, 'Many young Pandaren warriors have vanished into this...tomb. Have your wits about you.', 0, 12, 0, 100, 0, 0, 30199, 65170, 'LOREWALKER_CHO_INTRO_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 2, 0, 'The large Quilen seem to be guarding that door. Tread carefully, lest you wake them.', 0, 12, 0, 100, 0, 0, 30179, 65172, 'LOREWALKER_CHO_DOGS');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 3, 0, 'That was close!', 0, 14, 0, 100, 0, 0, 30180, 65173, 'LOREWALKER_CHO_DOGS_DIE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 4, 0, 'The door mechanism seems to have activated with the demise of the guardians. I fear what we may find within.', 0, 12, 0, 100, 0, 0, 30181, 65174, 'LOREWALKER_CHO_DOGS_DOOR_EVENT');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 5, 0, 'We seem to have awakened a spirit of some kind.', 0, 12, 0, 100, 0, 0, 30193, 65175, 'LOREWALKER_CHO_FENG_EVENT_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 6, 0, 'So the stories were true...', 0, 12, 0, 100, 0, 0, 30194, 65176, 'LOREWALKER_CHO_FENG_EVENT_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 7, 0, 'We have disturbed what should not have been disturbed. He cannot be free to escape this prison! You must destroy him!', 0, 12, 0, 100, 0, 0, 30195, 65177, 'LOREWALKER_CHO_FENG_EVENT_3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 8, 0, 'Feng the Accursed was betrayed by four emperors of the mogu, his body destroyed each time, only to rise again.', 0, 12, 0, 100, 0, 0, 30196, 65178, 'LOREWALKER_CHO_FENG_PAST_EVENT_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 9, 0, 'This is not the last we have seen of Feng...', 0, 12, 0, 100, 0, 0, 30197, 65180, 'LOREWALKER_CHO_FENG_PAST_EVENT_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 10, 0, 'The Zandalari, ancient allies of the mogu! They must seek the legendary weapon within the depths of the Vaults!', 0, 12, 0, 100, 0, 0, 30209, 65181, 'LOREWALKER_CHO_GARAJAL_EVENT_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 11, 0, 'I am sorry I did not tell you earlier! These vaults house a weapon of unimaginable power! Stop them!', 0, 12, 0, 100, 0, 0, 30208, 65188, 'LOREWALKER_CHO_GARAJAL_EVENT_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 12, 0, 'I will stay behind and investigate this dais further...', 0, 12, 0, 100, 0, 0, 30210, 65189, 'LOREWALKER_CHO_GARAJAL_EVENT_3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 13, 0, 'Ah, the ancient Vaults of the Kings of Mogu\'shan. What might this button do...?', 0, 12, 0, 100, 0, 0, 30176, 60833, 'LOREWALKER_CHO_SPIRIT_KINGS_EVENT_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 14, 0, 'Look here! It\'s the history of Zian of the Endless Shadow, a powerful sorcerer who kept the kingdoms in check through terror and darkness.', 0, 12, 0, 100, 0, 0, 30207, 60728, 'LOREWALKER_CHO_SPIRIT_KINGS_EVENT_ZIAN');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 15, 0, 'This looks to be the record of Qiang the Merciless, a great conqueror who ruled his vassals with an iron fist!', 0, 12, 0, 100, 0, 0, 31529, 60857, 'LOREWALKER_CHO_SPIRIT_KINGS_EVENT_QUIANG');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 16, 0, 'I\'ve heard tales of this one: Subetai the Swift, well known as a masterful archer and genius thief. He stole from the rich, and kept everything for himself.', 0, 12, 0, 100, 0, 0, 30177, 60861, 'LOREWALKER_CHO_SPIRIT_KINGS_EVENT_SUBETAI');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 17, 0, 'What is this? I\'ve heard nothing of this emperor. Meng the Demented? According to these texts, Meng was the most foul and maniacal ruler to ever abuse the imperial throne.', 0, 12, 0, 100, 0, 0, 31528, 60865, 'LOREWALKER_CHO_SPIRIT_KINGS_EVENT_MENG');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61348, 18, 0, 'Heroes! You\'re alive. I brought help.', 0, 14, 0, 100, 0, 0, 38147, 74996, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61350, 0, 0, 'Help! Help!', 0, 14, 0, 100, 20, 0, 0, 60978, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61350, 1, 0, 'I thought I was doomed. Thank you so much!', 0, 12, 0, 100, 0, 0, 0, 60966, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61398, 0, 0, 'What is this, more have come to claim my crown? They are small and weak.', 0, 14, 0, 100, 0, 0, 28355, 61048, 'Xin the Weaponmaster - TALK_INTRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61398, 1, 0, 'You are not the first to challenge me, peons. You will not be the last.', 0, 14, 0, 100, 0, 0, 28349, 61047, 'Xin the Weaponmaster - TALK_AGGRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61398, 2, 0, 'Face the might of my arsenal, weaklings!', 0, 14, 0, 100, 0, 0, 28350, 61055, 'Xin the Weaponmaster - TALK_AXES');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61398, 3, 0, '%s activates his |cFFFF0404|Hspell:119374|h[Whirlwinding Axe]|h|r trap!', 0, 41, 0, 100, 0, 0, 28350, 63555, 'Xin the Weaponmaster - EMOTE_AXES');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61398, 4, 0, '%s activates his |cFFFF0404|Hspell:119311|h[Stream of Blades]|h|r trap!', 0, 41, 0, 100, 0, 0, 0, 63551, 'Xin the Weaponmaster - EMOTE_BLADES');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61398, 5, 0, 'MY MIGHT IS ENDLESS!', 0, 14, 0, 100, 0, 0, 28351, 61052, 'Xin the Weaponmaster - TALK_BLADES');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61398, 6, 0, 'ENOUGH OF THIS FARCE! You will feel PAIN, cowards!', 0, 14, 0, 100, 0, 0, 28360, 61058, 'Xin the Weaponmaster - TALK_CROSSBOW');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61398, 7, 0, '%s activates his |cFFFF0404|Hspell:120142|h[Crossbow]|h|r trap!', 0, 41, 0, 100, 0, 0, 28360, 63554, 'Xin the Weaponmaster - EMOTE_CROSSBOW');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61398, 8, 0, 'You call that... a fight...?', 0, 14, 0, 100, 0, 0, 28352, 61065, 'Xin the Weaponmaster to Player - TALK_DEATH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61398, 9, 0, '%s spits blood in your face.', 0, 16, 0, 100, 0, 0, 0, 61059, 'Xin the Weaponmaster to Player - EMOTE_DEATH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61398, 10, 0, 'See how effortlessly I CRUSH your bones!', 0, 14, 0, 50, 0, 0, 28357, 61069, 'Xin slay 1 - TALK_SLAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61398, 10, 1, 'Fall to your knees, coward!', 0, 14, 0, 50, 0, 0, 28358, 61070, 'Xin slay 2 - TALK_SLAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61398, 11, 0, 'I. Am. UNDEFEATED!', 0, 14, 0, 100, 0, 0, 28356, 61178, 'Xin reset - TALK_RESET');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61442, 0, 0, 'Clan Gurthan will show our King and the rest of you power-hungry imposters why we are the rightful ones at his side!', 0, 14, 0, 0, 0, 0, 27932, 60932, 'Kuai the Brute intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61442, 1, 0, 'We will never surrender our right to rule the destiny of our people!', 0, 14, 0, 0, 0, 0, 27933, 60943, 'Kuai retire');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61442, 2, 0, 'The glory of Gurthan!', 0, 14, 0, 0, 0, 0, 27942, 63314, 'Kuai kill target');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61442, 3, 0, 'Who allowed these outsiders inside our halls? Only Clan Harthak or Clan Kargesh would stoop to such treachery!', 0, 14, 0, 0, 0, 0, 27940, 61067, 'Kuai postcombat #1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61442, 4, 0, 'A well-scripted exchange; but we are not fooled. Your clans are clearly in league with one another. For the glory of our king, our empire, we will put you all down!', 0, 14, 0, 0, 0, 0, 27941, 61077, 'Kuai postcombat #2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61442, 5, 0, 'We are the greatest!', 0, 14, 0, 50, 0, 0, 27934, 61559, 'Kuai idle #1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61442, 5, 1, 'These other clans are nothing!', 0, 14, 0, 50, 0, 0, 27935, 61560, 'Kuai idle #2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61444, 0, 0, 'Clan Harthak will show all why they are the truest of Mogu!', 0, 14, 0, 0, 0, 0, 27947, 60934, 'Ming the Cunning intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61444, 1, 0, 'Our clan is the true clan! No interloper can change that!', 0, 14, 0, 0, 0, 0, 27948, 60944, 'Ming retire');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61444, 2, 0, 'This is the might of Harthak!', 0, 14, 0, 0, 0, 0, 27957, 63315, 'Ming kill target');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61444, 3, 0, 'Only the dogs of Clan Gurthan would point fingers at others for their own failures! Your clan is the most desperate. It must\'ve been you!', 0, 14, 0, 0, 0, 0, 27955, 61072, 'Ming postcombat #1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61444, 4, 0, 'Our clan holds true to the ways set forth by the ancients. We deserve the king\'s favor above all others!', 0, 14, 0, 50, 0, 0, 27950, 61562, 'Ming idle #1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61444, 4, 1, 'These other clans aren\'t worthy of your time, great king!', 0, 14, 0, 50, 0, 0, 27949, 61561, 'Ming idle #2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61445, 0, 0, 'Clan Kargesh will demonstrate why only the strong deserve to stand at our King\'s side!', 0, 14, 0, 0, 0, 0, 28246, 60933, 'Haiyan the Unstoppable intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61445, 1, 0, 'Impossible! Our might is the greatest in all the empire!', 0, 14, 0, 0, 0, 0, 28247, 60945, 'Haiyan retire');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61445, 2, 0, 'For Kargesh!', 0, 14, 0, 0, 0, 0, 28256, 63316, 'Haiyan kill target');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61445, 3, 0, 'Clan Gurthan is right! Only the honorless Clan Harthak could bring in a lesser race to do their bidding. You must be traitors!', 0, 14, 0, 0, 0, 0, 28254, 61074, 'Haiyan postcombat #1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61445, 4, 0, 'The Mogu cannot reclaim our rightful glory without our clan\'s strength!', 0, 14, 0, 50, 0, 0, 28248, 61563, 'Haiyan idle #1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61445, 4, 1, 'These pathetic clans are beneath you, my king. They sully the great legacy of our people!', 0, 14, 0, 50, 0, 0, 28249, 61564, 'Haiyan idle #2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61461, 0, 0, 'They\'ve overrun the village! HELP!', 0, 14, 0, 100, 20, 0, 0, 60980, '');
