INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61485, 1, 0, 'The temple will fall! You cannot stop my forces!', 0, 14, 0, 100, 0, 0, 30277, 61505, 'Geleral Pavalak - SAY_AGGRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61485, 2, 0, 'My army will crush you!', 0, 14, 0, 100, 0, 0, 30278, 61506, 'Geleral Pavalak - SAY_BULWARK');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61485, 2, 1, 'Stop them! Mantid attack!', 0, 14, 0, 100, 0, 0, 30280, 61507, 'Geleral Pavalak - SAY_BULWARK');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61485, 2, 2, 'Put down your weapons or die!', 0, 14, 0, 100, 0, 0, 30283, 61509, 'Geleral Pavalak - SAY_BULWARK');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61485, 3, 0, 'The empress demands it!', 0, 14, 0, 100, 0, 0, 30285, 61511, 'Geleral Pavalak - SAY_SLAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61485, 3, 1, 'The forces of Niuzao are weak!', 0, 14, 0, 100, 0, 0, 30286, 61512, 'Geleral Pavalak - SAY_SLAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61485, 4, 0, 'I have failed you, empress...', 0, 14, 0, 100, 0, 0, 30284, 61510, 'Geleral Pavalak - SAY_');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61492, 0, 0, 'Thank you, friend. Don\'t worry, it was just a scratch. Now I feel good as new!', 0, 12, 0, 100, 0, 0, 0, 61006, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61492, 0, 1, 'I thought I was a goner. I owe you my life.', 0, 12, 0, 100, 0, 0, 0, 61003, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61492, 0, 2, 'Thanks for saving me. It was rough up there.', 0, 12, 0, 100, 0, 0, 0, 53196, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61492, 0, 3, 'I\'m alive? Ha! That\'s more than I can say for those Horde at Garrosh\'ar Point. We tore them up good.', 0, 12, 0, 100, 0, 0, 0, 53197, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61492, 0, 4, 'Thank you. Time to lick my wounds and get back to the fight!', 0, 12, 0, 100, 0, 0, 0, 53195, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61567, 0, 0, 'I hope they make it this far. Then my strength and skill will be clear to all!', 0, 14, 0, 100, 0, 0, 29392, 66443, 'Vizier Jin\'bak - SAY_INTRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61567, 1, 0, 'Ah-hah! The lot of you will soon wish you hadn\'t come this way...', 0, 14, 0, 100, 0, 0, 29390, 66444, 'Vizier Jin\'bak - SAY_AGGRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61567, 2, 0, 'But... I was... who will...?!', 0, 14, 0, 100, 0, 0, 29391, 66445, 'Vizier Jin\'bak - SAY_SAY_DEATH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61567, 3, 0, 'Did you think you stood a chance?!', 0, 14, 0, 100, 0, 0, 29394, 66447, 'Vizier Jin\'bak - SAY_SLAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61567, 4, 0, 'Allow me to show you the power of amber alchemy...', 0, 14, 0, 100, 0, 0, 29395, 66448, 'Vizier Jin\'bak - SAY_DETONATE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 0, 0, 'Thanks for the save, but we\'re still stuck in the middle of the entire mantid army.', 0, 12, 0, 100, 0, 0, 31505, 61584, 'Yang Ironclaw - SAY_INTRO_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 1, 0, 'Look out there. Too many for us to take head on.', 0, 12, 0, 100, 0, 0, 31506, 61591, 'Yang Ironclaw - SAY_INTRO_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 2, 0, 'Hmmm... I have a crazy idea that just might work, but we\'ll need your help heroes!', 0, 12, 0, 100, 0, 0, 31507, 62580, 'Yang Ironclaw - SAY_INTRO_3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 3, 0, 'Let\'s give these mantid a taste of their own medicine!', 0, 12, 0, 100, 0, 0, 31508, 62581, 'Yang Ironclaw - SAY_INTRO_4');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 4, 0, 'Li and Lo, get the sap into position!', 0, 12, 0, 100, 0, 0, 31509, 62582, 'Yang Ironclaw - SAY_AGGRO_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 5, 0, 'Let\'s get this party started, shall we?', 0, 12, 0, 100, 0, 0, 31510, 62583, 'Yang Ironclaw - SAY_AGGRO_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 6, 0, 'Bring it, uglies!', 0, 14, 0, 100, 0, 0, 31513, 62584, 'Yang Ironclaw - SAY_AGGRO_3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 7, 0, 'We\'ll squish you like bugs!', 0, 14, 0, 100, 0, 0, 31514, 66224, 'Yang Ironclaw - SAY_AGGRO_4');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 8, 0, 'We\'ve got your sap -- come and get it!', 0, 14, 0, 100, 0, 0, 31515, 66225, 'Yang Ironclaw - SAY_AGGRO_5');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 10, 0, 'Swarmers are coming up the ramp -- use the sap to slow them down!', 0, 14, 0, 100, 0, 0, 31511, 62586, 'Yang Ironclaw - SAY_WAVE_ONE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 12, 0, 'Demolishers are carrying explosives! Any direct hit will blow them up!', 0, 14, 0, 100, 0, 0, 31504, 62025, 'Yang Ironclaw - SAY_WAVE_TWO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 14, 0, 'Warrior coming in!  Focus your fire!', 0, 14, 0, 100, 0, 0, 31512, 62168, 'Yang Ironclaw - SAY_WAVE_THREE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 17, 0, 'The Amberwing is flying in, watch out!', 0, 14, 0, 100, 0, 0, 31503, 62023, 'Yang Ironclaw - SAY_AMBERWING');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 18, 0, 'Well, that was fun, wasn\'t it?', 0, 12, 0, 100, 11, 0, 31497, 62551, 'Yang Ironclaw - SAY_OUTRO_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 19, 0, 'Li and Lo, go look after our friends down below.', 0, 12, 0, 100, 25, 0, 31498, 62734, 'Yang Ironclaw - SAY_OUTRO_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 20, 0, 'Ah, this gate is locked, but I think I have something that\'ll open it.', 0, 12, 0, 100, 0, 0, 31499, 62735, 'Yang Ironclaw - SAY_GATE_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 21, 0, 'Let me see here... thirty second fuse...', 0, 12, 0, 100, 0, 0, 31500, 62736, 'Yang Ironclaw - SAY_GATE_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 22, 0, 'Whoops, did I say thirty? I meant three.', 0, 12, 0, 100, 0, 0, 31501, 62737, 'Yang Ironclaw - SAY_GATE_3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 23, 0, 'Go, hurry! You must stop the siege!', 0, 12, 0, 100, 5, 0, 31502, 62738, 'Yang Ironclaw - SAY_GATE_4');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 9, 0, 'Look out there. Too many for us to take head on.', 0, 12, 0, 100, 0, 0, 31506, 62738, 'Yang Ironclaw - EMOTE_WAVE_ONE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 11, 0, 'Let\'s give these mantid a taste of their own medicine!', 0, 12, 0, 100, 0, 0, 31508, 62738, 'Yang Ironclaw - EMOTE_WAVE_TWO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 13, 0, 'Let\'s get this party started, shall we?', 0, 12, 0, 100, 0, 0, 31510, 62738, 'Yang Ironclaw - EMOTE_WAVE_THREE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 16, 0, 'Bring it, uglies!', 0, 14, 0, 100, 0, 0, 31513, 62738, 'Yang Ironclaw - EMOTE_BOMBARD');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61620, 15, 0, 'Warriors coming in! Focus your fire!', 0, 12, 0, 100, 0, 0, 31512, 62738, 'Yang Ironclaw - EMOTE_WAVE_FOUR');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61634, 0, 0, 'Fools! Attacking the might of the mantid head on?  Your deaths will be swift.', 0, 14, 0, 100, 0, 0, 30264, 61296, 'Commander Vo\'jak - SAY_AGGRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61634, 1, 0, 'Swarmers, attack!  Tear the flesh from their bones!', 0, 14, 0, 100, 0, 0, 30265, 61297, 'Commander Vo\'jak - SAY_WAVE_ONE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61634, 2, 0, 'Demolishers, get in there!  Leave nothing but a pile of ash!', 0, 14, 0, 100, 0, 0, 30266, 62587, 'Commander Vo\'jak - SAY_WAVE_TWO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61634, 3, 0, 'Warriors, trample these weaklings!  Crush them!', 0, 14, 0, 100, 0, 0, 30267, 62588, 'Commander Vo\'jak - SAY_WAVE_THREE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61634, 4, 0, 'Everyone! Send everything! Slaughter these cowards, they are making us look like fools!', 0, 14, 0, 100, 0, 0, 30268, 62589, 'Commander Vo\'jak - SAY_WAVE_FOUR');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61634, 5, 0, 'Where... where is it? Worthless, useless... damn you!', 0, 14, 0, 100, 0, 0, 30272, 66200, 'Commander Vo\'jak - SAY_AIR_SUPPORT');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61634, 6, 0, 'Pah! Those who failed deserved to die; you have merely culled the weak!', 0, 14, 0, 100, 0, 0, 30262, 66496, 'Commander Vo\'jak - SAY_ENGAGE_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61634, 7, 0, 'I must finish this myself, then. So be it!', 0, 14, 0, 100, 0, 0, 30263, 62167, 'Commander Vo\'jak - SAY_ENGAGE_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61634, 8, 0, 'Carpet bomb the platform! Send in the Amberwing!', 0, 14, 0, 100, 0, 0, 30269, 61299, 'Commander Vo\'jak - SAY_AMBERWING');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61634, 8, 1, 'They are holding us off, bring in the air support!', 0, 14, 0, 100, 0, 0, 30270, 62626, 'Commander Vo\'jak - SAY_AMBERWING');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61634, 8, 2, 'Amberwing, get in there! Lay siege to the holdouts!', 0, 14, 0, 100, 0, 0, 30271, 62627, 'Commander Vo\'jak - SAY_AMBERWING');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61634, 9, 0, 'It was... a trap...', 0, 14, 0, 100, 0, 0, 30273, 66201, 'Commander Vo\'jak - SAY_DEATH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61634, 10, 0, 'Too easy!', 0, 14, 0, 100, 0, 0, 30274, 66202, 'Commander Vo\'jak - SAY_SLAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61634, 10, 1, 'Were you even trying?!', 0, 14, 0, 100, 0, 0, 30275, 66203, 'Commander Vo\'jak - SAY_SLAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61634, 10, 2, 'Haha, you die so easily!', 0, 14, 0, 100, 0, 0, 30276, 66204, 'Commander Vo\'jak - SAY_SLAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61636, 7, 0, 'The Illusionary Mystic will heal any ally that is significantly wounded. Interrupt its healing spells.', 0, 14, 0, 100, 0, 0, 38495, 75856, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61636, 6, 0, 'The Illusionary Amber-Weaver conjures globs of amber that float toward you and explode on any contact. Lead the amber globs into other illusions.', 0, 14, 0, 100, 0, 0, 38497, 75854, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61636, 4, 0, 'Success! Hah, well done. See me for your next trial.', 0, 14, 0, 100, 0, 0, 38554, 75851, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61636, 5, 0, 'As before, defeat each wave of illusions before time runs out. This time, you will encounter more difficult illusions.', 0, 14, 0, 100, 0, 0, 38553, 75848, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61636, 3, 0, 'Illusionary Varmints come in packs, and are harmless. Use attacks that affect multiple opponents to kill them quickly.', 0, 14, 0, 100, 0, 0, 38498, 75853, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61636, 2, 0, 'A master of defense, the Illusionary Guardian deflects all attacks from the front. Stay behind it.', 0, 14, 0, 100, 0, 0, 38494, 75857, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61636, 0, 0, 'Welcome to the Proving Grounds. Your first trial will now begin. Defeat each wave of illusions before time runs out.', 0, 14, 0, 100, 0, 0, 38500, 75847, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61636, 1, 0, 'This Illusionary Slayer will test your ability to damage your opponent. Do not worry; it cannot harm you.', 0, 14, 0, 100, 0, 0, 38492, 75859, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61636, 11, 0, 'Oh, the Illusionary Sha! Save your strongest attacks for when its barrier falls, leaving it exposed.', 0, 14, 0, 100, 0, 0, 38493, 75858, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61636, 10, 0, 'An Illusionary Banshee! Kill it, quickly, before it explodes!', 0, 14, 0, 100, 0, 0, 38491, 75860, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61636, 9, 0, 'Ah, the Illusionary Banana-Tosser! It will rapidly toss bananas toward you. Move to avoid them. Prevent it from running away from you, if you can.', 0, 14, 0, 100, 0, 0, 38496, 75855, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61636, 8, 0, 'Prepare yourself for the most difficult illusions. Defeat them before time expires, and success will be yours.', 0, 14, 0, 100, 0, 0, 38552, 75849, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61707, 0, 0, 'You will soon join the corpses buried here, little $c!', 0, 14, 0, 100, 0, 0, 0, 66738, 'abomination - intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61707, 1, 0, '|TInterface\\Icons\\sha_ability_rogue_envelopingshadows.blp:20|t%s is unleashing |cFFFF0000|Hspell:120215|h[Deathforce]|h|r! RUN AWAY!', 0, 41, 0, 100, 0, 0, 0, 61265, 'abomination - announce');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61766, 0, 0, 'Intruders in the crypt!  Don\'t let them leave alive!', 0, 14, 0, 100, 0, 0, 0, 66737, 'vault guardian - intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61783, 0, 0, 'Who dares disturb this crypt?', 0, 14, 0, 100, 0, 0, 0, 61725, 'hall guardian - combat');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61792, 0, 0, '', 0, 12, 0, 100, 273, 0, 27390, 61428, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61792, 1, 0, '', 0, 12, 0, 100, 273, 0, 27391, 61433, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61792, 2, 0, '', 0, 12, 0, 100, 24, 0, 27392, 61441, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61792, 3, 0, '', 0, 12, 0, 100, 274, 0, 27393, 61457, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 0, 0, 'Walk with me. All three of you.', 0, 12, 0, 100, 1, 0, 29574, 61424, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 1, 0, 'I understand you want to join the Alliance.', 0, 12, 0, 100, 0, 0, 29575, 61425, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 2, 0, 'We always have need of allies. I\'d be a fool to turn you away, especially at a time of war.', 0, 12, 0, 100, 1, 0, 29576, 61426, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 3, 0, 'But there are a number of things I need to make clear to you.', 0, 12, 0, 100, 0, 0, 29577, 61427, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 4, 0, 'First: the races of the Alliance look out for one another.', 0, 12, 0, 100, 0, 0, 29578, 61429, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 5, 0, 'You are expected to provide aid to Alliance members in need, whether they be human, gnome, draenei... or pandaren like yourselves.', 0, 12, 0, 100, 1, 0, 29579, 61430, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 6, 0, 'It is a simple guideline, but a meaningful one. Do you understand?', 0, 12, 0, 100, 0, 0, 29580, 61432, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 7, 0, 'Good. Because that brings me to my second point.', 0, 12, 0, 100, 273, 0, 29581, 61434, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 8, 0, 'Not all pandaren have chosen the same path as you three.', 0, 12, 0, 100, 0, 0, 29582, 61435, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 9, 0, 'Some have sided with the Horde.', 0, 12, 0, 100, 0, 0, 29583, 61436, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 10, 0, 'They, along with the other barbarian clans of the Horde, are your new enemies.', 0, 12, 0, 100, 1, 0, 29584, 61437, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 11, 0, 'Those who you once considered friends, or even those you might have loved, are now your sworn adversaries.', 0, 12, 0, 100, 274, 0, 29585, 61438, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 12, 0, 'I am deeply sorry, but the battle lines have been drawn. I will not tolerate any fraternizing with the enemy, as you could expose our Alliance to danger.', 0, 12, 0, 100, 5, 0, 29586, 61439, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 13, 0, 'Do I make myself clear?', 0, 12, 0, 100, 0, 0, 29587, 61440, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 14, 0, 'Few people know this, but I was once a pit fighter myself. A gladiator for the Horde\'s amusement. So I know a thing or two about martial combat.', 0, 12, 0, 100, 1, 0, 29568, 66865, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 15, 0, 'Now, pandaren... let me see what you\'ve got!', 0, 12, 0, 100, 5, 0, 29569, 61454, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 16, 0, 'Don\'t hold back now! Let\'s have it!', 0, 12, 0, 100, 5, 0, 29570, 61455, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 17, 0, 'What\'s the matter? You\'ll have to do better than that.', 0, 12, 0, 100, 6, 0, 29571, 61456, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 18, 0, 'Come on, pandaren! HIT ME!', 0, 14, 0, 100, 5, 0, 29572, 61458, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61796, 19, 0, 'Ha ha ha haaa! I haven\'t felt like this in YEARS! Don\'t worry, $p... I\'ve been in worse scraps than this. A fighting spirit like yours is welcome in the Alliance.  Now, let\'s get back to business.', 0, 12, 0, 100, 11, 0, 29573, 61459, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61814, 0, 0, 'Good!  These pathetic initiates were boring me!', 0, 14, 0, 100, 0, 0, 0, 66256, 'ironfist - past intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61884, 0, 0, 'Useless, all of you! Even the guards you give me in tribute can\'t keep these lesser beings from my palace.', 0, 14, 0, 0, 15, 0, 28353, 61394, 'Trial of the King intro #1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (61884, 1, 0, 'Redeem, nay, PROVE yourselves now. Deal with these intruders. The clan who brings me their heads will have my favor!', 0, 14, 0, 0, 14, 0, 28354, 61395, 'Trial of the King intro #2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62081, 0, 0, 'Yes, warchief.', 0, 12, 0, 100, 2, 0, 27290, 61595, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62081, 1, 0, 'I... I believe so. Yes.', 0, 12, 0, 100, 273, 0, 27291, 61603, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62081, 2, 0, 'Well fought. I don\'t think I could have survived that battle without you at my back.', 0, 12, 0, 100, 0, 0, 27293, 61613, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 0, 0, 'You\'ve made a wise decision to join my Horde.', 0, 12, 0, 100, 273, 0, 30746, 61586, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 1, 0, 'There are some that would call us barbarians... the "mongrel races" of Azeroth.', 0, 12, 0, 100, 396, 0, 30747, 61587, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 2, 0, 'They are ignorant, and blind to reality. Look around you, pandaren.', 0, 12, 0, 100, 274, 0, 30748, 61588, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 3, 0, 'You will find no better partner in battle than an orc.', 0, 12, 0, 100, 397, 0, 30749, 61589, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 4, 0, 'The tauren have made themselves useful. Look - this one is a shaman.', 0, 12, 0, 100, 6, 0, 30750, 61590, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 5, 0, 'Even a blood elf can hold a sword.', 0, 12, 0, 100, 396, 0, 30751, 61592, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 6, 0, 'Do you see it? Everyone in my Horde earns their keep. You and your friends will be no exception.', 0, 12, 0, 100, 396, 0, 30752, 61593, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 7, 0, 'Do you understand this, pandaren?', 0, 12, 0, 100, 6, 0, 30753, 61594, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 8, 0, 'Hm. Good.', 0, 12, 0, 100, 0, 0, 30754, 61596, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 9, 0, 'Moving on. I know that you are not the only pandaren to escape from that island.', 0, 12, 0, 100, 396, 0, 30755, 61597, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 10, 0, 'Some of your kind chose to join the Alliance.', 0, 12, 0, 100, 14, 0, 30756, 61598, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 11, 0, 'They are now your enemies.', 0, 12, 0, 100, 397, 0, 30757, 61599, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 12, 0, 'You may have had friends and kin who chose to cast their lot with the enemies of the Horde. They are no longer your friends... no longer your family.', 0, 12, 0, 100, 396, 0, 30758, 61600, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 13, 0, 'The minute they put on that Alliance tabard, they died. I will NOT tolerate any lingering ties across enemy lines. Traitors to the Horde will die a traitor\'s death!', 0, 12, 0, 100, 274, 0, 30759, 61601, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 14, 0, 'Do I make myself clear?', 0, 12, 0, 100, 5, 0, 30760, 61602, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 15, 0, 'Good.', 0, 12, 0, 100, 0, 0, 30761, 61604, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 16, 0, 'Meet me in the Valley of Honor. I have a gift for you.', 0, 12, 0, 100, 396, 0, 30762, 61605, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 17, 0, 'Welcome to the Ring of Valor, pandaren. Make yourself comfortable while I finish preparing.', 0, 14, 0, 100, 0, 0, 30735, 61606, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 18, 0, 'Hah! It looks like we\'re ready. Now, newcomers... prepare to fight!', 0, 14, 0, 100, 0, 0, 30736, 61607, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 19, 0, 'This is my gift to you: a gift, and a trial.', 0, 14, 0, 100, 0, 0, 30737, 61609, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 20, 0, 'I\'ve heard stories about the legendary pandaren and their exotic fighting styles. Now I want to see them.', 0, 14, 0, 100, 0, 0, 30738, 61610, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 21, 0, 'This is a rare opportunity, pandaren. Prove your might to the Warchief of the Horde!', 0, 14, 0, 100, 0, 0, 30739, 61611, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62087, 22, 0, 'Lok\'tar ogar! Victory, or death!', 0, 14, 0, 100, 0, 0, 30740, 61612, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62202, 0, 0, '<cough> <hack> I have... returned.', 0, 12, 0, 100, 0, 0, 30299, 66031, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62205, 0, 0, 'You may have come this far. You may have carved a path through my army, but I... will kill you, and I will build the bridge.', 0, 14, 0, 100, 0, 0, 30359, 63348, 'Wing Leader Neronok - SAY_AGGRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62205, 1, 0, 'I will do what Pa\'valak could not!', 0, 14, 0, 100, 0, 0, 30360, 63347, 'Wing Leader Neronok - SAY_FLIGHT');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62205, 2, 0, 'Get back to work! I want this bridge finished by first light!', 0, 14, 0, 100, 0, 0, 30363, 63351, 'Wing Leader Neronok - SAY_WIPE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62205, 3, 0, 'The... bridge...', 0, 14, 0, 100, 0, 0, 30361, 63349, 'Wing Leader Neronok - SAY_DEATH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62205, 4, 0, 'Out of my way.', 0, 14, 0, 100, 0, 0, 30362, 63350, 'Wing Leader Neronok - SAY_SLAY');
