INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62385, 0, 0, 'You\'ll have to ask Mung-Mung about that. He maintains our foodstores.', 0, 12, 0, 100, 0, 0, 0, 56250, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62397, 0, 0, 'Your defiance of the Empress ends here!', 0, 14, 0, 100, 0, 0, 30335, 62853, 'MELJARAK_INTRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62397, 1, 0, 'All of Pandaria will fall beneath the Wings of the Empress!', 0, 14, 0, 100, 0, 0, 30336, 62854, 'MELJARAK_AGGRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62397, 2, 0, 'The Empress commands it!', 0, 14, 0, 100, 0, 0, 30338, 62856, 'MELJARAK_SLAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62397, 3, 0, 'Empress... I have... failed you...', 0, 14, 0, 100, 0, 0, 30337, 62855, 'MELJARAK_DEATH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62397, 4, 0, 'My blade never misses its mark!', 0, 14, 0, 100, 0, 0, 30340, 62858, 'MELJARAK_WHIRLING_BLADE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62397, 5, 0, 'The skies belong to the Empress!', 0, 14, 0, 100, 0, 0, 30341, 62859, 'MELJARAK_RAIN_BLADES');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62397, 6, 0, 'You fight like cowards!', 0, 14, 0, 100, 0, 0, 30333, 62866, 'MELJARAK_WATCHFULL_EYE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62397, 7, 0, 'To die for the empress is an honor!', 0, 14, 0, 100, 0, 0, 30345, 62863, 'MELJARAK_GROUP_DIE_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62397, 7, 1, 'The wings of the empress cannot fail!', 0, 14, 0, 100, 0, 0, 30346, 62864, 'MELJARAK_GROUP_DIE_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62397, 7, 2, 'You will pay for your transgressions!', 0, 14, 0, 100, 0, 0, 30347, 62865, 'MELJARAK_GROUP_DIE_3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62442, 3, 0, 'No... the waters... I must... resist... I shall not... fear...', 0, 14, 0, 100, 0, 0, 29359, 63272, 'Tsulong - SAY_INTRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62442, 4, 0, 'You do not belong here! The waters must be protected... I will cast you out, or slay you!', 0, 14, 0, 100, 0, 0, 29350, 63273, 'Tsulong - SAY_AGGRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62442, 5, 0, 'Flee this place... or die!', 0, 14, 0, 100, 0, 0, 29365, 63285, 'Tsulong - SAY_NIGHTMARES');
