INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62442, 8, 0, 'No...!', 0, 14, 0, 100, 0, 0, 29362, 63279, 'Tsulong - SAY_SLAY_DAY');
