INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62442, 9, 1, 'The night surrounds you...', 0, 14, 0, 100, 0, 0, 29361, 63277, 'Tsulong - SAY_SLAY_NIGHT');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62442, 10, 0, 'I thank you, strangers. I have been freed.', 0, 14, 0, 100, 0, 0, 29352, 63275, 'Tsulong - SAY_DEATH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62442, 11, 0, 'Protect... the waters...', 0, 14, 0, 100, 0, 0, 29351, 63274, 'Tsulong - SAY_WIPE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62465, 0, 0, 'YOU BETTER BE READY TO GET SOME OOK IN YER DOOKER!', 0, 14, 0, 100, 0, 0, 0, 61877, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62465, 1, 0, 'Captain Ook is comin\' down with the scurvy! Keep the orange away from him!', 0, 41, 0, 100, 0, 0, 0, 61859, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62467, 0, 0, 'Ah! Good! You made it. It is time to teach these "pirate" hozen why they should not raid our villages.', 0, 12, 0, 100, 0, 0, 0, 63624, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62467, 1, 0, 'The trick to dealing with pirate hozen is to have the element of surprise.', 0, 12, 0, 100, 1, 0, 0, 61850, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62467, 2, 0, 'Watch out!', 0, 12, 0, 100, 0, 0, 0, 61853, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62467, 3, 0, 'Okay... so... the new trick to dealing with pirate hozen is to do what you just did right there.', 0, 12, 0, 100, 0, 0, 0, 61851, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62467, 4, 0, 'Birds too?! How\'d they even learn to tame the birds?!', 0, 12, 0, 100, 0, 0, 0, 61862, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62467, 5, 0, 'Well look what we have here. Our first keg of recovered brew! I\'ll take this one, folks.', 0, 12, 0, 100, 0, 0, 0, 67373, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62467, 6, 0, 'We\'d better keep moving before this whole island finds out we\'re here.', 0, 12, 0, 100, 0, 0, 0, 61854, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62467, 7, 0, 'Hozen gladiators? Where did they get all of these weapons?', 0, 12, 0, 100, 0, 0, 0, 61855, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62467, 8, 0, 'Well! This looks like as good a place to brew as any.', 0, 12, 0, 100, 0, 0, 0, 61856, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62468, 0, 0, 'You are what\'s on the menu!', 0, 12, 0, 100, 0, 0, 0, 64784, '');
