INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62491, 0, 0, 'This cauldron will be perfect for brewing our... brew.', 0, 12, 0, 100, 1, 0, 0, 61872, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62491, 1, 0, 'Err... What was that?', 0, 12, 0, 100, 5, 0, 0, 65670, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62491, 2, 0, 'Phew! That was close. Ok, here\'s the deal...', 0, 12, 0, 100, 5, 0, 0, 65674, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62491, 3, 0, 'One of you, stay with me to keep the cauldron safe. The others, go find some Unga Jungle Brew and bring it back!', 0, 12, 0, 100, 1, 0, 0, 61898, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62491, 4, 0, 'Search the island to collect brew for Bo\'s cauldron! But be sure to leave one person nearby to defend Bo!', 0, 41, 0, 100, 1, 0, 0, 65966, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62491, 5, 0, 'You can use the bombs and cannons nearby to blast the hozen away!', 0, 41, 0, 100, 5, 0, 0, 65666, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62491, 6, 0, 'We need more brew. I think I saw brew guzzling hozen on the mountain peak to the southeast.', 0, 14, 0, 100, 0, 0, 0, 64025, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62491, 7, 0, 'DELICIOUS!', 0, 12, 0, 100, 7, 0, 0, 61873, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62491, 8, 0, 'Oh... my. That brew is a bit stronger than I had expected... I...', 0, 12, 0, 100, 64, 0, 0, 66242, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62491, 9, 0, 'Just need to rest my eyes a bit...', 0, 12, 0, 100, 12, 0, 0, 66243, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62491, 10, 0, 'Search the island to collect brew for Bo\'s cauldron! But be sure to leave one person nearby to defend Bo!', 2, 41, 0, 100, 1, 0, 0, 65966, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62491, 11, 0, 'You can use the bombs and cannons nearby to blast the hozen away!', 2, 41, 0, 100, 5, 0, 0, 65666, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62491, 12, 0, 'A hozen bandit is stealing the brew! Stop him!', 2, 41, 0, 100, 1, 0, 0, 65572, '');
