INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62511, 0, 0, 'Ah, I see you have found a few of my experiments.  Step closer, and you may witness the full glory of my work here.', 0, 14, 0, 100, 0, 0, 31559, 62227, 'UNSOK_INTRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62511, 1, 0, 'You must be powerful, to have come this far. Yes, yes... you will make worthy test subjects.', 0, 14, 0, 100, 0, 0, 31555, 62226, 'UNSOK_AGGRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62511, 2, 0, 'Watch your step!', 0, 14, 0, 100, 0, 0, 31563, 62235, 'UNSOK_SPELL_AMBER_SCALPEL');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62511, 3, 0, 'Feel fortunate to receive this gift.', 0, 14, 0, 100, 0, 0, 31562, 62233, 'UNSOK_SPELL_PARASITIC');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62511, 4, 0, 'From death comes life, and from life, death....', 0, 14, 0, 100, 0, 0, 31564, 62234, 'UNSOK_SPELL_ABOMINATION');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62511, 5, 0, 'You must think yourselves mighty. How little you know. Behold now, my greatest creation!', 0, 14, 0, 100, 0, 0, 31557, 62229, 'UNSOK_PHASE_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62511, 6, 0, 'What have you done? No, this will not be how my work ends. I will not allow it!', 0, 14, 0, 100, 0, 0, 31558, 62230, 'UNSOK_PHASE_3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62511, 7, 0, 'Excellent!', 0, 14, 0, 100, 0, 0, 31561, 62232, 'UNSOK_SLAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62511, 8, 0, 'Forgive me, Empress....', 0, 14, 0, 100, 0, 0, 31556, 62228, 'UNSOK_DEATH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62540, 0, 0, 'What... where am I?', 0, 12, 0, 100, 0, 0, 0, 62337, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62543, 0, 0, 'They approach? Good. Now, if only my students were here to see and learn from the coming display of martial prowess...', 0, 14, 0, 100, 0, 0, 29258, 64306, 'Tayak Intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62543, 1, 0, 'On your guard, invaders. I, Ta\'yak, Lord of Blades, will be your opponent.', 0, 14, 0, 100, 0, 0, 29252, 62941, 'Tayak Intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62543, 2, 0, 'I\'d always hoped to cut someone like that someday, to hear that sound. But to have it happen to my own prothorax is ridiculous.', 0, 14, 0, 100, 0, 0, 29253, 62944, 'Tayak Death');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62543, 3, 0, 'A perfect cut.', 0, 14, 0, 100, 0, 0, 29259, 62942, 'Tayak Kill');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62543, 3, 1, 'This is the technique of a Blade Lord.', 0, 14, 0, 100, 0, 0, 29260, 62943, 'Tayak Kill 2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62543, 4, 0, 'Mere initiates.', 0, 14, 0, 100, 0, 0, 29254, 64302, 'Tayak - 1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62543, 4, 1, 'They should have studied harder.', 0, 14, 0, 100, 0, 0, 29255, 64303, 'Tayak - 2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62543, 4, 2, 'One technique alone is not enough.', 0, 14, 0, 100, 0, 0, 29256, 64304, 'Tayak - 3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62543, 4, 3, 'They failed their test. Will you?', 0, 14, 0, 100, 0, 0, 29257, 64305, 'Tayak - 4');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62543, 5, 0, 'Can you follow my blade?', 0, 14, 0, 100, 0, 0, 29261, 66887, 'Tayak - Heroic Spell');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62543, 6, 0, 'Now go, impart my techniques to the initiates.', 0, 14, 0, 100, 0, 0, 0, 64272, 'Tayak - Intro');
