INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62837, 7, 0, '', 0, 14, 0, 100, 0, 0, 29268, 62892, 'SHEKZEER_DEATH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 1, 0, 'The Divine chose us to give mortal voice to Her divine will. We are but the vessel that enacts Her will.', 0, 14, 0, 100, 0, 0, 29301, 62465, 'Imperial Vizier Zor\'lok - TALK_AGGRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 2, 0, 'We will not give in to the despair of the dark void. If Her will for us is to perish, then it shall be so.', 0, 14, 0, 100, 0, 0, 29302, 62475, 'Imperial Vizier Zor\'lok - TALK_DEATH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 3, 0, 'We are the extension of our Empress\'s will.', 0, 14, 0, 100, 0, 0, 29303, 62452, 'Imperial Vizier Zor\'lok - TALK_EVENT_IDLE_1A');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 4, 0, 'Ours is but to serve in Her divine name.', 0, 14, 0, 100, 0, 0, 29304, 62453, 'Imperial Vizier Zor\'lok - TALK_EVENT_IDLE_1B');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 5, 0, 'Never to question, nor to contemplate; we simply act.', 0, 14, 0, 100, 0, 0, 29305, 62454, 'Imperial Vizier Zor\'lok - TALK_EVENT_IDLE_1C');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 6, 0, 'We fight, toil, and serve so that Her vision for us is made reality.', 0, 14, 0, 100, 0, 0, 29306, 62455, 'Imperial Vizier Zor\'lok - TALK_EVENT_IDLE_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 7, 0, 'Her happiness is our reward, her sorrow our failure.', 0, 14, 0, 100, 0, 0, 29307, 62456, 'Imperial Vizier Zor\'lok - TALK_EVENT_IDLE_3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 8, 0, 'We will give our lives for the Empress without hesitation. She is the light, and without Her our lives will be lost to darkness.', 0, 14, 0, 100, 0, 0, 29308, 62457, 'Imperial Vizier Zor\'lok - TALK_EVENT_IDLE_4');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 9, 0, 'Her gifts are many, our gratitude immeasurable.', 0, 14, 0, 100, 0, 0, 29309, 62472, 'Imperial Vizier Zor\'lok - TALK_EVENT_PHASE_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 10, 0, 'She tests us. We will not disappoint Her.', 0, 14, 0, 100, 0, 0, 29310, 62473, 'Imperial Vizier Zor\'lok - TALK_EVENT_PHASE_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 11, 0, 'We are as unwavering as the amber that shapes our halls. With Her might we will vanquish all who dare intrude upon Her divine home.', 0, 14, 0, 100, 0, 0, 29311, 62474, 'Imperial Vizier Zor\'lok - TALK_EVENT_PHASE_3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 12, 0, 'The chaff of the world tumbles across our doorstep, driven by fear; Her royal storm will whisk them away.', 0, 14, 0, 100, 0, 0, 29312, 62458, 'Imperial Vizier Zor\'lok - TALK_EVENT_TRASH_A_COMBAT');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 13, 0, 'They were clearly unworthy of Her divine embrace.', 0, 14, 0, 100, 0, 0, 29313, 62461, 'Imperial Vizier Zor\'lok - TALK_EVENT_TRASH_A_DIES');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 14, 0, 'They are but the waves crashing upon the mountain of Her divine will. They may rise again and again; but will accomplish nothing.', 0, 14, 0, 100, 0, 0, 29314, 62459, 'Imperial Vizier Zor\'lok - TALK_EVENT_TRASH_B_COMBAT');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 15, 0, 'We are unfazed. We will stand firm.', 0, 14, 0, 100, 0, 0, 29315, 62462, 'Imperial Vizier Zor\'lok - TALK_EVENT_TRASH_B_DIES');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 16, 0, 'The Divine challenges us to face these intruders.', 0, 14, 0, 100, 0, 0, 29316, 62460, 'Imperial Vizier Zor\'lok - TALK_EVENT_TRASH_C_COMBAT');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 17, 0, 'And so it falls to us, Her chosen voice.', 0, 14, 0, 100, 0, 0, 29317, 62463, 'Imperial Vizier Zor\'lok - TALK_EVENT_TRASH_C_DIES');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 18, 0, 'The Divine will not suffer your taint here, outsider.', 0, 14, 0, 100, 0, 0, 29318, 62466, 'Imperial Vizier Zor\'lok - TALK_SLAY_01');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 19, 0, 'Ours is but to serve; yours is but to die.', 0, 14, 0, 100, 0, 0, 29319, 62467, 'Imperial Vizier Zor\'lok - TALK_SLAY_02');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 20, 0, 'Behold the voice of Her divine wrath.', 0, 14, 0, 100, 0, 0, 29320, 62469, 'Imperial Vizier Zor\'lok - TALK_EXHALE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 21, 0, 'Through the air we breathe, She strengthens our voice.', 0, 14, 0, 100, 0, 0, 29321, 62468, 'Imperial Vizier Zor\'lok - TALK_INHALE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 22, 0, 'Her will is greater than us all. You will join us or you will perish.', 0, 14, 0, 100, 0, 0, 29322, 62471, 'Imperial Vizier Zor\'lok - TALK_CONVERT');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 23, 0, 'Our voice gives form to Her will.', 0, 14, 0, 100, 0, 0, 29323, 62470, 'Imperial Vizier Zor\'lok - TALK_PITCH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 24, 0, 'Imperial Vizier Zor\'lok flies to one of his platforms!', 0, 41, 0, 100, 0, 0, 0, 63770, 'Imperial Vizier Zor\'lok - TALK_FLY_ANN');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 25, 0, 'Imperial Vizier Zor\'lok inhales the Pheromones of Zeal!', 0, 41, 0, 100, 0, 0, 0, 63394, 'Imperial Vizier Zor\'lok - TALK_INHALE_ZEAL_ANN');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 28, 0, '|TInterface\\Icons\\warrior_disruptingshout.blp:20|tImperial Vizier Zor\'lok shouts with |cFFFF0000|Hspell:122713|h[Force and Verve]|h|r!', 0, 41, 0, 100, 0, 0, 0, 63774, 'Imperial Vizier Zor\'lok - TALK_FORCE_AND_VERVE_ANN');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62980, 27, 0, '|TInterface\\Icons\\spell_arcane_mindmastery.blp:20|tImperial Vizier Zor\'lok uses his voice to |cFFFF0000|Hspell:122740|h[Convert]|h|r members of the raid to fight on his side!', 0, 41, 0, 100, 0, 0, 0, 63776, 'Imperial Vizier Zor\'lok - TALK_CONVERT_ANN');
