INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62983, 1, 0, 'Wh-what are you doing here?! G-go away!', 0, 14, 0, 100, 0, 0, 29328, 63176, 'Lei Shi - AGGRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62983, 2, 0, 'I\'m hiding until you leave!', 0, 41, 0, 100, 0, 0, 29333, 63198, 'Lei Shi - HIDE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62983, 2, 1, 'I don\'t want to see you anymore!', 0, 41, 0, 100, 0, 0, 29334, 63199, 'Lei Shi - HIDE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62983, 3, 0, 'Get AWAY!', 0, 14, 0, 100, 0, 0, 29331, 63196, 'Lei Shi - GET AWAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62983, 3, 1, 'Stay away from me!', 0, 14, 0, 100, 0, 0, 29332, 63197, 'Lei Shi - GET AWAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62983, 4, 0, 'S-sorry!', 0, 14, 0, 100, 0, 0, 29329, 63194, 'Lei Shi - SLAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62983, 4, 1, 'It\'s your fault it happened!', 0, 14, 0, 100, 0, 0, 29330, 63195, 'Lei Shi - SLAY');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62983, 5, 0, 'Wh-what are you doing here?! G-go away!', 0, 14, 0, 100, 0, 0, 29328, 63176, 'Lei Shi - INTRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62983, 6, 0, 'I... ah... oh! Did I...? Was I...? It was... so... cloudy.', 0, 14, 0, 100, 0, 0, 29325, 62834, 'Lei Shi - DEFEAT');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62983, 7, 0, 'I have to go now.', 0, 14, 0, 100, 0, 0, 29326, 62835, 'Lei Shi - OUTRO_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62983, 8, 0, 'I have to make the water not cloudy anymore. Will... will you help?', 0, 14, 0, 100, 0, 0, 29327, 62836, 'Lei Shi - OUTRO_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62988, 0, 0, 'You have my gratitude!', 0, 12, 0, 100, 0, 0, 0, 60964, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62989, 0, 0, 'You have my gratitude!', 0, 12, 0, 100, 0, 0, 0, 60964, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (62990, 0, 0, 'You have my gratitude!', 0, 12, 0, 100, 0, 0, 0, 60964, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63013, 0, 0, 'I will give you a free meal if you can get this dwarf off of my new chair!', 0, 12, 0, 100, 1, 0, 0, 66752, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63179, 0, 0, 'This amber is ours, trespasser! Amber not for the mantid!', 0, 12, 0, 100, 0, 0, 0, 62655, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63217, 0, 0, 'This stone tells of a great hozen warlord who lived here twenty-five hundred years ago. What are your leaders like? Poets? Warriors? I should like to meet them someday.', 0, 12, 0, 100, 6, 0, 30072, 62703, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63217, 1, 0, 'Prepare yourself! Saurok raiders approach!', 0, 12, 0, 100, 430, 0, 30073, 62705, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63217, 2, 0, 'The saurok have plagued this land since the time of the mogu. They are impossible to negotiate with. But we never stop trying! We fight them only as a last resort.', 0, 12, 0, 100, 396, 0, 30074, 62712, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63217, 3, 0, 'Thank you for your help - these notes are important to my studies. Your people value knowledge too, yes? I assume so, only because you arrived on a great airship.', 0, 12, 0, 100, 396, 0, 30075, 62716, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63256, 0, 0, 'Heyarrrrrrrr!', 0, 12, 0, 100, 0, 0, 0, 65167, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63307, 4, 0, 'We Pandaren do not worship our ancestors, but we revere their memory and the wisdom that they have passed on to us.', 0, 12, 0, 100, 396, 0, 31776, 62760, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63307, 3, 0, 'Ah, this is a special place. My great grandparents called this part of the forest their home. Come, this will take only a moment.', 0, 12, 0, 100, 396, 0, 31774, 62731, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63307, 0, 0, 'I have everything I need. Quickly, let\'s move on before they return!', 0, 12, 0, 100, 396, 0, 30076, 62717, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63307, 1, 0, 'Enough about pandaren history. Let\'s talk about your people, traveler.', 0, 12, 0, 100, 396, 0, 31772, 62718, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63307, 2, 0, 'I\'ve learned a great deal already just by watching you in action. You fight with the fervor of youth but the experience of an old veteran. You\'ve seen a lifetime of warfare, haven\'t you?', 0, 12, 0, 100, 396, 0, 31773, 62719, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63307, 5, 0, 'It was my Great Grandfather who first told me the tale of the mogu that I recently shared with you.', 0, 12, 0, 100, 396, 0, 31775, 62761, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63315, 0, 0, 'Welcome to our first officially-sanctioned combat event, the Arena of Annihilation!', 0, 14, 0, 100, 0, 0, 0, 62842, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63315, 1, 0, 'Are you ready to display your martial prowess here, in the eye of The Tiger?', 0, 14, 0, 100, 0, 0, 0, 63395, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63315, 2, 0, 'Then HIT THAT GONG, and show these monks what you\'re made of! ...one way or another, heh.', 0, 14, 0, 100, 0, 0, 0, 62847, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63315, 3, 0, 'For our first show of skill and power we bring you SCAR-SHELL, mightiest of dragon turtles!', 0, 14, 0, 100, 0, 0, 0, 62915, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63315, 4, 0, 'Whew, what a mess! Someone inform the monastery kitchens: We\'re having turtle soup, tonight!', 0, 14, 0, 100, 0, 0, 0, 62916, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63315, 5, 0, 'Ready for the next fight? Ring that gong and GET! IT! ON!', 0, 14, 0, 100, 0, 0, 0, 63396, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63315, 6, 0, 'It\'s time for the grummle-grubbing goliath, fifteen feet of furry fury, Kun-Lai Summit\'s own snow-stalking horror, JOL\'GRUM!', 0, 14, 0, 100, 0, 0, 0, 62917, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63315, 7, 0, 'OUCH! Safe to say that Jol\'Grum wasn\'t quite yet-i ready for his battle with these three killing machines! Speaking of ready...', 0, 14, 0, 100, 0, 0, 0, 62918, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63315, 8, 0, 'She\'s here to make new friends... and then set them on fire! It\'s Pandaria\'s prettiest little playful pyromaniac, Liuyang!', 0, 14, 0, 100, 0, 0, 0, 62919, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63315, 9, 0, 'Snuffed out like a candle! Is there anyone these three can\'t beat?!', 0, 14, 0, 100, 0, 0, 0, 62920, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63315, 10, 0, 'Did someone say Yak Attack?! It\'s ax-master Chagan Firehoof, wandering warrior of the Townlong Steppes!', 0, 14, 0, 100, 0, 0, 0, 62921, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63315, 11, 0, 'What a battle! Our fine combatants served up Chagan a heaping helping of Yaungolian barbeque...', 0, 14, 0, 100, 0, 0, 0, 62922, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63315, 12, 0, 'Flitting through the mists with death upon his fists comes the sinister and serene jinyu assassin: Satay Byu!', 0, 14, 0, 100, 0, 0, 0, 65632, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63315, 13, 0, 'Ooo, that\'s one killer carp whose slaying days have come to a fishy end.', 0, 14, 0, 100, 0, 0, 0, 65633, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63315, 14, 0, 'Everyone, please cheer for the NEW CHAMPIONS of the Arena of Annihilation!', 0, 14, 0, 100, 0, 0, 0, 65636, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63359, 0, 0, 'Skeer the Bloodseeker has returned! Unnngghhh...', 0, 12, 0, 100, 0, 0, 0, 62832, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63359, 1, 0, 'Yes, Klaxxi\'va. What does the Council command? Skeer hears. Skeer obeys.', 0, 12, 0, 100, 0, 0, 0, 62833, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63359, 2, 0, 'It appears that Empress Shek\'zeer has let our empire fall into chaos. This we must address. But first, I must regain my strength.', 0, 12, 0, 100, 0, 0, 0, 66451, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63420, 0, 0, 'The cavern trembles violently!', 0, 41, 0, 100, 0, 0, 0, 72594, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63420, 1, 0, 'An ancient beast stirs within the mists!', 0, 41, 0, 100, 0, 0, 0, 72595, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63420, 2, 0, '|cFFF00000Megaera|r rises from the mists!', 0, 41, 0, 100, 0, 0, 0, 72587, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63420, 3, 0, '|TInterface\\Icons\\inv_misc_volatilefire:20|tYou are afflicted with |cFFF00000|Hspell:139822|h[Cinders]|h|r!', 0, 41, 0, 100, 0, 0, 0, 72966, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63420, 4, 0, '|TInterface\\Icons\\spell_frost_piercing chill:20|tYou are being targeted by |cFFF00000|Hspell:139866|h[Torrent of Ice]|h|r!', 0, 41, 0, 100, 0, 0, 0, 72965, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63420, 5, 0, '|TInterface\\Icons\\trade_archaeology_whitehydrafigurine:20|tMegaera begins to |cFFF00000|Hspell:139458|h[Rampage]|h|r!', 0, 41, 0, 100, 0, 0, 0, 72655, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63420, 6, 0, 'Megaera\'s rage subsides.', 0, 41, 0, 100, 0, 0, 0, 72748, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63520, 0, 0, '|TInterface\\Icons\\creatureportrait_bubble.blp:20|t%s casts |cFFFF0000|Hspell:124653|h[Water Shell]|h|r! Use the Water Spouts to pop it!', 0, 41, 0, 100, 0, 0, 0, 63642, 'lite shield');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63528, 0, 0, 'Warbring Qobi dropped his axes. Grab one!', 0, 14, 0, 100, 0, 0, 0, 64112, 'qobi waraxe');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63535, 0, 0, 'Someone has come to save us? Perhaps all is not lost...', 0, 12, 0, 100, 1, 0, 0, 67232, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63542, 0, 0, 'Someone has come to save us? Perhaps all is not lost...', 0, 12, 0, 100, 1, 0, 0, 67232, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63751, 0, 0, 'I don\'t know about this "Horde", but you look trustworthy. I\'ll gather everyone up and meet you when we\'re ready to rebuild!', 0, 12, 0, 100, 1, 0, 0, 67236, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63754, 0, 0, 'I don\'t know about this "Alliance", but you look trustworthy. I\'ll gather everyone up and meet you when we\'re ready to rebuild!', 0, 12, 0, 100, 1, 0, 0, 67234, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63811, 0, 0, 'Thanks, $p! I thought I was done for.', 0, 14, 0, 100, 0, 0, 0, 63621, 'intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63811, 1, 0, 'I panicked when I saw all those saurok and wrecked my boat.', 0, 14, 0, 100, 0, 0, 0, 63622, 'brewing');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63812, 0, 0, '', 0, 14, 0, 100, 0, 0, 0, 63628, 'aph intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63818, 0, 0, '', 0, 14, 0, 100, 0, 0, 0, 63630, 'hiseu intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63818, 1, 0, 'A Warbringer approaches! Fall back!', 0, 14, 0, 100, 0, 0, 0, 67649, 'hiseu qobi intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63942, 0, 0, 'No more excuses, Empress! Eliminate these cretins or I will kill you myself!', 0, 14, 0, 100, 0, 0, 32647, 64111, 'SHA_AGGRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63942, 1, 0, 'You must destroy these outsiders, empress! They have come to conquer your people!', 0, 14, 0, 100, 0, 0, 32648, 62905, 'SHA_INTRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63942, 2, 0, 'USELESS! Fall limp and die before me, wretched insect. Your kind are worthless to me!', 0, 14, 0, 100, 0, 0, 32645, 62906, 'SHA_SHEKZEER_DEFEAT_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63942, 3, 0, 'As for the rest of you, seek me if you dare. Like the others, you will all die screaming.', 0, 14, 0, 100, 0, 0, 32646, 62907, 'SHA_SHEKZEER_DEFEAT_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63955, 0, 0, '%s swims excitedly through the vents of warm water.', 0, 16, 0, 100, 0, 0, 0, 63810, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63955, 1, 0, '%s rubs against the prow of the Mist-Hopper and whimpers.', 0, 16, 0, 100, 0, 0, 0, 63811, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (63955, 2, 0, '%s looks around cautiously before leaving his mark on the whale corpse.', 0, 16, 0, 100, 0, 0, 0, 63812, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64071, 0, 0, '', 0, 12, 0, 100, 1, 0, 0, 66877, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64072, 0, 0, 'How\'s about another round?', 0, 12, 0, 100, 1, 0, 0, 76713, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64072, 0, 1, 'Well played, sir.', 0, 12, 0, 100, 1, 0, 0, 76712, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64115, 0, 0, 'Hrmph. Nice card.', 0, 12, 0, 100, 1, 0, 0, 76716, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64115, 0, 1, 'Alright, alright, you win.', 0, 12, 0, 100, 1, 0, 0, 76724, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64115, 0, 2, 'Best two out of three!', 0, 12, 0, 100, 1, 0, 0, 76719, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64149, 0, 0, '', 0, 12, 0, 100, 1, 0, 0, 64897, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64164, 0, 0, 'Fear not if your weapon leaves your grasp. Your body is an ample tool against any foe.', 0, 12, 0, 100, 1, 0, 0, 66774, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64185, 0, 0, 'A Bataari War Yeti approaches from the Ancient Passage!', 0, 14, 0, 100, 0, 0, 0, 63945, 'yeti passage');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64185, 1, 0, 'A Bataari War Yeti approaches from the Bridge!', 0, 14, 0, 100, 0, 0, 0, 63946, 'yeti bridge');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64185, 2, 0, 'A Bataari War Yeti approaches from the west!', 0, 14, 0, 100, 0, 0, 0, 63947, 'yeti west');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64312, 0, 0, 'Hmmm, I reckon I don\'t know, $r. All I can understand is the part about the valley - that\'s right here! The Valley of the Four Winds!', 0, 12, 0, 100, 0, 0, 0, 64254, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64313, 0, 0, '', 0, 12, 0, 100, 0, 0, 0, 64252, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64315, 0, 0, 'Oh, I HAVE heard of the ancestors\' stair. That\'s what some folks call those mountains to the north.', 0, 12, 0, 100, 0, 0, 0, 64255, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64327, 0, 0, 'What echoes and what\'s dark? It\'s simple! A cave!', 0, 12, 0, 100, 0, 0, 0, 64253, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64347, 0, 0, '', 0, 12, 0, 100, 0, 0, 0, 64246, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64347, 1, 0, '', 0, 12, 0, 100, 0, 0, 0, 64251, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64361, 0, 0, 'Uncle Gao, this brewery was left in your care! What have you done?', 0, 12, 0, 100, 0, 0, 31998, 61478, 'Chen Stormstout to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64361, 1, 0, 'At what cost? The Brewery is trashed! Infested!', 0, 12, 0, 100, 0, 0, 29773, 61479, 'Chen Stormstout to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64361, 2, 0, 'And there are Virmen in the main store!', 0, 12, 0, 100, 0, 0, 29774, 63022, 'Chen Stormstout to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64361, 3, 0, 'I think the brewery might be on fire.', 0, 12, 0, 100, 0, 0, 29775, 63023, 'Chen Stormstout to Player');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64389, 0, 0, 'Mmmmm! I smell a delicious sheep!', 0, 14, 0, 100, 0, 0, 0, 64357, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64389, 1, 0, 'Time to munch on some mutton!', 0, 14, 0, 100, 0, 0, 0, 64360, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64528, 0, 0, 'Ah, another stranger from beyond the mists.', 0, 12, 0, 100, 396, 0, 29030, 65214, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64528, 1, 0, 'Ten thousand years ago, the last Pandaren Emperor came seeking my advice. He, too, was filled with conflict. Let us see if you are correct, Taran. Let us see how this stranger deals with turmoil of the soul.', 0, 12, 0, 100, 396, 0, 29031, 65215, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64528, 2, 0, 'We can agree it is noble to fight for a righteous cause: to defend home or family. But what of vengeance? What of anger? I sense these emotions run deep in this one.', 0, 12, 0, 100, 396, 0, 29033, 65273, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64528, 3, 0, 'Well spoken, Zhi. The strangers from beyond the mists have no shortage of passions, but let us see if they indeed control them. Beyond anger lies hatred, the wellspring of all misery. What is this? Your hatred has a face!', 0, 12, 0, 100, 396, 0, 29035, 65326, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64528, 4, 0, 'This one has indeed proven mastery over anger. Between the young cub\'s words and the actions of this hero, I think I understand. Those from beyond the mists are hardened by battle. They are scarred, yes, but they have learned much. I trust them.', 0, 12, 0, 100, 396, 0, 29037, 65350, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64536, 0, 0, 'Soon they will overwhelm this continent! Where do we draw the line?', 0, 12, 0, 100, 396, 0, 28417, 65216, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64536, 1, 0, 'We have witnessed your "help" in the Jade Forest. This "Horde," this "Alliance" of yours - you have no control over your own nature. You leave misery in your wake.', 0, 12, 0, 100, 396, 0, 28418, 65217, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64536, 2, 0, 'Violence. See? They are filled with it.', 0, 12, 0, 100, 396, 0, 28419, 65248, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64536, 3, 0, 'All you seek to conquer is each other!', 0, 12, 0, 100, 396, 0, 28420, 65323, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64536, 4, 0, 'See? See how these outsiders hate one another! They wear their hatred to battle and carry it to their graves!', 0, 12, 0, 100, 396, 0, 28421, 65327, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64537, 0, 0, 'It is true. They are strong. There is much they can teach us.', 0, 12, 0, 100, 5, 0, 29050, 65258, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64537, 1, 0, 'To live without anger is not to live at all. It is ours to overcome. In defeating our anger, we draw great strength.', 0, 12, 0, 100, 5, 0, 29051, 65324, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64537, 2, 0, 'How interesting. They bring much to our land, but they also bring the shadow of war.', 0, 12, 0, 100, 5, 0, 29052, 65329, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64540, 0, 0, 'We\'re here to lend our aid. We can help you rebuild the Vale.', 0, 12, 0, 100, 396, 0, 38017, 75749, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64540, 1, 0, 'Yes... we are fighters. But we come from a violent land. We fought off demons, we fought off death, we even fought against the very end of the world!', 0, 12, 0, 100, 396, 0, 27589, 65251, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64540, 2, 0, 'Anger is a part of us all, yes. But we value control. The orcs were once driven solely by anger - they lived in a state of bloodlust - but they conquered it.', 0, 12, 0, 100, 396, 0, 27590, 65322, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64540, 3, 0, 'That... that looks just like Garrosh Hellscream!', 0, 12, 0, 100, 396, 0, 27592, 65341, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64540, 4, 0, 'No, that\'s not true - there is so much more to us than this!', 0, 12, 0, 100, 396, 0, 27593, 65328, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64540, 5, 0, 'Peace will come. I swear it. And old hatreds will go away. You have to believe me.', 0, 12, 0, 100, 396, 0, 27594, 65330, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64562, 0, 0, 'Here we go. Do try to keep yourselves alive until the end.', 0, 12, 0, 100, 0, 0, 0, 65072, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64562, 1, 0, 'We\'re coming for you, Chillheart! Time for payback!', 0, 14, 0, 100, 0, 0, 0, 65074, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64562, 2, 0, 'The Talking Skull cackles gleefully as the instructor\'s soul is destroyed.', 0, 16, 0, 100, 0, 0, 0, 65077, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64562, 3, 0, 'The Talking Skull seems hesitant to enter the hall, but proceeds with you nonetheless.', 0, 16, 0, 100, 0, 0, 0, 65078, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64562, 4, 0, 'I can\'t bear to watch.', 0, 12, 0, 100, 0, 0, 0, 65085, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64562, 5, 0, 'The Talking Skull proceeds into the next chamber without saying a word.', 0, 16, 0, 100, 0, 0, 0, 65086, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64562, 6, 0, 'Die, Gandling! Kill him!', 0, 14, 0, 100, 0, 0, 0, 65149, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64562, 7, 0, 'NO! Get back here coward!', 0, 14, 0, 100, 0, 0, 0, 65203, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64562, 8, 0, 'Let\'s go!', 0, 12, 0, 100, 0, 0, 0, 65207, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64562, 9, 0, 'There he is! Kill him now, $p!', 0, 14, 0, 100, 0, 0, 0, 65213, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64562, 10, 0, 'Stop him! Forget about her!', 0, 14, 0, 100, 0, 0, 0, 65221, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64562, 11, 0, 'PREPARE TO DIE, VILLAIN!', 0, 14, 0, 100, 0, 0, 0, 65253, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64597, 0, 0, '', 0, 12, 0, 100, 0, 0, 0, 65914, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64727, 0, 0, '', 0, 12, 0, 100, 0, 0, 0, 64956, 'Jaina - Intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64727, 1, 0, '', 0, 12, 0, 100, 0, 0, 0, 64957, 'Jaina - 5td chapter Done');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64727, 2, 0, '', 0, 14, 0, 100, 0, 0, 0, 64958, 'Jaina - Slain');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64727, 3, 0, '', 0, 14, 0, 100, 0, 0, 0, 64959, 'Jaina - Aggro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64738, 0, 0, '', 0, 12, 0, 100, 0, 0, 0, 65384, 'HOODED_CRUSADER_INTRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64738, 1, 0, '', 0, 12, 0, 100, 0, 0, 0, 65385, 'HOODED_CRUSADER_CODEX');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64812, 0, 0, 'Very good. Let\'s talk.', 0, 12, 0, 100, 0, 0, 29136, 65088, 'WRATHION_INTRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64812, 1, 0, 'Tong! Drinks, please.', 0, 12, 0, 100, 0, 0, 29137, 65089, 'TONG_DRINKS');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64812, 2, 0, 'My father, Deathwing, tried to destroy the whole of Azeroth.', 0, 12, 0, 100, 0, 0, 29138, 65090, 'ABOUT_DEATHWING_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64812, 3, 0, 'He was misguided, of course, but he was right about one thing: Our world is... so fragile.', 0, 12, 0, 100, 0, 0, 29139, 65091, 'ABOUT_DEATHWING_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64812, 4, 1, 'We are a point of light in a universe of shadow. A candle in a tempest.', 0, 12, 0, 100, 0, 0, 29140, 65092, 'AZEROTH_SCENE_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64812, 5, 0, 'Sometimes I think it was the very precariousness of our world that drove my father to madness.', 0, 12, 0, 100, 0, 0, 29141, 65093, 'AZEROTH_SCENE_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64812, 6, 0, 'Ah, thank you Tong.', 0, 12, 0, 100, 0, 0, 29142, 65094, 'TONG_HOLDING_CUP');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64812, 7, 0, 'Now, to my point: I believe we are headed towards a reckoning. And no, I am not talking about the current conflict between the Alliance and the Horde.', 0, 12, 0, 100, 0, 0, 29143, 65095, 'WRATHION_OPINION');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64812, 8, 0, 'Believe me - what Garrosh Hellscream achieved in Theramore is nothing compared to the horrors that are even now bearing down on our fragile home.', 0, 12, 0, 100, 0, 0, 29144, 65096, 'ABOUT_GARROSH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64812, 9, 0, 'But the war deeply troubles me. Do you see my concern?', 0, 12, 0, 100, 0, 0, 29145, 65097, 'AZEROTH_SCENE_3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64812, 10, 0, 'A divided Azeroth cannot possibly stand against the darkness.', 0, 12, 0, 100, 0, 0, 29146, 65098, 'AZEROTH_SCENE_4');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64812, 11, 0, 'This war has to end. Soon. Before it consumes our strength!', 0, 12, 0, 100, 0, 0, 29147, 65099, 'ABOUT_WAR');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64812, 12, 0, 'You can rest assured that my loyalties lie with your Horde.', 0, 12, 0, 100, 0, 0, 29149, 65101, 'FACTION_H');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64812, 13, 0, 'I want you to know that your Alliance has my full and unwavering support in this campaign.', 0, 12, 0, 100, 0, 0, 29148, 65100, 'FACTION_A');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64812, 14, 0, 'How do we bring a swift and decisive end to the conflict? I believe the answer lies with heroes like you.', 0, 12, 0, 100, 0, 0, 29150, 65102, 'ABOUT_CONFLICT');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64812, 15, 0, 'We must ensure that you are up to the task... and then equip you accordingly.', 0, 12, 0, 100, 0, 0, 29151, 65103, 'ABOUT_GEARED');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64822, 0, 0, 'I often venture out here to think. The view is breathtaking, would you agree?', 0, 12, 0, 100, 0, 0, 29124, 65109, 'WRATHION_INTRO_VENTURE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64822, 1, 0, 'Pandaria is indeed a jewel. Such beauty. It makes you want to protect her, yes?', 0, 12, 0, 100, 0, 0, 29125, 65110, 'WRATHION_ABOUT_PANDARIA');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64822, 2, 0, 'I feel the same way about Azeroth herself.', 0, 12, 0, 100, 0, 0, 29126, 65111, 'WRATHION_ABOUT_PROTECT_AZEROTH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64822, 3, 0, 'You have done great things, friend. I can assure you this is but the beginning of our work together.', 0, 12, 0, 100, 0, 0, 29127, 65112, 'WRATHION_GREAT_THINGS');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64822, 4, 0, 'Behold: The power of the foes you\'ve vanquished.', 0, 12, 0, 100, 0, 0, 29128, 65113, 'WRATHION_POWER_OF_ENEMIES');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64822, 5, 0, 'And the wisdom of those you\'ve outsmarted.', 0, 12, 0, 100, 0, 0, 29129, 65114, 'WRATHION_WISDOM_OF_ENEMIES');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64822, 6, 0, 'Here! The embodiment of the fear that you\'ve conquered. May it now strike terror into the hearts of your enemies!', 0, 12, 0, 100, 0, 0, 29130, 65115, 'WRATHION_TERROR_OF_ENEMIES');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64822, 7, 0, 'Finally, a gift from me. Make no mistake: the Black Dragonflight lives on!', 0, 12, 0, 100, 0, 0, 29131, 65116, 'WRATHION_BREATH_TO_GEM');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64822, 8, 0, 'There. My work is done. You can now greatly augment the power of any sha-touched weapon.', 0, 12, 0, 100, 0, 0, 29132, 65117, 'WRATHION_WORK_IS_DONE');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64838, 1, 0, 'Destroy the Piles of Corpses to stop the Zombified Corpses!', 0, 41, 0, 100, 0, 0, 0, 61734, 'INSTANCE_INTRO_ANN');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64901, 0, 0, 'Good to see you, $n! Do you have a minute?', 0, 14, 0, 100, 0, 0, 0, 62265, 'brewmaster intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64901, 1, 0, 'That\'s the last of the ingredients! Good work, $p! Now the festival can begin!', 0, 14, 0, 100, 0, 0, 0, 62249, 'brewmaster breewing');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64901, 2, 0, 'Kieu, Tart, Buzaki toss the ingredients in!', 0, 14, 0, 100, 0, 0, 0, 62273, 'brewmaster ingredients');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64901, 3, 0, 'Now comes the hard part. Everyone stand back, precision is the key to the perfect Kun-Lai Kicker!', 0, 14, 0, 100, 0, 0, 0, 62250, 'brewmaster process');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64901, 4, 0, 'Yaungol!? Now!?', 0, 14, 0, 100, 0, 0, 0, 63826, 'brewmaster yaungols');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64901, 5, 0, 'They must be after the Kun-Lai Kicker! $p, investigate these sightings and setup the village defenses! They won\'t get my brew without a fight!', 0, 14, 0, 100, 0, 0, 0, 63829, 'brewmaster defence 2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64901, 6, 0, 'I\'ll defend the brew until it\'s ready. Then we can use it against these Bataari raiders! Everyone else, get to safety!', 0, 14, 0, 100, 0, 0, 0, 63830, 'brewmaster defence 3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64901, 7, 0, 'The Kun-Lai Kicker is finished, $p! Now we can use it to kick some yaungol butt!', 0, 14, 0, 100, 0, 0, 0, 64082, 'brewmaster done');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64901, 8, 0, 'Good work, $p, you saved the village. We are forever in your debt.', 0, 14, 0, 100, 0, 0, 0, 65704, 'brewmaster end scenario');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (64946, 0, 0, 'Mmmmm. Mmmmmm...... This is... simply put... amazing. Your friend Ella has real talent! Tell her to speak to my Uncle Gao at the Brewery. I think she has found a new home with us Stormstouts.', 0, 12, 0, 100, 0, 0, 0, 65916, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65153, 0, 0, 'Hold your blade, adventurer!', 0, 14, 0, 100, 0, 0, 0, 47004, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65153, 1, 0, 'This beast leads the Riverpaw gnoll gang and may be the key to ending gnoll aggression in Elwynn.', 0, 12, 0, 100, 25, 0, 0, 47007, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65153, 2, 0, 'We\'re taking him into custody in the name of King Varian Wrynn.', 0, 12, 0, 100, 1, 0, 0, 47008, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65153, 3, 0, 'Take us to the Stockades, Andromath.', 0, 12, 0, 100, 25, 0, 0, 47009, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65271, 0, 0, 'Someone! Please help!', 0, 14, 0, 100, 20, 0, 0, 60969, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65271, 1, 0, 'Many thanks!', 0, 12, 0, 100, 0, 0, 0, 60684, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65271, 2, 0, '', 0, 12, 0, 100, 1, 0, 0, 67528, '');
