INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65442, 0, 0, 'NO ONE CAN PROTECT YOU!  Kill the Proudmoore wench, and bring me that bomb.', 0, 14, 0, 100, 0, 0, 0, 64960, 'Warlord - Intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65442, 1, 0, 'Brave words, mage.  I\'ll SPIT in your FACE when you beg for mercy.', 0, 14, 0, 100, 0, 0, 0, 64962, 'Warlord - Aggro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65475, 0, 0, 'Is he gone? Is it over?', 0, 12, 0, 100, 0, 0, 0, 66531, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65478, 0, 0, 'Give me the resonating crystal you puny little bark eater!', 0, 14, 0, 100, 0, 0, 0, 66524, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65501, 0, 0, 'Pitiful, wingless grubs. You step out into my domain, and I will hear your death screams as you plummet from this terrace!', 0, 14, 0, 100, 0, 0, 30348, 66433, 'MELJARAK_INTRO_EVENT_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65501, 1, 0, 'Set\'thik, in formation! Show them who controls the winds of Pandaria!', 0, 14, 0, 100, 0, 0, 30349, 66434, 'MELJARAK_INTRO_EVENT_2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65501, 2, 0, 'Breath of the empress, bring mighty Garalon to us!', 0, 14, 0, 100, 0, 0, 30350, 66435, 'MELJARAK_INTRO_EVENT_SUMMON_GARALON');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65501, 3, 0, 'Garalon, grind their bones to dust!', 0, 14, 0, 100, 0, 0, 30351, 66436, 'MELJARAK_INTRO_EVENT_GARALON_COMBAT');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65501, 4, 0, 'Impossible! Sri\'thik, Zar\'thik, Kor\'thik, to the top of the terrace! We must defend the Empress!', 0, 14, 0, 100, 0, 0, 30352, 66437, 'MELJARAK_INTRO_EVENT_GARALON_DEATH');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65586, 0, 0, 'Thank you. I owe you greatly.', 0, 12, 0, 100, 0, 0, 0, 66734, 'Restless Leng');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65586, 1, 0, 'I overheard a great deal through the cage. Rensai will be pleased with what I have to tell him.', 0, 12, 0, 100, 1, 0, 0, 66735, 'Restless Leng');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65586, 2, 0, 'I hope we meet again soon, my new friend.', 0, 12, 0, 100, 1, 0, 0, 66736, 'Restless Leng');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65590, 0, 0, '$n stepped on a lightning trap!', 0, 41, 0, 100, 0, 0, 0, 66740, 'step on lightning trap');
