INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65910, 0, 0, 'Just a moment, Teng. We have... a visitor.', 0, 12, 0, 100, 0, 0, 0, 67703, 'q31736 - Sunke Khang');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65960, 0, 0, '', 0, 12, 0, 100, 0, 0, 0, 67437, '');
