INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65977, 0, 0, 'Some enemies gain most of their power from the weapon that they wield.  Take that away, and their power over you goes with it.', 0, 12, 0, 100, 0, 0, 0, 67436, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65977, 1, 0, 'Bow to me and we shall begin.', 0, 12, 0, 100, 0, 0, 0, 67433, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (65977, 2, 0, 'Your power is impressive.  I admit defeat.', 0, 12, 0, 100, 0, 0, 0, 67389, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66073, 0, 0, 'Sometimes the best defense is to prevent your opponent from acting against you.', 0, 12, 0, 100, 0, 0, 0, 67435, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66073, 1, 0, 'Bow to me and we shall begin.', 0, 12, 0, 100, 0, 0, 0, 67433, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66073, 2, 0, 'Your power is impressive.  I admit defeat.', 0, 12, 0, 100, 0, 0, 0, 67389, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66138, 0, 0, 'As a monk, your reflexes are among your greatest resources.  Often, the ability to think and move quickly can save your life.', 0, 12, 0, 100, 0, 0, 0, 67434, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66138, 1, 0, 'Bow to me and we shall begin.', 0, 12, 0, 100, 0, 0, 0, 67433, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66138, 2, 0, 'Master Cheng is burning you alive!  Move!', 0, 41, 0, 100, 0, 0, 0, 67311, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66138, 3, 0, 'Well done.  Remember that swiftness of mind and body can keep you alive.', 0, 12, 0, 100, 0, 0, 0, 67386, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66149, 0, 0, 'Not all problems can be solved with your fists.  Sometimes you must center yourself and channel your energy towards a distant target.', 0, 12, 0, 100, 0, 0, 0, 67432, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66149, 1, 0, 'Bow to me and we shall begin.', 0, 12, 0, 100, 0, 0, 0, 67433, '');
