INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66253, 0, 0, '', 0, 12, 0, 100, 0, 0, 0, 67988, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66254, 0, 0, '', 0, 12, 0, 100, 0, 0, 0, 67989, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66255, 0, 0, 'I look forward to working with you.', 0, 12, 0, 100, 0, 0, 0, 67985, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66256, 0, 0, '', 0, 12, 0, 100, 0, 0, 0, 67989, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66257, 0, 0, 'Welcome to the Peak of Serenity.  Good luck in your training.', 0, 12, 0, 100, 0, 0, 0, 67986, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66258, 0, 0, 'Welcome.  Speak with me when you are ready to begin.', 0, 12, 0, 100, 0, 0, 0, 67991, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66260, 0, 0, 'My assistant will show you to Master Cheng.  Good Luck!', 0, 12, 0, 100, 0, 0, 0, 68001, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66366, 0, 0, 'Can... can I go home?', 0, 1, 0, 100, 6, 0, 0, 67546, 'q31744 - Captive Pandaren Cub');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66366, 0, 1, 'Get me OUT OF HERE!', 0, 1, 0, 100, 5, 0, 0, 67544, 'q31744 - Captive Pandaren Cub');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66366, 0, 2, 'I miss my family. I miss my home!', 0, 1, 0, 100, 0, 0, 0, 67550, 'q31744 - Captive Pandaren Cub');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66366, 0, 3, 'I thought those eyes would NEVER leave me alone!', 0, 1, 0, 100, 5, 0, 0, 67547, 'q31744 - Captive Pandaren Cub');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66366, 0, 4, 'I... I thought I\'d be here forever...', 0, 1, 0, 100, 1, 0, 0, 67545, 'q31744 - Captive Pandaren Cub');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66366, 0, 5, 'Thanks! I don\'t like the Horde.', 0, 1, 0, 100, 0, 0, 0, 67551, 'q31744 - Captive Pandaren Cub');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66374, 0, 0, 'Ow.  That really hurts.  Not.', 0, 12, 0, 100, 0, 0, 0, 67519, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66374, 0, 1, 'Oooh.  Mr. Big Mean Mogu.  I\'m so scared.', 0, 12, 0, 100, 0, 0, 0, 67520, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66374, 0, 2, 'Bring it on, jerk.', 0, 12, 0, 100, 0, 0, 0, 67521, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66374, 0, 3, 'You want a piece of me?', 0, 12, 0, 100, 0, 0, 0, 67522, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66374, 0, 4, 'That\'s all you got?', 0, 12, 0, 100, 0, 0, 0, 67530, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66374, 0, 5, 'That doesn\'t even tickle.', 0, 12, 0, 100, 0, 0, 0, 67531, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66374, 0, 6, 'You\'re not very good at this torture thing.', 0, 12, 0, 100, 0, 0, 0, 67532, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66374, 0, 7, 'Is that supposed to hurt?', 0, 12, 0, 100, 0, 0, 0, 67533, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66396, 0, 0, 'By our sweat and your blood, this land WILL be ours!', 0, 12, 0, 100, 0, 0, 32473, 67592, 'q31735 - Ga\'trul');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66564, 0, 0, 'Thank you, friend. Don\'t worry, it was just a scratch. Now I feel good as new!', 0, 12, 0, 100, 0, 0, 0, 61006, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66564, 0, 1, 'I thought I was a goner. I owe you my life.', 0, 12, 0, 100, 0, 0, 0, 61003, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66564, 0, 2, 'Thanks for saving me. It was rough up there.', 0, 12, 0, 100, 0, 0, 0, 53196, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66564, 0, 3, 'I\'m alive? Ha! That\'s more than I can say for those Horde at Garrosh\'ar Point. We tore them up good.', 0, 12, 0, 100, 0, 0, 0, 53197, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66564, 0, 4, 'Thank you. Time to lick my wounds and get back to the fight!', 0, 12, 0, 100, 0, 0, 0, 53195, '');
