INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66732, 0, 0, 'To defeat me, you will need to use all that you have learned from the other masters.  If you can do that, your training will be complete.', 0, 12, 0, 100, 0, 0, 0, 68159, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66732, 1, 0, 'Bow to me and we shall begin.', 0, 12, 0, 100, 0, 0, 0, 67144, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66732, 2, 0, 'Well done.  Your training is now complete.  Come see me in the Shrine of the Ox for your reward.', 0, 12, 0, 100, 0, 0, 0, 67870, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66791, 0, 0, 'Treasonous Klaxxi... her magnificence was right about you.', 0, 14, 0, 100, 1, 0, 29295, 67838, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66791, 1, 0, 'Grand Vizier Zor\'lok, voice of the Empress.', 0, 14, 0, 100, 6, 0, 29296, 67840, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66791, 2, 0, 'The Klaxxi are to be disbanded, by the will of the Empress.', 0, 14, 0, 100, 0, 0, 29297, 67842, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66791, 3, 0, 'The desperate Klaxxi have resorted to digging up fossils. Very well: let us finish this.', 0, 14, 0, 100, 1, 0, 29298, 67844, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66791, 4, 0, '"Unscathed," indeed.', 0, 14, 0, 100, 1, 0, 29299, 67846, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66791, 5, 0, 'Forward! The Klaxxi reveal themselves as traitors. Decimate them, and leave nothing behind.', 0, 14, 0, 100, 1, 0, 29300, 67847, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66797, 0, 0, 'Who holds this swarm? Show yourself! Answer to the Klaxxi.', 0, 14, 0, 100, 6, 0, 30910, 67837, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66797, 1, 0, 'Speak your name puppet, that our annals will be complete.', 0, 14, 0, 100, 396, 0, 30911, 67839, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66797, 2, 0, 'Your "Empress" has fallen. The Klaxxi have pronounced judgment. Stand aside or be brushed away.', 0, 14, 0, 100, 0, 0, 30912, 67841, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66797, 3, 0, 'Blaspheme!', 0, 14, 0, 100, 0, 0, 30913, 67843, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66800, 0, 0, 'Wakener, your deeds have earned you the trust of the Klaxxi.', 0, 12, 0, 100, 0, 0, 30312, 67848, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66800, 1, 0, 'You are to be rewarded. Walk with me.', 0, 12, 0, 100, 0, 0, 30313, 67849, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66800, 2, 0, 'We mantid are an elder race. The pandaren you associate with - they are but children. They have their role to play.', 0, 12, 0, 100, 0, 0, 30314, 67850, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66800, 3, 0, 'Each cycle, our young swarm their walls. The pandaren slay the weak. The strong return.', 0, 12, 0, 100, 0, 0, 30315, 67851, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66800, 4, 0, 'With each generation, we grow ever stronger.', 0, 12, 0, 100, 0, 0, 30316, 67852, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66800, 5, 0, 'Before your history began, our empire was vast. We shared this world with our sister kingdoms, Ahn\'Qiraj and Azjol-Nerub.', 0, 12, 0, 100, 0, 0, 30317, 67853, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66800, 6, 0, 'Our Gods were many, and powerful. We mantid worshipped the seven heads of Y\'shaarj.', 0, 12, 0, 100, 0, 0, 30318, 67854, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66800, 7, 0, 'Great was the Old One, and terrible was His wrath. He consumed hope and begat despair; He inhaled courage and breathed fear.', 0, 12, 0, 100, 0, 0, 30319, 67855, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66800, 8, 0, 'When the usurpers came - the ones you call "Titans" - Y\'shaarj was destroyed.', 0, 12, 0, 100, 0, 0, 30320, 67856, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66800, 9, 0, 'His last terrible breath has haunted this land ever since, but the shadows he left behind are mere whispers of his former glory.', 0, 12, 0, 100, 0, 0, 30321, 67857, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66800, 10, 0, 'I tell you now, because you have earned this warning.', 0, 12, 0, 100, 0, 0, 30322, 67858, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66800, 11, 0, 'Your gods are not our gods, outsider.', 0, 12, 0, 100, 0, 0, 30323, 67859, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66800, 12, 0, 'If the Old Ones ever return, we mantid will once again stand by their side.', 0, 12, 0, 100, 0, 0, 30324, 67860, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66800, 13, 0, 'The wisest among you will do the same.', 0, 12, 0, 100, 0, 0, 30325, 67861, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66844, 11, 0, 'And with that, I must depart.  Good luck with your training.', 0, 12, 0, 100, 0, 0, 0, 67978, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66844, 10, 0, 'And finally, this is Master Cheng.  Master Cheng focuses on reflex and swift motion and will help you begin your training.', 0, 12, 0, 100, 0, 0, 0, 67977, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66844, 9, 0, 'This is Master Hsu.  He specializes in using all kinds of weaponry and is masterfully proficient with nearly any weapon under the sun.', 0, 12, 0, 100, 0, 0, 0, 67973, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66844, 8, 0, 'This is Master Tsang.  He is a master of meditation and the ability to sense what is happening around him.', 0, 12, 0, 100, 0, 0, 0, 67966, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66844, 7, 0, 'This is Master Cheng.  She has mastered the art of fighting on the move.', 0, 12, 0, 100, 0, 0, 0, 67964, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66844, 6, 0, 'This is Master Yoon.  He is practiced in using powerful, focused healing to maintain himself in battle.', 0, 12, 0, 100, 0, 0, 0, 67963, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66844, 5, 0, 'This is Master Woo.  She specializes in defense against persistent attacks.', 0, 12, 0, 100, 0, 0, 0, 67962, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66844, 4, 0, 'This is Master Kistane, she is a master in the art of channeling the mists to assist her and her allies.', 0, 12, 0, 100, 0, 0, 0, 67961, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66844, 3, 0, 'This is the Training Ground, where most of your training will take place.  Come, I will introduce you to the masters who will be teaching you.', 0, 12, 0, 100, 0, 0, 0, 67960, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66844, 2, 0, 'Here, you\'ll find several of us who have mastered the art of various crafts.  If you need any supplies along your journey, they can probably help.', 0, 12, 0, 100, 0, 0, 0, 67958, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66844, 0, 0, 'Follow me and I will show you around the Peak of Serenity.', 0, 12, 0, 100, 0, 0, 0, 68002, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66844, 1, 0, 'Don\'t forget to take it all in while you\'re here.  The Peak of Serenity has a wonderful view of beautiful Kun-Lai summit.', 0, 12, 0, 100, 0, 0, 0, 67957, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66870, 0, 0, 'I will help you carry the barrels. Or did you think they were going to magically fit in your bag?', 0, 12, 0, 100, 0, 0, 0, 68021, 'Gi-Oh');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66870, 1, 0, 'My village will not enter your conflict. Though, I fear your arrival has changed our peaceful existence forever.', 0, 12, 0, 100, 0, 0, 0, 68128, 'Gi-Oh');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66870, 1, 1, 'Your actions will no doubt attract the attention of the Shado-Pan. I may even get to meet one!', 0, 12, 0, 100, 0, 0, 0, 68127, 'Gi-Oh');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66870, 1, 2, 'One keg?! I was carrying one keg with ease when I was a young cub, barely higher than your knee!', 0, 12, 0, 100, 0, 0, 0, 68109, 'Gi-Oh');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66870, 2, 0, 'Heavy, but I can hold more! Keep going!', 0, 12, 0, 100, 0, 0, 0, 68110, 'Gi-Oh');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66870, 2, 1, 'A minor inconvenience.', 0, 12, 0, 100, 0, 0, 0, 68125, 'Gi-Oh');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66870, 2, 2, 'Why is it you can\'t just talk out your differences? I have never encountered a problem that a good long talk couldn\'t fix!', 0, 12, 0, 100, 0, 0, 0, 68116, 'Gi-Oh');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66870, 3, 0, 'Why have you brought your war to our land? What of your homes? Was there nothing worth fighting for there?', 0, 12, 0, 100, 0, 0, 0, 68126, 'Gi-Oh');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66870, 3, 1, 'All this fighting is such a waste. How do your people do it all the time? When do you find time to celebrate and enjoy the simple things?', 0, 12, 0, 100, 0, 0, 0, 68114, 'Gi-Oh');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66870, 3, 2, 'Horde, Alliance... what\'s the difference?! Do you not both love a good brew? Do you both not value your friends and families the same?', 0, 12, 0, 100, 0, 0, 0, 68115, 'Gi-Oh');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66870, 4, 0, 'I may have overestimated my own strength...', 0, 12, 0, 100, 0, 0, 0, 68111, 'Gi-Oh');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66870, 4, 1, 'That\'s it, I\'ve reached my limit! This is quite the haul!', 0, 12, 0, 100, 0, 0, 0, 68112, 'Gi-Oh');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66870, 5, 0, 'This Honeybrew is the lifeblood of this village. Now THAT is something worth fighting for!', 0, 12, 0, 100, 0, 0, 0, 68129, 'Gi-Oh');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66870, 5, 1, 'Honeydew Village is run by the Honeydews, but the real heart of the village is the brewery! The brewery belongs to my family, the Honeypaws. Best Honeybrew you\'ll find anywhere!', 0, 12, 0, 100, 0, 0, 0, 68119, 'Gi-Oh');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66870, 6, 0, 'Thank you for your help. I\'ll make sure this Honeybrew gets back to the village. Try not to blow anything else up!', 0, 12, 0, 100, 0, 0, 0, 68133, 'Gi-Oh');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (66870, 6, 1, 'Thank you for your help. Try not to blow anything else up!', 0, 12, 0, 100, 0, 0, 0, 68134, 'Gi-Oh');
