INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 0, 0, 'Now entering the arena: a Rank 1 $r $c, $n! $n is pretty new around here, so go easy!', 0, 14, 0, 100, 22, 0, 0, 68842, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 1, 0, 'Now entering the arena: an up-and coming Rank 2 $c, $n!', 0, 14, 0, 100, 22, 0, 0, 68888, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 2, 0, 'Put your hands together for $n! $n is a Rank 3 $c.', 0, 14, 0, 100, 22, 0, 0, 68889, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 3, 0, 'Here\'s our challenger. Today, we welcome a Rank 4 $r $c, $n!', 0, 14, 0, 100, 22, 0, 0, 68890, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 4, 0, 'Now entering the arena, we have a very talented $c. Say hello to $n, a Rank 5 brawler!', 0, 14, 0, 100, 22, 0, 0, 68891, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 5, 0, 'Look out... here comes the deadly Rank 6 $r, $n!', 0, 14, 0, 100, 22, 0, 0, 68892, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 6, 0, 'Now entering the arena: a returning champion. Our Rank 7 $c, $n!', 0, 14, 0, 100, 22, 0, 0, 68893, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 7, 0, 'Now entering the arena: You know $ghim:her;. You love $ghim:her;. Give it up for our Rank 8 Brawler, $N!!', 0, 14, 0, 100, 22, 0, 0, 68894, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 8, 0, '$gHe\'s:She\'s; one of our veteran Rank 9 brawlers here in the Brawlpub. It\'s $n!', 0, 14, 0, 100, 22, 0, 0, 73744, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 9, 0, 'Also entering the arena: Bruce, a crocolisk that we found!', 0, 14, 0, 100, 22, 0, 0, 69936, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 10, 0, 'In the other corner, we have Vian, the flame-flinging follower of the Thunder King!', 0, 14, 0, 100, 22, 0, 0, 69937, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 38, 3, '$n, $n, $n. Next time, try not to die so much.', 0, 14, 0, 100, 0, 0, 0, 68849, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 38, 2, 'That... did not end well.', 0, 14, 0, 100, 0, 0, 0, 68848, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 38, 1, 'Yechhh... what a mess. Can we get a priest or something over here?!', 0, 14, 0, 100, 0, 0, 0, 68847, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 38, 0, 'Ouch... sorry, $n. Get back in line and try again.', 0, 14, 0, 100, 0, 0, 0, 68841, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 37, 3, 'It\'s official! $n managed not to die. Great job not dying, $n!', 0, 14, 0, 100, 0, 0, 0, 68846, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 37, 2, 'Congratulations, $n! Try not to leave any bloody footprints outside of the arena.', 0, 14, 0, 100, 0, 0, 0, 68845, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 37, 0, '$n is our victor!', 0, 14, 0, 100, 0, 0, 0, 68840, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67260, 37, 1, 'Well well well! $n wins! Looks like I owe someone some money.', 0, 14, 0, 100, 0, 0, 0, 68844, '');
