INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67263, 1, 0, 'The Darkhatched Lizard-Lord prepares to unleash a group of Water Jets!', 2, 41, 0, 100, 0, 0, 0, 69127, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67266, 0, 0, 'Proceed by foot. We\'ll circle around and look for another entrance.', 2, 12, 0, 100, 25, 0, 34419, 69666, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67266, 1, 0, 'This is the chamber we\'re looking for.', 2, 14, 0, 100, 1, 0, 34403, 70792, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67266, 2, 0, 'The Warchief has learned there\'s a connection between the Mogu and these creatures.', 2, 14, 0, 100, 1, 0, 34404, 70793, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67266, 3, 0, 'Troll! You know all about this... voodoo.  What can you find?', 2, 14, 0, 100, 25, 0, 34405, 70794, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67266, 4, 0, 'Yes! The power to shape flesh, to build warriors.  This is what the Warchief wants!', 2, 14, 0, 100, 5, 0, 34400, 70802, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67266, 5, 0, 'He knew you were a traitor!', 2, 14, 0, 100, 5, 0, 34401, 70804, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67266, 6, 0, 'The Warchief will annihilate his enemies!', 2, 14, 0, 100, 15, 0, 34402, 70806, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67266, 7, 0, 'Strength!', 2, 14, 0, 100, 0, 0, 34411, 70860, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67266, 8, 0, 'Is THIS where your loyalties lie?', 2, 14, 0, 100, 0, 0, 34406, 70855, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67266, 9, 0, '...die!', 2, 14, 0, 100, 0, 0, 34413, 70862, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67266, 10, 0, 'Hellscream\'s Horde demands absolute obedience!', 2, 14, 0, 100, 0, 0, 34407, 70856, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67266, 11, 0, 'The Warchief will hear of this betrayal!', 2, 14, 0, 100, 0, 0, 34408, 70857, '');
