INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67596, 1, 0, 'I see nothing but purple before me...', 0, 14, 0, 100, 0, 0, 0, 70125, 'Fjoll - Death');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67678, 0, 0, 'What are you afraid of?', 0, 14, 0, 100, 0, 0, 0, 21102, 'Dark Summoner - Init Battle');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67678, 1, 0, 'At last, my soul is free!', 0, 14, 0, 100, 0, 0, 0, 6429, 'Dark Summoner - Defeat');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67678, 2, 0, '', 0, 42, 0, 100, 0, 0, 0, 69121, 'Dark Summoner - Init Announce');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67678, 3, 0, 'The Dark Summoner is vulnerable for a short time, now\'s your chance!', 0, 42, 0, 100, 0, 0, 0, 69123, 'Dark Summoner - Spirit defeat');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67678, 4, 0, 'The Dark Summoner\'s protective magics are active once again.', 0, 42, 0, 100, 0, 0, 0, 69124, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67682, 0, 0, 'I hear you, Hilda. Mogu mixin\' up with sha? I don\'t like that one bit! We\'d better bring this evidence back to the King. $n, thanks fer yer help today, but we\'ll take it from here!', 0, 12, 0, 100, 0, 0, 0, 69179, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67686, 0, 0, 'You\'ve gained Mogu Power!  Pick up more runes and your power will grow.', 2, 42, 0, 100, 0, 0, 0, 70095, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67688, 0, 0, 'This jungle will soon be cleansed of Alliance filth!', 2, 14, 0, 100, 30, 0, 0, 69111, '');
