INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67748, 0, 0, 'A Darkhatched Sorcerer has put the boat into Stasis!  Kill it to interrupt the spell.', 2, 41, 0, 100, 0, 0, 0, 70012, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67761, 0, 0, 'A test! A trial of patience. Will the wolf behave as a warrior... or a King?', 2, 12, 0, 100, 1, 0, 0, 70309, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67761, 1, 0, 'A sword is the weapon of a warrior, but patience is the weapon of kings.', 2, 12, 0, 100, 1, 0, 34344, 70310, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67761, 2, 0, 'This day brings victory, but more difficult trials lie ahead...', 2, 12, 0, 100, 1, 0, 34345, 70311, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67782, 0, 0, 'Hey - wait for me! I don\'t like goons, either.', 2, 14, 0, 100, 0, 0, 34365, 69670, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67782, 1, 0, 'We can take this boat inside.  Just let me make a few modifications...', 2, 12, 0, 100, 0, 0, 34356, 70774, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67782, 2, 0, 'And...done! A self-propelled saurok slaying machine.', 2, 12, 0, 100, 4, 0, 34357, 70775, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67782, 3, 0, 'Ack, get that thing off us!', 2, 14, 0, 100, 430, 0, 34358, 70778, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67782, 4, 0, 'Here, take this! It\'ll help you against any swarms.', 2, 12, 0, 100, 0, 0, 34373, 70784, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67782, 5, 0, 'I\'ll just ... be right here. Don\'t forget about me!', 2, 12, 0, 100, 0, 0, 34374, 70785, '');
