INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67801, 1, 0, 'For Theramore!', 0, 12, 0, 100, 0, 0, 0, 70819, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67801, 2, 0, 'Monsters... all of yeh... *sputter*', 0, 12, 0, 100, 0, 0, 0, 70823, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67833, 0, 0, 'The Mercurial Guardian comes to life!', 0, 41, 0, 100, 0, 0, 0, 69235, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67833, 1, 0, 'The Mercurial Guardian\'s skin becomes as hard as silver!', 0, 41, 0, 100, 0, 0, 0, 69260, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67834, 0, 0, 'If I am correct, $n, then this... Divine Bell... is exactly the type of weapon that Garrosh is looking for. Return to your other duties for now. I will bring this information to the Warchief myself.', 0, 12, 0, 100, 0, 0, 0, 69264, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67862, 0, 0, 'The statue appears to be of an ancient Mogu emperor.', 2, 41, 0, 100, 0, 0, 0, 69163, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67863, 0, 0, 'It seems as though this cave used to contain powerful Mogu magic.', 2, 41, 0, 100, 0, 0, 0, 69164, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67879, 0, 0, 'Zan\'vess will never fall!', 0, 14, 0, 100, 0, 0, 0, 70911, 'telvrak intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67883, 0, 0, 'Gnome Construction Site Under Attack!', 2, 41, 0, 100, 30, 0, 34768, 69311, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67930, 0, 0, 'CARRIER HAS ARRIVED.', 0, 12, 0, 100, 0, 0, 0, 72088, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67940, 0, 0, 'Time is of the essence, $n. Proveditor Grantley may be able to help you get back to Stormwind. Of course, everything has a price.', 0, 12, 0, 100, 0, 0, 0, 70398, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67990, 0, 0, 'I have selected a landing site for our forces: a small peninsula to our east that is easily defended.', 0, 12, 0, 100, 0, 0, 35616, 71641, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67990, 1, 0, 'Alas, the Zandalari trolls have already claimed and fortified it.', 0, 12, 0, 100, 0, 0, 35617, 71642, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67990, 2, 0, 'They\'re protecting it with a powerful spirit barrier. We will need to disable it before our ships can land.', 0, 12, 0, 100, 0, 0, 35618, 71643, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67990, 3, 0, 'Champion, I need you to lead a ground assault and take down that barrier from within!', 0, 12, 0, 100, 0, 0, 35619, 71644, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67990, 4, 0, 'Mount up and rendezvous with Scout Captain Elsia. She is organizing an elite group of rangers to accompany you. May the light of the sun shine upon you.', 0, 12, 0, 100, 0, 0, 35620, 71645, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67990, 5, 0, 'Victory to the Sin\'dorei! And another triumph for the Horde.', 0, 12, 0, 100, 0, 0, 35621, 71672, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67990, 6, 0, 'Rommath, establish a perimeter. Aethas, begin placing the sentry wards. Kethiel, unload the shelters and supplies.', 0, 12, 0, 100, 0, 0, 35622, 71673, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (67990, 7, 0, 'Brothers and sisters of the Horde! Enemies surround us on all sides! Your courage in weeks to come will be the difference between victory and defeat.', 0, 12, 0, 100, 0, 0, 35623, 71674, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68006, 0, 0, 'Thank you for saving me from that nightmare. I wanted to rip everything apart!', 0, 12, 0, 100, 0, 0, 0, 69338, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68006, 0, 1, 'I don\'t know what came over me. Thank you for clearing my head.', 0, 12, 0, 100, 0, 0, 0, 69339, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68006, 0, 2, 'Thank you for saving me, soldier. You too, young prince.', 0, 12, 0, 100, 0, 0, 0, 69340, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68006, 0, 3, 'I fought against the anger, but it overwhelmed me. You have my thanks.', 0, 12, 0, 100, 0, 0, 0, 69341, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68036, 0, 0, 'Thousands of years ... without new playthings ... I will enjoy this.', 0, 14, 0, 100, 0, 0, 35339, 72631, 'Durumu - Intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68036, 1, 0, 'I ... welcome ... the void\'s ... embrace.', 0, 14, 0, 100, 0, 0, 35338, 72634, 'Durumu - Aggro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68036, 2, 0, 'I expected so much more. How ... disappointing.', 0, 14, 0, 100, 0, 0, 35337, 72633, 'Durumu - Death');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68036, 3, 0, 'I expected so much more. How ... disappointing.', 0, 14, 0, 100, 0, 0, 35337, 72633, 'Durumu - Berserk');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68036, 4, 0, 'Do you see what I did there?', 0, 14, 0, 100, 0, 0, 35340, 72635, 'Durumu - Kill 1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68036, 5, 0, 'Looks like you couldn\'t keep up ... pity.', 0, 14, 0, 100, 0, 0, 35341, 72636, 'Durumu - Kill 2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68036, 6, 0, 'Watch your footing ... muhahaha!', 0, 14, 0, 100, 0, 0, 35342, 72637, 'Durumu - Spell 1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68036, 7, 0, 'The mists hide many secrets ... if you know where to look.', 0, 14, 0, 100, 0, 0, 35343, 72638, 'Durumu - Spell 2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68036, 8, 0, 'I\'m keeping an eye on you.', 0, 14, 0, 100, 0, 0, 35344, 72639, 'Durumu - Spell 3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68036, 9, 0, 'Observe your demise.', 0, 14, 0, 100, 0, 0, 35345, 72640, 'Durumu - Spell 4');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68057, 0, 0, 'Dwarf Construction Site Under Attack!', 2, 41, 0, 100, 30, 0, 34768, 69435, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68065, 0, 0, '|TInterface\\Icons\\trade_archaeology_whitehydrafigurine:20|tMegaera begins to |cFFF00000|Hspell:139458|h[Rampage]|h|r!', 0, 41, 0, 100, 0, 0, 0, 72655, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68065, 1, 0, 'Megaera\'s rage subsides.', 0, 41, 0, 100, 0, 0, 0, 72748, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68078, 0, 0, 'Strike quickly, Manchu. Show them no mercy!', 0, 14, 0, 100, 0, 0, 35474, 72169, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68078, 1, 0, 'All brawn. No brains. A recoverable loss.', 0, 14, 0, 100, 0, 0, 35475, 72170, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68078, 2, 0, 'Focus, Weisheng. Show your enemy the meaning of pain!', 0, 14, 0, 100, 0, 0, 35479, 72171, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68078, 3, 0, 'He fought with honor. His first and last mistake.', 0, 14, 0, 100, 0, 0, 35476, 72172, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68078, 4, 0, 'Stare into the face of true power!', 0, 14, 0, 100, 0, 0, 35465, 72162, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68078, 5, 0, 'How can this be?!', 0, 14, 0, 100, 0, 0, 35462, 72176, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68078, 6, 0, 'Quet\'zal! Unleash the storm upon my enemies!!!', 0, 14, 0, 100, 0, 0, 35467, 72167, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68078, 7, 0, 'Dam\'ren!  Freeze them to oblivion!', 0, 14, 0, 100, 0, 0, 35466, 72168, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68078, 8, 0, 'ARGH! Worthless mongrels! I will kill you myself!', 0, 14, 0, 100, 0, 0, 35477, 72173, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68078, 9, 0, 'More... training...', 0, 14, 0, 100, 0, 0, 35469, 72166, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68088, 0, 0, 'Here! Stop the boats here.', 0, 14, 0, 100, 0, 0, 34241, 69427, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68088, 1, 0, 'This island will make a perfect coastal stronghold. Rokla! When the peons arrive, clear these trees and begin construction of the docks.', 0, 12, 0, 100, 1, 0, 34242, 69428, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68088, 2, 0, 'Champion, you have been here for many weeks, and know this land well. Come with me.', 0, 12, 0, 100, 25, 0, 34243, 69429, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68096, 0, 0, 'This temple is the domain of the Ashtongue Brotherhood. I must ask you to leave - now.', 0, 12, 0, 100, 0, 0, 0, 69433, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68096, 1, 0, 'What did you see, brother?', 0, 12, 0, 100, 0, 0, 0, 69431, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68106, 0, 0, '$p, we should discuss matters back in Krasarang. For now, thank you for your help. There is a portal that can return you to Lion\'s Landing whenever you choose.', 0, 12, 0, 100, 0, 0, 0, 69831, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68137, 0, 0, 'Foolish warlocks. I knew you would try again to ransack our temple...!', 0, 12, 0, 100, 0, 0, 0, 69487, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68137, 1, 0, 'Wait... you\'re not one of the warlocks that snuck in here before. Why then are you here?', 0, 12, 0, 100, 0, 0, 0, 69488, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68137, 2, 0, 'It has been many months since those two warlocks broke into the temple... but I\'m still not sure what they were after.', 0, 12, 0, 100, 0, 0, 0, 69498, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68137, 3, 0, 'Follow me. They visited both the Shrine of Lost Souls and the Temple Summit.', 0, 12, 0, 100, 0, 0, 0, 69499, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68137, 4, 0, 'I have decided to trust this warlock for now. Let $ghim:her; move about the temple freely.', 0, 12, 0, 100, 0, 0, 0, 69500, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68137, 5, 0, 'It will take many years for us to undo the damage that Magtheridon, Illidan and the fel orcs did to this holy place.', 0, 12, 0, 100, 0, 0, 0, 69501, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68137, 6, 0, 'Within lies the Shrine of Lost Souls. The souls of thousands of my fallen brethren remain within this area.', 0, 12, 0, 100, 0, 0, 0, 69502, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68137, 7, 0, 'Use caution, there are many traps still active within. Perhaps one of your demon spells will grant you the ability to scout ahead before advancing forward.', 0, 12, 0, 100, 0, 0, 0, 69503, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68137, 8, 0, '$n! Some of my men are trapped in the basement - there is little time, help me save them!!', 0, 14, 0, 100, 0, 0, 0, 69538, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68151, 0, 0, 'The powers of this place are NOT for your kind, warlock!', 0, 14, 0, 100, 0, 0, 0, 69529, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68151, 1, 0, 'DIE!', 0, 14, 0, 100, 0, 0, 0, 69531, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68151, 2, 0, 'If you love demons so much... then you shall have them!', 0, 14, 0, 100, 0, 0, 0, 69537, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68287, 0, 0, 'Garrosh, please. There may be another way. One that requires no bloodshed.', 0, 12, 0, 100, 1, 0, 0, 69926, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68287, 1, 0, 'Let us use persuasion, not force. I have here a cask of pandaren Memory Wine. It will allow us to see into Shan Kien\'s past.', 0, 12, 0, 100, 1, 0, 0, 69991, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68305, 0, 0, 'I SMASH. YOU DIE. DEAL WITH IT!', 0, 14, 0, 100, 0, 0, 0, 69840, 'Smash - Init Battle');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68305, 1, 0, 'THESE SHOULDERS... SO HEAVY...', 0, 14, 0, 100, 0, 0, 0, 69839, 'Smash - Defeat');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68305, 2, 0, 'SMASH MY GOOD LUCK? I SMASH YOU!', 0, 14, 0, 100, 0, 0, 0, 69835, 'Smash - Goblin death 1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68305, 3, 0, 'GOBLIN WAS NOT LUCKY AT ALL!', 0, 14, 0, 100, 0, 0, 0, 69836, 'Smash - Goblin death 2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 5, 0, 'Look out... here comes the deadly Rank 6 $r, $n!', 0, 14, 0, 100, 22, 0, 0, 68892, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 1, 0, 'Now entering the arena: an up-and coming Rank 2 $c, $n!', 0, 14, 0, 100, 22, 0, 0, 68888, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 2, 0, 'Put your hands together for $n! $n is a Rank 3 $c.', 0, 14, 0, 100, 22, 0, 0, 68889, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 3, 0, 'Here\'s our challenger. Today, we welcome a Rank 4 $r $c, $n!', 0, 14, 0, 100, 22, 0, 0, 68890, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 4, 0, 'Now entering the arena, we have a very talented $c. Say hello to $n, a Rank 5 brawler!', 0, 14, 0, 100, 22, 0, 0, 68891, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 38, 3, '$n, $n, $n. Next time, try not to die so much.', 0, 14, 0, 100, 0, 0, 0, 68849, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 38, 2, 'That... did not end well.', 0, 14, 0, 100, 0, 0, 0, 68848, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 38, 1, 'Yechhh... what a mess. Can we get a priest or something over here?!', 0, 14, 0, 100, 0, 0, 0, 68847, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 38, 0, 'Ouch... sorry, $n. Get back in line and try again.', 0, 14, 0, 100, 0, 0, 0, 68841, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 37, 3, 'It\'s official! $n managed not to die. Great job not dying, $n!', 0, 14, 0, 100, 0, 0, 0, 68846, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 37, 2, 'Congratulations, $n! Try not to leave any bloody footprints outside of the arena.', 0, 14, 0, 100, 0, 0, 0, 68845, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 36, 0, 'And... Meatball.', 0, 14, 0, 100, 22, 0, 0, 70059, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 37, 0, '$n is our victor!', 0, 14, 0, 100, 0, 0, 0, 68840, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 37, 1, 'Well well well! $n wins! Looks like I owe someone some money.', 0, 14, 0, 100, 0, 0, 0, 68844, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 35, 0, 'Also entering the arena: Battletron! This hunk o\' junk is just waiting to explode all over you!', 0, 14, 0, 100, 22, 0, 0, 69942, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 34, 0, 'Also entering the arena: The Dark Summoner. Wow, he doesn\'t even have a real name. Spooky!', 0, 14, 0, 100, 22, 0, 0, 69961, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 33, 0, 'Their partnership was never meant to be. It actually makes me sick a little bit. Here comes GG Engineering!', 0, 14, 0, 100, 22, 0, 0, 69960, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 32, 0, 'Quiet down, everyone... we don\'t want to make it angry. Just gaze in silent awe at Unguloxx, the murderaffe!', 0, 14, 0, 100, 22, 0, 0, 69965, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 31, 0, 'We also welcome Smash Hoofstomp, the terrible tauren from Thunder Bluff! Look at those GREAT shoulderpads!', 0, 14, 0, 100, 22, 0, 0, 69958, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 30, 0, 'Some of you veterans might remember this guy. He may be Broken, but today, he means to break you! It\'s Akama!', 0, 14, 0, 100, 22, 0, 0, 69953, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 29, 0, 'We found this one cawing up a storm in Auchindoun. Give it up for Yikkan Izu!', 0, 14, 0, 100, 22, 0, 0, 69956, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 28, 0, 'The opposition includes some of Gnomeregan\'s finest... although they\'ve all seen better days. It\'s the leper gnome quintet!', 0, 14, 0, 100, 22, 0, 0, 69941, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 27, 0, 'Oh dear... I\'m sorry, but it looks like you\'re going to have to fight Proboskus.', 0, 14, 0, 100, 22, 0, 0, 69964, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 26, 0, 'Also entering the arena: Everybody\'s favorite magical 12-foot-tall corpse, Fjoll! Am I pronouncing that right?', 0, 14, 0, 100, 22, 0, 0, 69954, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 0, 0, 'Now entering the arena: a Rank 1 $r $c, $n! $n is pretty new around here, so go easy!', 0, 14, 0, 100, 22, 0, 0, 68842, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 25, 0, 'Up next, it\'s Millie Watt! I used to date her in engineering school!', 0, 14, 0, 100, 22, 0, 0, 69951, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 24, 0, 'Get ready to dance! Deeken\'s in the house!', 0, 14, 0, 100, 22, 0, 0, 69957, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 16, 0, '', 0, 14, 0, 100, 22, 0, 0, 69871, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 14, 0, '', 0, 14, 0, 100, 22, 0, 0, 68863, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 15, 0, '', 0, 14, 0, 100, 22, 0, 0, 69854, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 13, 0, '', 0, 14, 0, 100, 22, 0, 0, 69855, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 8, 0, '$gHe\'s:She\'s; one of our veteran Rank 9 brawlers here in the Brawlpub. It\'s $n!', 0, 14, 0, 100, 22, 0, 0, 73744, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 9, 0, 'Also entering the arena: Bruce, a crocolisk that we found!', 0, 14, 0, 100, 22, 0, 0, 69936, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 10, 0, 'In the other corner, we have Vian, the flame-flinging follower of the Thunder King!', 0, 14, 0, 100, 22, 0, 0, 69937, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 11, 0, '', 0, 14, 0, 100, 22, 0, 0, 69849, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 12, 0, '', 0, 14, 0, 100, 22, 0, 0, 69850, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 7, 0, 'Now entering the arena: You know $ghim:her;. You love $ghim:her;. Give it up for our Rank 8 Brawler, $N!!', 0, 14, 0, 100, 22, 0, 0, 68894, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 6, 0, 'Now entering the arena: a returning champion. Our Rank 7 $c, $n!', 0, 14, 0, 100, 22, 0, 0, 68893, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 39, 0, 'What\'s this? Bruce is back! A better, faster, stronger Bruce! It\'s MECHA-BRUCE!! YAYYYY!!', 0, 14, 0, 100, 22, 0, 0, 74148, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 40, 0, 'Dust off that luckydo, because you\'re gonna need it. It\'s Grandpa Grumplefloot!', 0, 14, 0, 100, 22, 0, 0, 74143, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 41, 0, 'Ever wonder what we do with all our obsolete robots? We send them to their deaths in bloody gladiatorial arenas, of course! Here comes Blingtron 3000!', 0, 14, 0, 100, 22, 0, 0, 74150, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68323, 42, 0, 'Also entering the arena: the hozen with the box of bombs! It\'s Master Boom Boom, the dynamite throwing monkey monk!', 0, 14, 0, 100, 22, 0, 0, 73112, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68336, 0, 0, 'Use the Kor\'kron Disguise, then search for intel in the middle of Garrosh\'ar Advance.', 0, 42, 0, 100, 0, 0, 0, 70082, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68336, 1, 0, 'I am losing patience, Shan Kien. Tell me where I can find the Divine Bell!', 0, 12, 0, 100, 5, 0, 0, 69923, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68336, 2, 0, 'Ishi, draw your blade. A few carefully delivered cuts will change his mind.', 0, 12, 0, 100, 1, 0, 0, 69925, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68336, 3, 0, 'Speak, then!', 0, 12, 0, 100, 1, 0, 0, 69927, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68339, 0, 0, 'Kill me if you must, but I owe no allegiance to you or your Horde. The only master I serve is the Thunder King.', 0, 12, 0, 100, 0, 0, 0, 69924, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68375, 0, 0, 'WHAT?! You saw where the mogu hid the Divine Bell? I\'ll alert Lion\'s Landing immediately. We gotta get this thing before Garrosh. We\'ll contact you as soon as we find it!', 0, 12, 0, 100, 5, 0, 0, 70083, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68397, 0, 0, 'You! You have earned my ire. I will make an example of you, such that all who look upon my might will tremble and submit.', 0, 14, 0, 100, 0, 0, 35589, 72509, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68397, 1, 0, 'I am Lei Shen, slayer of kings and gods. You have made a grave mistake.', 0, 14, 0, 100, 0, 0, 35581, 72510, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68397, 2, 0, 'Submit!', 0, 14, 0, 100, 0, 0, 35601, 72513, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68397, 3, 0, 'You would challenge me in my own sanctum? Witness your folly!', 0, 14, 0, 100, 0, 0, 35595, 72518, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68397, 4, 0, 'Bah! I will crush you myself. Let us see how you fare against Lightning.', 0, 14, 0, 100, 0, 0, 35596, 72519, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68397, 5, 0, 'Lightning, seek my foes!', 0, 14, 0, 100, 0, 0, 35602, 72514, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68397, 6, 0, 'You are NOTHING! I wield the power of the heavens!', 0, 14, 0, 100, 0, 0, 35597, 72520, '');
