INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68408, 0, 0, 'Speak with a guard if you wish to fight, $c.', 0, 12, 0, 100, 66, 0, 0, 70089, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68408, 1, 0, 'You want to fight?  Come back when you\'re a bit stronger.$b$bYou\'re welcome to watch, though.', 0, 12, 0, 100, 6, 0, 0, 71305, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68417, 0, 0, 'Pssssst! Hey, over here, it\'s me, Agent Connelly! Don\'t blow our disguise!', 0, 12, 0, 100, 0, 0, 0, 70832, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68417, 1, 0, 'So Garrosh is looking for something called the Divine Bell? Whatever that is, it can\'t be good. I\'ll get this information back to headquarters.', 0, 12, 0, 100, 0, 0, 0, 70830, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68417, 2, 0, 'Excellent work! I\'ll be in touch soon.', 0, 12, 0, 100, 0, 0, 0, 70831, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68474, 0, 0, 'Alliance dogs! Where are your teeth?', 2, 14, 0, 100, 1, 0, 34476, 70312, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68474, 1, 0, 'Bring up the grunts. Attack!', 2, 14, 0, 100, 1, 0, 34477, 70313, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68474, 2, 0, 'For the warchief!', 2, 14, 0, 100, 1, 0, 34469, 70314, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68474, 3, 0, 'Your Alliance is WEAK!', 2, 14, 0, 100, 0, 0, 34471, 70316, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68474, 4, 0, 'I am done with you!', 2, 14, 0, 100, 1, 0, 34472, 70317, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68474, 5, 0, 'Commander Scargash is filled with rage! Avoid his melee attacks!', 2, 41, 0, 100, 0, 0, 0, 70166, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68474, 6, 0, 'Is this how you fight your battles?', 2, 12, 0, 100, 1, 0, 34470, 70315, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68514, 0, 0, 'There\'s my survey team. Bring us in.', 0, 14, 0, 100, 0, 0, 34681, 69704, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68514, 1, 0, 'What\'s the situation?', 0, 12, 0, 100, 547, 0, 34682, 69705, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68514, 2, 0, 'The Horde?', 0, 12, 0, 100, 6, 0, 34683, 69707, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68514, 3, 0, 'Hmm... Troteman may have gotten himself in over his head. We\'ll split up.', 0, 12, 0, 100, 396, 0, 34684, 69709, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68514, 4, 0, 'Lynna, stay here and establish a camp. Your first priority should be medical tents and shelters. When the peasants arrive, see that they begin construction of the docks.', 0, 12, 0, 100, 397, 0, 34685, 69710, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68514, 5, 0, 'Hornswaggle, notify the rest of the survey team. Quarry and mining operations will begin as soon as the fleet arrives.', 0, 12, 0, 100, 396, 0, 34686, 69711, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68514, 6, 0, 'Champion, you know this land better than anyone. You\'re with me. Let\'s go find Troteman.', 0, 12, 0, 100, 396, 0, 34687, 69713, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68519, 0, 0, '', 0, 12, 0, 100, 396, 0, 34244, 69706, '');
