INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68526, 3, 0, '', 0, 12, 0, 100, 0, 0, 0, 70222, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68531, 0, 0, 'So ye want me to go join the Alliance front in this... Pandaria... eh?', 0, 12, 0, 100, 0, 0, 0, 70256, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68531, 1, 0, 'Alright, I\'ll go. But this is the last time I\'m going to infiltrate the Horde! And I mean it.', 0, 12, 0, 100, 0, 0, 0, 70865, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68531, 2, 0, 'I\'m off to gather supplies and reconnect with old contacts. If I need backup in Pandaria, I\'ll send for ya!', 0, 12, 0, 100, 0, 0, 0, 70866, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68537, 0, 0, 'Now I can really get this party started!', 0, 12, 0, 100, 0, 0, 0, 70242, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68539, 0, 0, '', 0, 12, 0, 100, 0, 0, 0, 70239, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68540, 0, 0, '', 0, 12, 0, 100, 0, 0, 0, 70251, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68551, 0, 0, 'Light... I see... light. Finally, after centuries of silence, someone comes at last.', 0, 12, 0, 100, 0, 0, 34197, 69693, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68551, 1, 0, 'I have kept the Bell safe as promised, Master.', 0, 12, 0, 100, 0, 0, 34198, 69694, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68551, 2, 0, 'Wait... you are not mogu! Intruders!', 0, 12, 0, 100, 0, 0, 34199, 69695, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68551, 3, 0, 'Guardians, awaken! Destroy those who would steal the Thunder King\'s treasure!', 0, 12, 0, 100, 0, 0, 34200, 69696, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68586, 0, 0, 'Aethas, this way! Into the portal!', 0, 12, 0, 100, 0, 0, 34275, 69662, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68586, 1, 0, 'Enter the portal!', 0, 41, 0, 100, 0, 0, 0, 70408, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68589, 0, 0, 'Jaina\'s gone over the edge. She\'s imprisoning the Sunreavers and attacking those who resist. What\'s worse, the Silver Covenant has seized the opportunity to join the bloodshed.', 0, 12, 0, 100, 0, 0, 34269, 69656, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68590, 0, 0, 'It\'s chaos up there. We need to save whoever we can, and we\'ll need to move quickly.', 0, 12, 0, 100, 0, 0, 34270, 69657, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68593, 0, 0, 'Wait for my command...', 0, 12, 0, 100, 0, 0, 34271, 69658, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68593, 1, 0, 'Go go go go! To the center of the city! This way!', 0, 14, 0, 100, 0, 0, 34272, 69659, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68599, 0, 0, '', 2, 12, 0, 100, 1, 0, 0, 70429, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68599, 1, 0, 'Night Elf Construction Site Under Attack!', 2, 41, 0, 100, 30, 0, 34768, 70706, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68631, 0, 0, 'Aethas isn\'t here... they must have moved him. We can continue without him.', 0, 12, 0, 100, 0, 0, 34273, 69660, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68634, 0, 0, 'Stay by me! We\'re going out the tunnel!', 0, 12, 0, 100, 0, 0, 34276, 69663, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68634, 1, 0, 'Stay by my side!', 0, 12, 0, 100, 0, 0, 34277, 69664, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68692, 0, 0, 'You should not have interfered!', 0, 12, 0, 100, 0, 0, 0, 70520, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68692, 0, 1, 'What do you think you\'re doing?', 0, 12, 0, 100, 0, 0, 0, 70546, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68692, 0, 2, 'Interference will not be tolerated!', 0, 12, 0, 100, 0, 0, 0, 70551, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68692, 0, 3, 'Silence!', 0, 12, 0, 100, 0, 0, 0, 70548, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68692, 0, 4, 'Halt!', 0, 12, 0, 100, 0, 0, 0, 70547, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68692, 0, 5, 'Just who do you think YOU are?', 0, 12, 0, 100, 0, 0, 0, 70549, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68692, 0, 6, 'The Sunreavers are scum!', 0, 12, 0, 100, 0, 0, 0, 70550, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68695, 0, 0, 'I guess I can head back to Silvermoon...', 0, 12, 0, 100, 547, 0, 0, 70469, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68695, 0, 1, 'This is an outrage! Aethas will hear about this!', 0, 12, 0, 100, 412, 0, 0, 70481, '');
