INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68828, 0, 0, '', 0, 14, 0, 100, 0, 0, 0, 70852, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68831, 0, 0, 'We\'ve got our work cut out for us, $p.', 0, 14, 0, 100, 0, 0, 0, 69477, 'nalley intro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68831, 1, 0, 'Shield generators all over the island are protecting the Heart of Zan\'vess, preventing us from reaching the weapons cache.', 0, 14, 0, 100, 0, 0, 0, 69478, 'nalley about shields 1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68831, 2, 0, 'If we don\'t get this shield down soon we\'re going to be overrun.', 0, 14, 0, 100, 0, 0, 0, 69479, 'nalley about shields 2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68831, 3, 0, 'Aim for those amber bombs, they\'ll cause a huge explosion if we manage to set one off!', 0, 14, 0, 100, 0, 0, 0, 69483, 'nalley about thields 3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68831, 4, 0, 'Good shot, $p! Keep aiming for those bombs.', 0, 14, 0, 100, 0, 0, 0, 69484, 'nalley hit bombs');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68831, 5, 0, 'We\'re going in! Take out those towers!', 0, 14, 0, 100, 0, 0, 0, 69482, 'nalley about towers');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68831, 6, 0, 'Those Mantid are protecting the towers, $p. Take them out or we won\'t be able to land any shots.', 0, 14, 0, 100, 0, 0, 0, 69486, 'nalley protective towers');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68831, 7, 0, 'Only a few more towers and that whole thing is coming down!', 0, 14, 0, 100, 0, 0, 0, 70890, 'nalley towers');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68831, 8, 0, 'Those scorpid are moving the towers! Take them out!', 0, 14, 0, 100, 0, 0, 0, 70887, 'nalley scorpid towers');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (68831, 9, 0, 'The shield is down! Nice work, $p! I\'ll take us to the beachhead.', 0, 14, 0, 100, 0, 0, 0, 70891, 'nalley end of');
