INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69017, 1, 0, 'We will not be forgotten, no, no, no...', 0, 12, 0, 100, 0, 0, 35745, 71587, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69017, 2, 0, 'We are the perfect one, yes, yes, not a failure, no no, not a failure...', 0, 12, 0, 100, 0, 0, 35746, 71588, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69017, 3, 0, 'Your flesh, yes yes, so perfect, GIVE IT TO US!', 0, 14, 0, 100, 0, 0, 35742, 71590, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69017, 4, 0, 'No no no, the pain returns!', 0, 14, 0, 100, 0, 0, 35749, 71591, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69017, 5, 0, 'Again...we are torn apart...again...to the cold darkness...', 0, 14, 0, 100, 0, 0, 35743, 71594, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69017, 6, 0, 'Primordius |cFFFF0000|Hspell:136209|h[Evolves]|h|r and gains |cFFFF0000|Hspell:136210|h[Ventral Sacs]|h|r!', 0, 41, 0, 100, 0, 0, 0, 71603, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69017, 7, 0, 'Primordius |cFFFF0000|Hspell:136209|h[Evolves]|h|r and gains |cFFFF0000|Hspell:136215|h[Gas Bladder]|h|r!', 0, 41, 0, 100, 0, 0, 0, 71604, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69017, 8, 0, 'Primordius |cFFFF0000|Hspell:136209|h[Evolves]|h|r and gains |cFFFF0000|Hspell:136218|h[Acidic Spines]|h|r!', 0, 41, 0, 100, 0, 0, 0, 71605, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69017, 9, 0, 'Primordius |cFFFF0000|Hspell:136209|h[Evolves]|h|r and gains |cFFFF0000|Hspell:136225|h[Pathogen Glands]|h|r!', 0, 41, 0, 100, 0, 0, 0, 71606, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69017, 10, 0, 'Primordius |cFFFF0000|Hspell:136209|h[Evolves]|h|r and gains |cFFFF0000|Hspell:136245|h[Metabolic Boost]|h|r!', 0, 41, 0, 100, 0, 0, 0, 71607, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69017, 11, 0, 'Primordius |cFFFF0000|Hspell:136209|h[Evolves]|h|r and gains |cFFFF0000|Hspell:136246|h[Erupting Pustules]|h|r!', 0, 41, 0, 100, 0, 0, 0, 71608, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69026, 0, 0, 'You three are my eyes and ears. Fan out! Prepare a defense!', 2, 12, 0, 100, 397, 0, 34690, 70263, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69026, 1, 0, 'Make use of every resource! Leave them nowhere to run!', 2, 12, 0, 100, 1, 0, 0, 70276, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69026, 2, 0, 'Patience, Tyrande.', 2, 12, 0, 100, 1, 0, 0, 70277, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69026, 3, 0, 'We\'re almost ready. Keep on working!', 2, 12, 0, 100, 1, 0, 0, 70271, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69026, 4, 0, 'Ready our defenses. They have to come out eventually.', 2, 12, 0, 100, 1, 0, 0, 70266, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69026, 5, 0, 'This terrain is ours. Make them pay for every inch.', 2, 12, 0, 100, 1, 0, 0, 70274, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69026, 6, 0, 'Patience, Tyrande. We\'ll have our victory, but we\'re not going in unprepared.', 2, 12, 0, 100, 1, 0, 0, 70278, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69026, 7, 0, 'Don\'t charge in. Let them make the first mistake.', 2, 12, 0, 100, 1, 0, 0, 70273, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69026, 8, 0, 'I won\'t throw away Alliance lives. I will accept nothing less than total victory.', 2, 12, 0, 100, 1, 0, 0, 70279, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69026, 9, 0, 'They\'re coming. Get behind the defenses!', 2, 14, 0, 100, 1, 0, 34703, 70286, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69026, 10, 0, 'Come out and count them, coward!', 2, 14, 0, 100, 1, 0, 34704, 70287, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69026, 11, 0, 'There is no army here. Only a handful of heroes stand between you and the King of Stormwind. Are you afraid?', 2, 14, 0, 100, 1, 0, 34705, 70288, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69026, 12, 0, 'The day is ours! And not a drop of blood was shed on holy ground.', 2, 12, 0, 100, 1, 0, 34655, 70294, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69026, 13, 0, 'There was a time where I would have done the same. But I\'ve learned a lot over the years. I\'ve fallen for too many of Garrosh\'s tricks.', 2, 12, 0, 100, 1, 0, 34656, 70295, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69026, 14, 0, 'From now on, we define the terms of the engagement.', 2, 12, 0, 100, 1, 0, 34657, 70296, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69026, 15, 0, 'Tyrande, I may not be a politician. But I am a fighter. I know we can win this war.', 2, 12, 0, 100, 1, 0, 34658, 70297, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69026, 16, 0, 'We just need to stand together. You have to trust me. Know that I will do the right thing. If we act as one, we cannot fail.', 2, 12, 0, 100, 1, 0, 34659, 70298, '');
