INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69135, 1, 0, 'Lei Shen let us prove ta ya the might of the Zandalari. We will crush des intruders where dey stand!', 0, 14, 0, 100, 0, 0, 35405, 72021, 'Garajal Council Intro 1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69135, 2, 0, 'We will never fail ya!', 0, 14, 0, 100, 0, 0, 35406, 72022, 'Garajal Council Intro 2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69201, 0, 0, 'Unda my command, dis ship gonna be feared by all! Isn\'t dat right, Ripi?', 0, 14, 0, 100, 0, 0, 0, 73005, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69250, 0, 0, 'Once the incision is made, the blood flows quickly.', 0, 12, 0, 100, 0, 0, 0, 71398, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69272, 0, 0, 'Dis ship is unda new management! Anybody not pullin\' dey weight is gonna have dey spirit re-assigned to a golem.', 0, 14, 0, 100, 0, 0, 0, 73004, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69374, 0, 0, 'Welcome, weaklings, to the rebirth of the Zandalari Empire!', 0, 14, 0, 100, 0, 0, 36009, 71613, 'War-God Jalak INTRO 1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69374, 1, 0, 'The tribes have assembled - ye face not one force, but the combined might of all of the troll empire! The hand of Zul will span all the continents of Azeroth once again! An ye lesser races will know pain!', 0, 14, 0, 100, 0, 0, 36010, 71614, 'War-God Jalak INTRO 2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69374, 2, 0, 'Now, witness the true might of the Beast Ward. D\'akala di\'chuka HORRIDON! Kalimaste!', 0, 14, 0, 100, 0, 0, 36011, 71615, 'War-God Jalak INTRO 3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69374, 3, 0, 'Hahaha! Now it be my turn! Ye gonna see what it means to be a War-God!', 0, 14, 0, 100, 0, 0, 36007, 71620, 'War-God Jalak aggro');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69374, 4, 0, 'Drakkari tribe, show them the might of the frozen North!', 0, 14, 0, 100, 0, 0, 36013, 71616, 'War-God Jalak SPELL 1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69374, 5, 0, 'Gurubashi tribe, send their bloated corpses down the river with ya jungle poisons.', 0, 14, 0, 100, 0, 0, 36014, 71617, 'War-God Jalak SPELL 2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69374, 6, 0, 'Farraki tribe, flay their flesh wit the fury of the sands!', 0, 14, 0, 100, 0, 0, 36015, 71618, 'War-God Jalak SPELL 3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69374, 7, 0, 'Amani tribe, avenge ye fallen warlords, in the name of Zul\'jin!', 0, 14, 0, 100, 0, 0, 36016, 71619, 'War-God Jalak SPELL 4');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69374, 8, 0, 'Ya skull gonna make a fine ornament for my tusks.', 0, 14, 0, 100, 0, 0, 36012, 71621, 'War-God Jalak Kill');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69374, 9, 0, 'Da\'kala koraste...Horridon...destroy them...', 0, 14, 0, 100, 0, 0, 36008, 71622, 'War-God Jalak Death');
