INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69465, 10, 0, 'So easy to break.', 0, 14, 0, 100, 0, 0, 35552, 71973, 'Jin\'rokh the Breaker - Berserk');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69465, 11, 0, 'How you beat me?', 0, 14, 0, 100, 0, 0, 35549, 71974, 'Jin\'rokh the Breaker Death');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69473, 0, 0, 'For millennia I have waited in darkness, in silence pierced only by the shrieks of the tormented, my dreams a waking nightmare.', 0, 14, 0, 100, 0, 0, 35756, 72237, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69473, 1, 0, 'I see the naive hope in your eyes. You... think you are my saviors? Wring that mocking pity from your hearts and focus it inwards, for you shall soon know my agony.', 0, 14, 0, 100, 0, 0, 35757, 72238, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69473, 2, 0, 'This twisted world is beyond redemption, beyond the reach of deluded heroes. The only answer to corruption is destruction. And that begins... now.', 0, 14, 0, 100, 0, 0, 35758, 72239, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69473, 3, 0, 'When I am finished with you, I shall not rest until this world is cleansed. Now perish!', 0, 14, 0, 100, 0, 0, 35754, 72240, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69473, 4, 0, 'The touch of creation... and destruction.', 0, 14, 0, 100, 0, 0, 35763, 72236, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69473, 5, 0, 'You still think victory possible? Fools!', 0, 14, 0, 100, 0, 0, 35761, 72244, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69473, 6, 0, 'This is but a taste of my power.', 0, 14, 0, 100, 0, 0, 35765, 72233, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69473, 7, 0, 'Wait! I am... I am not your enemy. You are powerful. More powerful than he was, even.... Perhaps you are right. Perhaps there is still hope.', 0, 14, 0, 100, 0, 0, 35766, 72246, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69473, 8, 0, 'But there is a yawning chasm of darkness beneath you, mortals. Vast, endless, and all-consuming. I do not believe that you can correct this doomed course.', 0, 14, 0, 100, 0, 0, 0, 72250, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69473, 9, 0, 'Ah, the familiar embrace of the storms....', 0, 14, 0, 100, 0, 0, 35764, 72235, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69473, 10, 0, 'Feel your own life essence betray you.', 0, 14, 0, 100, 0, 0, 35762, 72241, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69475, 0, 0, 'They come! To arms me brothers - hurl them back to the sea!', 0, 12, 0, 100, 0, 0, 35415, 71676, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69475, 1, 0, 'You have not seen the full might o\' the Zandalari!', 0, 12, 0, 100, 0, 0, 35416, 71731, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69475, 2, 0, 'Hold the ruins. Slay them where they stand!', 0, 12, 0, 100, 0, 0, 35417, 71677, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69475, 3, 0, 'Crush the intruders. The barrier must not fall.', 0, 12, 0, 100, 0, 0, 35418, 71678, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69475, 4, 0, 'Face me now! My will is iron. My people, unbreakable!', 0, 12, 0, 100, 0, 0, 35419, 71679, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69475, 5, 0, 'Zandalar...', 0, 12, 0, 100, 0, 0, 35423, 71683, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69476, 0, 0, 'The %s begins to cast Hymn of Silence! Hide inside a barrier!', 0, 41, 0, 100, 0, 0, 0, 73044, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69481, 0, 0, 'Elsia is waiting for you, champion.', 0, 12, 0, 100, 0, 0, 35664, 72410, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69481, 1, 0, '', 0, 14, 0, 100, 0, 0, 35938, 72647, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69481, 2, 0, 'Incredible work. I knew the Shado-Pan would make excellent allies.', 0, 12, 0, 100, 0, 0, 35666, 72412, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69481, 3, 0, 'Regardless, we have delivered a terrible blow to the Thunder King today. Horde metalsmiths can begin studying mogu fabrication techniques immediately.', 0, 12, 0, 100, 0, 0, 35667, 72413, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69481, 4, 0, 'In good time, my pandaren friend. In good time.', 0, 12, 0, 100, 0, 0, 35668, 72414, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69482, 0, 0, 'Hm-wha? Stowaways!', 0, 12, 0, 100, 0, 0, 35321, 71842, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69482, 1, 0, 'Oh hoh, you tickle me. But I be done playin\' with you.', 0, 12, 0, 100, 0, 0, 35323, 71844, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69482, 2, 0, '|TInterface\\Icons\\spell_arcane_mindmastery.blp:20|t %s wakes up with a terrible |cFFFF0000|Hspell:140432|h[Hangover]|h|r!', 0, 41, 0, 100, 0, 0, 0, 73028, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69482, 3, 0, 'You... never get outta here... alive.', 0, 12, 0, 100, 0, 0, 35325, 71846, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69483, 0, 0, 'The Thunder King wills it; it shall be done.', 0, 12, 0, 100, 0, 0, 35446, 71834, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69483, 1, 0, 'Enough hiding, little ones!', 0, 14, 0, 100, 0, 0, 35447, 71854, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69483, 2, 0, 'There is no escape now. The only way out is through me!', 0, 14, 0, 100, 0, 0, 35448, 71855, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69483, 3, 0, '|TInterface\\Icons\\spell_shadow_destructivesoul.blp:20|t%s casts |cFFFF0000|Hspell:140543|h[Empower Axe]|h|r! Run Away!', 0, 41, 0, 100, 0, 0, 0, 73035, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69483, 4, 0, 'You are finished!', 0, 14, 0, 100, 0, 0, 35454, 71863, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69483, 5, 0, '|TInterface\\Icons\\warrior_talent_icon_bloodandthunder.blp:20|t%s begins to cast |cFFFF0000|Hspell:140531|h[Desist]|h|r! Kill the  |cFFFF0000Chains|r to free yourself!', 0, 41, 0, 100, 0, 0, 0, 73036, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69483, 6, 0, 'Trespassers will be executed!', 0, 14, 0, 100, 0, 0, 35452, 71861, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69483, 7, 0, 'To strike me is to strike his Majesty! He will not suffer this insult!', 0, 14, 0, 100, 0, 0, 35455, 71864, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69483, 8, 0, '|TInterface\\Icons\\ability_thunderking_thunderstruck.blp:20|tLei Shen has become angered and calls down a |cFFFF0000|Hspell:140549|h[Lightning Storm]|h|r!', 0, 41, 0, 100, 0, 0, 0, 73034, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69483, 9, 0, 'None... shall...', 0, 12, 0, 100, 0, 0, 35456, 71865, '');
