INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 0, 0, 'YOU will kneel when you beg for my mercy, monster.', 0, 14, 0, 100, 0, 0, 35888, 71766, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 1, 0, 'Those statues wield the power of the first emperor. Destroy them!', 0, 14, 0, 100, 0, 0, 35889, 71768, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 2, 0, 'Taoshi, get those doors open.', 0, 12, 0, 100, 0, 0, 35890, 71769, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 3, 0, 'Champion, with me. We must press forward while your comrades cover the rear.', 0, 12, 0, 100, 0, 0, 35891, 71770, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 4, 0, 'Shan Bu! Again you try to create a God. When will you learn?', 0, 14, 0, 100, 0, 0, 35892, 71776, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 5, 0, 'Zandalari reinforcements are coming.', 0, 14, 0, 100, 0, 0, 35893, 71777, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 6, 0, 'Yalia - we need more healing! Taoshi - cover the rear!', 0, 14, 0, 100, 0, 0, 35894, 71778, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 7, 0, 'Face me, Shan Bu! Or is the court of the Thunder King a den of cowards?', 0, 14, 0, 100, 0, 0, 35895, 71779, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 8, 0, 'Dance around Shan Bu\'s attacks. We are faster! We are smarter!', 0, 14, 0, 100, 0, 0, 35898, 71879, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 9, 0, 'Auugh - kill him. Quickly!', 0, 14, 0, 100, 0, 0, 35903, 71886, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 10, 0, 'You see? No power of this world can trample the pure of spirit.', 0, 12, 0, 100, 0, 0, 35905, 71919, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 11, 0, 'Like children.', 0, 12, 0, 100, 0, 0, 36158, 71921, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 12, 0, 'Taoshi! Hand me my weapon.', 0, 12, 0, 100, 0, 0, 36159, 71922, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 13, 0, 'My weapon! Champion, with me. I want you to see this.', 0, 12, 0, 100, 0, 0, 35907, 71924, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 14, 0, 'ENOUGH!! There will be no more bloodshed today.', 0, 14, 0, 100, 0, 0, 35908, 71930, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 15, 0, 'I see now why your Alliance and your Horde cannot stop fighting.', 0, 14, 0, 100, 0, 0, 35909, 71931, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 16, 0, 'Every reprisal is itself an act of aggression, and every act of aggression triggers immediate reprisal.', 0, 14, 0, 100, 0, 0, 35910, 71932, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 17, 0, 'SILENCE! YOU must break the cycle.', 0, 14, 0, 100, 0, 0, 35911, 71935, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 18, 0, 'It ends TODAY. Here. The cycle ends when you, Regent Lord, and you, Lady Proudmoore, turn from one another. And walk. Away.', 0, 14, 0, 100, 0, 0, 35912, 71936, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 19, 0, 'Champion, this cease-fire may not last, but remember what you have seen today.', 0, 12, 0, 100, 0, 0, 35913, 71953, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69501, 20, 0, 'Shan Bu\'s monstrosity remains at the foot of the palace. It must be stopped before it wreaks havoc across Pandaria.', 0, 12, 0, 100, 0, 0, 35914, 71955, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69530, 0, 0, 'Taran Zhu! It does not matter how many you array against the Thunder King. Soon all will kneel before him.', 0, 14, 0, 100, 0, 0, 35769, 71767, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69530, 1, 0, 'Who needs a God? We have the Thunder King!', 0, 14, 0, 100, 0, 0, 35770, 71774, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69530, 2, 0, 'Trolls - make yourselves useful. I am almost finished here.', 0, 14, 0, 100, 0, 0, 35771, 71775, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69530, 3, 0, 'Arise, Nalak! Protect our beloved emperor.', 0, 14, 0, 100, 0, 0, 35772, 71780, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69530, 4, 0, 'The emperor will be most pleased when I present him with your head, Shado-Pan.', 0, 14, 0, 100, 0, 0, 35773, 71781, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69530, 5, 0, 'Ours is to rule. Yours is to serve!', 0, 14, 0, 100, 0, 0, 35778, 71882, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69530, 6, 0, 'I tire of your insolence! I will choke the life from you, impudent slave!', 0, 14, 0, 100, 0, 0, 35779, 71885, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69530, 7, 0, 'I ... no... My emperor! My emperor, I have failed you!', 0, 14, 0, 100, 0, 0, 0, 71889, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69538, 0, 0, 'They come! To arms me brothers - hurl them back to the sea!', 0, 12, 0, 100, 0, 0, 35415, 71676, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69538, 1, 0, 'You have not seen the full might o\' the Zandalari!', 0, 12, 0, 100, 0, 0, 35416, 71731, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69538, 2, 0, 'Hold the ruins. Slay them where they stand!', 0, 12, 0, 100, 0, 0, 35417, 71677, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69538, 3, 0, 'Crush the intruders. The barrier must not fall.', 0, 12, 0, 100, 0, 0, 35418, 71678, '');
