INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69617, 4, 0, 'One problem at a time, people. Focus on the Zandalari!', 0, 14, 0, 100, 0, 0, 35643, 71765, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69617, 5, 0, 'Proudmoore! You will release my people from the Violet Hold, or I will cut you down myself!', 0, 14, 0, 100, 0, 0, 35608, 71926, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69617, 6, 0, 'The Sunreavers knew NOTHING of Garrosh\'s raid on Darnassus!', 0, 14, 0, 100, 0, 0, 35609, 71928, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69617, 7, 0, 'I must protect my sovereign people.', 0, 14, 0, 100, 0, 0, 35610, 71934, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69617, 8, 0, 'Rangers. Lower your weapons.', 0, 12, 0, 100, 0, 0, 35611, 71939, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69617, 9, 0, 'That is precisely why I wish to conserve our strength today.', 0, 12, 0, 100, 0, 0, 35613, 71946, '');
