INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69617, 1, 0, 'Ahhh, so the grand lady of the Kirin Tor wants a fight, does she?', 0, 12, 0, 100, 0, 0, 35640, 71758, '');
