INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69923, 1, 0, 'Bend before me!', 0, 14, 0, 100, 0, 0, 35791, 72434, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69923, 2, 0, 'You will be... punished...', 0, 14, 0, 100, 0, 0, 35788, 72436, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69926, 0, 0, 'How dare you interrupt our preparations! The Zandalari will not be stopped, not this time!', 0, 14, 0, 100, 1, 0, 35737, 72392, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69926, 1, 0, 'Destroy them, I command you! No, stop, stop!', 0, 14, 0, 100, 25, 0, 35736, 72393, '');
