INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69964, 0, 0, 'BEHOLD! I have truly mastered the fel energies of this world! The demonic power I now command... It is indescribable, unlimited, OMNIPOTENT!', 0, 12, 0, 100, 1, 0, 0, 72452, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69964, 1, 0, 'You think I\'ve gone too far? There is no such thing. The Black Harvest will enslave the most powerful demons, draw on their power, and destroy all who oppose us, in this world, and on Azeroth! Do YOU mean to stop me, little one?', 0, 12, 0, 100, 1, 0, 0, 72453, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69964, 2, 0, 'I\'d like to see you try.', 0, 12, 0, 100, 1, 0, 0, 72457, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69964, 3, 0, 'Hah. Your feeble attacks are entertaining. Would you like to see one of my new pets?', 0, 12, 0, 100, 1, 0, 0, 72456, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69964, 4, 0, 'The Annihilan are incredibly destructive creatures. Their sheer strength and power is absolutely unrivaled.', 0, 12, 0, 100, 1, 0, 0, 72458, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69964, 5, 0, 'Hah ha ha... You thought you could defeat me? NOTHING can kill me now, puny mortal!', 0, 12, 0, 100, 1, 0, 0, 72500, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69964, 6, 0, 'And now, your sad tale comes to an end.', 0, 12, 0, 100, 1, 0, 0, 72501, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (69964, 7, 0, 'Jubeka?! What are you...?!', 0, 12, 0, 100, 1, 0, 0, 72502, '');
