INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70055, 0, 0, 'Cover one another! Stand your ground!', 0, 14, 0, 100, 0, 0, 35625, 72301, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70055, 1, 0, 'Spectacular! Move up - press onward. Show them no quarter!', 0, 14, 0, 100, 0, 0, 35632, 72677, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70055, 2, 0, 'Rangers - to me! We have broken their lines!', 0, 12, 0, 100, 0, 0, 35633, 72682, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70055, 3, 0, 'Nonsense. This is where I belong.', 0, 12, 0, 100, 0, 0, 35634, 72683, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70055, 4, 0, 'Champion! Again you are at the forefront of a Horde triumph. And once more I am in your debt.', 0, 12, 0, 100, 0, 0, 35635, 72684, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70055, 5, 0, 'Regroup behind the wards, everyone. They will counter-attack... but we will be ready.', 0, 12, 0, 100, 0, 0, 35636, 72685, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70056, 0, 0, 'Des fools thought they beat me once before. Dey only make me stronger. Now we show dem da true power of the Zandalari!', 0, 14, 0, 100, 11, 0, 35404, 72020, 'ToT Garajal Intro 1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70056, 1, 0, 'Let me share dis gift with ya!', 0, 14, 0, 100, 11, 0, 35395, 71361, 'ToT Garajal Intro 2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70056, 2, 0, 'Da Zandalari cannot be stopped!', 0, 14, 0, 100, 11, 0, 35400, 72016, 'ToT Garajal Intro 3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70056, 3, 0, 'Wit de Thunder King\'s power de Zandalari will be reborn!', 0, 14, 0, 100, 11, 0, 35401, 72017, 'ToT Garajal Intro 4');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70056, 4, 0, 'De Thunder King will reward us for stoppin\' ya!', 0, 14, 0, 100, 11, 0, 35397, 72013, 'ToT Garajal Intro End');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70061, 0, 0, 'The Thunder Forge has begun to charge an area nearby.  Run into it to |cFFFF2222|Hspell:139397|h[Overcharge]|h|r yourself!', 0, 41, 0, 100, 0, 0, 0, 72741, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70074, 0, 0, 'Forgemaster Vul\'kon equips his Staff and begins to channel the powers of lightning!', 0, 41, 0, 100, 0, 0, 0, 72995, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70074, 1, 0, 'Forgemaster Vul\'kon equips his Hammer and begins to swing with great force!', 0, 41, 0, 100, 0, 0, 0, 72996, '');
