INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70099, 0, 0, 'The Shado-pan Defender has placed a |cFFFF2222|Hspell:132744|h[Healing Orb]|h|r!', 0, 41, 0, 100, 0, 0, 0, 73015, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70099, 1, 0, 'Thank you for your help, adventurer.  We will assist you as best we can in fending off the mogu.', 0, 14, 0, 100, 0, 0, 0, 72419, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70099, 2, 0, 'The forge is safe, and we must take our leave.  Good luck on your quest, adventurer.', 0, 12, 0, 100, 0, 0, 0, 72800, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 0, 0, 'The trillium you brought me must be refined for our purposes.', 0, 12, 0, 100, 1, 0, 36021, 72551, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 1, 0, 'This foundry should be sufficient.  Help me manipulate the molds.', 0, 12, 0, 100, 25, 0, 36022, 72552, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 2, 0, 'The Shado-Pan are here! They may be of use to us. We should help.', 0, 12, 0, 100, 0, 0, 36402, 72656, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 3, 0, 'This foundry should be sufficient.  We will empower the metal with an electrical charge.', 0, 12, 0, 100, 1, 0, 36405, 72657, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 4, 0, 'Be ready to fight if we draw too much attention.', 0, 14, 0, 100, 0, 0, 36023, 72553, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 5, 0, 'Excellent! The materials are prepared.  Follow me.', 0, 12, 0, 100, 4, 0, 36026, 72556, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 6, 0, 'How interesting! This forge is built atop of ancient Titan machinery. Lei Shen, you clever, clever tyrant, you!', 0, 12, 0, 100, 0, 0, 36027, 72557, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 7, 0, 'Now if I were a dwarf I would just start randomly pushing buttons. But you and I are here for a purpose.', 0, 12, 0, 100, 0, 0, 36028, 72558, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 8, 0, 'Look at that. How easy it must have been for Lei Shen to build an empire, for he wielded the power to create worlds.', 0, 12, 0, 100, 0, 0, 36030, 72560, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 9, 0, 'The mogu were using only a fraction of the resources available to them. Few among them possessed the cunning of the Thunder King.', 0, 12, 0, 100, 0, 0, 36039, 72569, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 10, 0, 'What\'s this - "Anomaly detected." Anomaly?', 0, 12, 0, 100, 6, 0, 36040, 72570, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 11, 0, 'Look at that. This old Titan technology is acting like a magnet for the sha.', 0, 12, 0, 100, 1, 0, 36041, 72571, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 12, 0, 'You\'re an expert at destroying sha by now. Clean up this mess!', 0, 12, 0, 100, 396, 0, 36042, 72572, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 13, 0, 'Perhaps if I overcharge the forge, it will destabilize this sha nuisance? Let\'s try it.', 0, 12, 0, 100, 0, 0, 36406, 72658, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 14, 0, 'Oh - I may have spoken too soon. The sha grows in strength! Destroy it!', 0, 12, 0, 100, 274, 0, 36408, 72660, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 15, 0, 'You aren\'t harboring any bad thoughts, are you?', 0, 12, 0, 100, 0, 0, 36409, 72661, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 16, 0, 'There! Our work is complete - we have forged a masterpiece!', 0, 12, 0, 100, 1, 0, 36049, 72579, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70100, 17, 0, 'We are done here, but the weapon is not yet tempered.', 0, 12, 0, 100, 1, 0, 36050, 72580, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70126, 0, 2, 'I won a Brawler\'s Guild invitation just now! I\'ll surely be rank 8 by tonight!  I sure hope I don\'t lose my ticket before I get there.', 0, 12, 0, 100, 0, 0, 0, 72443, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70126, 0, 1, 'Everyone look! I won a Brawler\'s Guild invite! I can\'t wait to try it out!', 0, 12, 0, 100, 0, 0, 0, 72442, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70126, 0, 0, 'YES! Finally! I won a Brawler\'s Guild invite! Nobody can stop me now!', 0, 12, 0, 100, 0, 0, 0, 72440, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70126, 1, 0, 'I guess I wasn\'t ready to be a brawler.', 0, 12, 0, 100, 0, 0, 0, 72441, '');
