INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70283, 1, 0, 'I\'ve overcharged the anvils. They will be VERY effective against the sha, but each one can only be used once more!', 0, 12, 0, 100, 22, 0, 36410, 72662, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70286, 0, 0, 'Shaol\'mara Beastcaller summons raptors to his aid!', 0, 41, 0, 100, 0, 0, 0, 72708, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70358, 0, 0, '', 0, 12, 0, 100, 0, 0, 35349, 72780, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70371, 0, 0, 'Aethas, leave us for a moment.', 0, 12, 0, 100, 0, 0, 35651, 72790, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70371, 1, 0, 'Hero, you have served the Horde nobly across many campaigns. I feel I can take you into confidence.', 0, 12, 0, 100, 0, 0, 35652, 72791, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70371, 2, 0, 'Our Warchief\'s campaign across Kalimdor and Pandaria is pulling the Horde apart at the seams.', 0, 12, 0, 100, 0, 0, 35653, 72792, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70371, 3, 0, 'We are here to fight the Thunder King. Justice demands it, and Hellscream is too preoccupied at Domination Point to pay attention here.', 0, 12, 0, 100, 0, 0, 35654, 72793, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70371, 4, 0, 'But, in defeating him, we must seek out the source of his great power. We must claim it for ourselves, as leverage against our "Warchief."', 0, 12, 0, 100, 0, 0, 35655, 72794, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70371, 5, 0, 'In the event of an uprising, we must be prepared.', 0, 12, 0, 100, 0, 0, 35656, 72795, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70371, 6, 0, 'Heads up!', 0, 12, 0, 100, 0, 0, 35657, 72796, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70371, 7, 0, 'Ha-hah! It will feel good to bloody my blade again. Against Alliance or mogu, does not matter to me.', 0, 12, 0, 100, 0, 0, 35658, 72797, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70371, 8, 0, 'Enough talk. You know how precarious our position is. Between the Alliance and the Thunder King, only with the help of heroes like you can we hope to succeed here.', 0, 12, 0, 100, 0, 0, 35659, 72798, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70371, 9, 0, 'Anar\'alah belore, hero. Good luck.', 0, 12, 0, 100, 0, 0, 35660, 72799, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70372, 0, 0, '', 0, 12, 0, 100, 0, 0, 35970, 72765, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70372, 1, 0, '', 0, 12, 0, 100, 0, 0, 35971, 72766, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70372, 2, 0, '', 0, 12, 0, 100, 0, 0, 35972, 72767, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70373, 0, 0, '', 0, 12, 0, 100, 0, 0, 35350, 72781, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70373, 1, 0, '', 0, 12, 0, 100, 0, 0, 35351, 72782, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70373, 2, 0, '', 0, 12, 0, 100, 0, 0, 35352, 72783, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70437, 0, 0, 'You have swept the filth from my doorstep. Perhaps you are worthy of my attention.', 0, 14, 0, 100, 0, 0, 35587, 72507, 'Lei shen event 1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70437, 1, 0, 'But your trespass ends here. None may enter my forbidden stronghold. I shall rebuild this bridge with your bones for bricks.', 0, 14, 0, 100, 0, 0, 35588, 72508, 'Lei Shen event 2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70474, 0, 0, '$n has been |cFFFF0000|Hspell:141407|h[Frozen Solid]|h|r. Break $ghim:her; free!', 2, 41, 0, 100, 0, 0, 0, 73289, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70516, 11, 0, 'Ah! The final piece of the puzzle. The Thunder King took on the powers of Ra-Den, the very essence of a Titan. He united the mogu into an empire and tried to continue the Titans\' work. Of course he saw himself as a God!', 0, 12, 0, 100, 0, 0, 36423, 72372, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70516, 10, 0, 'Hmmm. This runestone speaks of mogu savagery. They fell apart into warrior clans. But one King was more ambitious than the others - Lei Shen. The Thunder King. When the Titan Keeper fell silent, Lei Shen decided to speak for him.', 0, 12, 0, 100, 0, 0, 36422, 72371, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70516, 9, 0, 'You\'ve found another piece of the puzzle! This was carved by the mogu. They were frustrated - a thousand years with no orders from their master, and their bodies had turned to flesh. The mogu began to argue among themselves.', 0, 12, 0, 100, 0, 0, 36421, 72370, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70516, 8, 0, 'This runestone is more recent than the others. Something about "The sleeping keeper." I think it\'s referring to Ra-Den. But why was he asleep?', 0, 12, 0, 100, 0, 0, 36420, 72369, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70516, 7, 0, 'Here, this fragment speaks of "The Winged Legions of Y\'Shaarj." Servants of an Old God? Could this mean the mantid?', 0, 12, 0, 100, 0, 0, 36419, 72368, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70516, 6, 0, 'We\'re beginning to put the pieces together, champion. The mogu were created and sent here to guard the Vale for their Titan masters. Guard it from what? Were the Old Gods still a threat?', 0, 12, 0, 100, 0, 0, 36418, 72367, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70516, 5, 0, 'This runestone speaks of a "Cradle of Life." A place where the Titans could create new creatures. Oh - of course - the Vale of Eternal Blossoms! It was a living laboratory, much like Un\'goro Crater or Sholazar Basin.', 0, 12, 0, 100, 0, 0, 36417, 72366, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70516, 4, 0, 'Here, there is mention of an Old God. "Y\'Shaarj." A beast with seven heads, who breathed darkness. I suspect that thing was the source of the sha that haunts Pandaria to this day.', 0, 12, 0, 100, 0, 0, 36416, 72365, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70516, 3, 0, 'According to this, the mogu and their master were sent to Pandaria. They were pursuing a great darkness - ah, the fragment is incomplete. We must find others.', 0, 12, 0, 100, 0, 0, 36415, 72364, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70516, 2, 0, 'The mogu served a Titan keeper. His name was "Ra." In the mogu tongue, "Ra-den" means "Master Ra." Fascinating! I must know more!', 0, 12, 0, 100, 0, 0, 36414, 72363, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70516, 1, 0, 'You\'ve found another stone! What have we learned? The "curse of flesh" made the mogu mortal. Ah - but later, they found a way to reverse the process. A clever race.', 0, 12, 0, 100, 0, 0, 36413, 72362, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70516, 0, 0, 'According to this runestone, the mogu were Titan constructs, soulless creatures built of stone. We suspected as much.', 0, 12, 0, 100, 0, 0, 36412, 72361, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70544, 0, 0, 'You may have stopped the storm, but you cannot stop the will of Zandalar! You will DIE, here... ALL OF YOU!', 2, 14, 0, 100, 0, 0, 37173, 73452, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70544, 1, 0, 'Trolls of the world will unite against you!', 2, 14, 0, 100, 0, 0, 37174, 73454, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70544, 2, 0, 'We control the sand, the seas, the winds...', 2, 14, 0, 100, 0, 0, 37175, 73455, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70544, 3, 0, 'We will bury you in your mountain!', 2, 14, 0, 100, 0, 0, 37176, 73456, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70544, 4, 0, 'There... will be more! Zandalar... will never fall...', 2, 14, 0, 100, 0, 0, 37172, 73457, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70602, 0, 0, 'Oh ho ho ho ho! By my beard, I thought I was done for! Oh, thank ye. Thank ye!', 2, 14, 0, 100, 4, 0, 37296, 73249, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70602, 1, 0, 'Oh... right. Now: Did the human king send ye? Oh, his spies are all dead... and most of us are, too.', 2, 12, 0, 100, 6, 0, 37297, 73250, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70602, 2, 0, 'I saw \'em draggin\' Boldbrew, kickin\' and bitin\', towards some hut south o\' here. Now if ye hurry, ye can help her, and she might know more.', 2, 12, 0, 100, 25, 0, 0, 73251, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70602, 3, 0, 'BUT STAY AWAY FROM THE  BIG VILLAGE! There\'s dozens o\' mad ice trolls in there! Oh, that Zandalari priest has \'em whipped up into a great frenzy.', 2, 12, 0, 100, 5, 0, 0, 73252, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 0, 0, 'Newt, get that door open.', 2, 12, 0, 100, 0, 0, 0, 73081, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 1, 0, 'Grit, Ticker and Volt, you three back her up.', 2, 12, 0, 100, 0, 0, 0, 73087, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 2, 0, 'Recruit $p, you\'re with me.', 2, 12, 0, 100, 0, 0, 0, 73082, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 3, 0, 'We\'ve tracked the recent disappearances in Durotar to this abandoned section of Ragefire.', 2, 12, 0, 100, 0, 0, 0, 73083, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 4, 0, 'It\'s our job to find out who\'s behind these abductions and put a stop to them.', 2, 12, 0, 100, 0, 0, 0, 73086, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 5, 0, 'Stay close.', 2, 12, 0, 100, 0, 0, 0, 73088, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 6, 0, 'Newt, we need this door open now.', 2, 12, 0, 100, 0, 0, 0, 73089, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 7, 0, 'Move! Move! Move!', 2, 14, 0, 100, 0, 0, 0, 73092, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 8, 0, 'What the...', 2, 14, 0, 100, 0, 0, 0, 73100, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 9, 0, 'Gob Squad, defend yourselves!', 2, 14, 0, 100, 0, 0, 0, 73707, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 10, 0, 'We aren\'t your enemy, Xorenth!', 2, 14, 0, 100, 0, 0, 0, 73447, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 11, 0, 'Lesser? We\'ll show you who\'s lesser!', 2, 14, 0, 100, 0, 0, 0, 73712, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 12, 0, 'This is bad.', 2, 12, 0, 100, 0, 0, 0, 73445, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 13, 0, 'All of you fan out and search the chamber. If Garrosh is behind this, we\'re going to need proof.', 2, 12, 0, 100, 0, 0, 0, 73446, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 14, 0, 'Quickly, $p! We need you over here!', 2, 14, 0, 100, 0, 0, 0, 73451, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 15, 0, 'Can you open this, Ticker?', 2, 12, 0, 100, 0, 0, 0, 73495, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 16, 0, 'A mantid bomb?! Are you sure that won\'t bring down the whole cavern?', 2, 12, 0, 100, 0, 0, 0, 73498, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 17, 0, 'I don\'t care what it\'s for! Is it going to get this door open?', 2, 12, 0, 100, 0, 0, 0, 73647, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 18, 0, 'That door didn\'t even budge...', 2, 12, 0, 100, 0, 0, 0, 73668, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 19, 0, 'Grit, setup the emergency teleporter! We need to report this to Vol\'jin immediately.', 2, 12, 0, 100, 0, 0, 0, 73677, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 20, 0, 'Recruits, you\'re the only ones left that can fight!', 2, 14, 0, 100, 0, 0, 0, 73683, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 21, 0, 'Defend us until the teleporter is ready!', 2, 14, 0, 100, 0, 0, 0, 73684, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70605, 22, 0, 'Everyone out before more of them show up!', 2, 14, 0, 100, 0, 0, 0, 73878, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70606, 0, 0, '', 2, 12, 0, 100, 0, 0, 0, 73084, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70606, 1, 0, '', 2, 12, 0, 100, 0, 0, 0, 73090, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70606, 2, 0, '', 2, 12, 0, 100, 0, 0, 0, 73091, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70606, 3, 0, '', 2, 12, 0, 100, 0, 0, 0, 73450, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70606, 4, 0, '', 2, 12, 0, 100, 0, 0, 0, 73645, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70606, 5, 0, '', 2, 14, 0, 100, 0, 0, 0, 73667, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70607, 0, 0, 'Proto-drake eggs... is Garrosh working with the Dragonmaw? Orcs on proto-drakes sounds bad to me, $p. Very bad.', 2, 12, 0, 100, 0, 0, 0, 73449, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70607, 1, 0, 'Warn us a little sooner next time!', 2, 14, 0, 100, 0, 0, 0, 73669, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70607, 2, 0, 'Calm down! I\'d hardly call that a fire!', 2, 14, 0, 100, 0, 0, 0, 73673, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70607, 3, 0, 'Right away, Sir!', 2, 12, 0, 100, 0, 0, 0, 73678, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70607, 4, 0, 'The teleporter is ready! Let\'s get out of here!', 2, 14, 0, 100, 0, 0, 0, 73839, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70608, 0, 0, 'Sir, these are Garrosh\'s men.', 2, 12, 0, 100, 0, 0, 0, 73440, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70608, 1, 0, 'It\'s... it\'s beautiful!', 2, 12, 0, 100, 0, 0, 0, 73644, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70608, 2, 0, 'Help!', 2, 14, 0, 100, 0, 0, 0, 73671, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70608, 3, 0, 'I\'m on fire!', 2, 14, 0, 100, 0, 0, 0, 73672, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70609, 0, 0, 'On it.', 2, 12, 0, 100, 0, 0, 0, 73163, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70609, 1, 0, 'Sir, these Kor\'kron don\'t seem very friendly!', 2, 14, 0, 100, 0, 0, 0, 73181, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70609, 2, 0, 'I\'ve got just the thing!', 2, 12, 0, 100, 0, 0, 0, 73497, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70609, 3, 0, 'I\'ve been saving this for a special occasion.', 2, 12, 0, 100, 0, 0, 0, 73496, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70609, 4, 0, 'We\'ll need to make a few modifications, but it will get the job done.', 2, 12, 0, 100, 0, 0, 0, 73499, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70609, 5, 0, 'Recruit, bring me that pool pony, three cannon balls, two batteries, and some of that proto-drake egg yolk.', 2, 12, 0, 100, 0, 0, 0, 73500, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70609, 6, 0, 'You carried that with your hands?! Uh... you should probably see a medic when we get back...', 2, 14, 0, 100, 0, 0, 0, 73630, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70609, 7, 0, 'That\'s exactly what we needed!', 2, 12, 0, 100, 0, 0, 0, 73627, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70609, 8, 0, 'The most important ingredient!', 2, 12, 0, 100, 0, 0, 0, 73639, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70609, 9, 0, 'Careful! This isn\'t a toy!', 2, 12, 0, 100, 0, 0, 0, 73628, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70609, 10, 0, 'Just a little more power...', 2, 12, 0, 100, 0, 0, 0, 73641, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70609, 11, 0, 'This should give us enough power to disable the stabilization matrix.', 2, 12, 0, 100, 0, 0, 0, 73640, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70609, 12, 0, 'My masterpiece is finished!', 2, 14, 0, 100, 0, 0, 0, 73643, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70609, 13, 0, 'Don\'t question my genius!', 2, 12, 0, 100, 0, 0, 0, 73646, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70609, 14, 0, 'This thing could open Gallywix\'s personal safe!', 2, 12, 0, 100, 0, 0, 0, 73649, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70609, 15, 0, 'Once the detonator goes off in... err... NOW!', 2, 12, 0, 100, 0, 0, 0, 73650, '');
