INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70683, 0, 0, 'Intruders! Stop them!', 2, 14, 0, 100, 0, 0, 0, 73107, '');
