INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70683, 2, 0, 'Seal the gate, and inform the overseer immediately.', 2, 14, 0, 100, 0, 0, 0, 73429, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70683, 3, 0, 'All lesser races are enemies of the true Horde!', 2, 14, 0, 100, 0, 0, 0, 73430, '');
