INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70744, 2, 0, '', 2, 12, 0, 100, 397, 0, 0, 73256, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70745, 0, 0, 'Filthy monsters! I owe ye me life.', 2, 14, 0, 100, 4, 0, 37294, 73274, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70745, 1, 0, 'Listen: The storm. We figured out what\'s makin\' it. That sneaky Zandalari has these ice trolls makin\' blood sacrifices ta elemental spirits.', 2, 12, 0, 100, 1, 0, 37295, 73275, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70745, 2, 0, 'There\'s a cave, north o\' here, across a bridge. If ye can slay the elemental within, the storm ought ta stop.', 2, 12, 0, 100, 25, 0, 0, 73276, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70788, 0, 0, '+5 Artifact Bonus!', 0, 41, 0, 100, 0, 0, 0, 74266, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70792, 0, 0, '+10 Artifact Bonus!', 0, 41, 0, 100, 0, 0, 0, 74267, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70799, 0, 0, 'This is where my agents tracked the Zandalari.', 2, 12, 0, 100, 25, 0, 37328, 73193, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70799, 1, 0, 'I can only imagine what foul business the Zandalari have with them...', 2, 12, 0, 100, 1, 0, 37329, 73194, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70799, 2, 0, 'Yes. This weather is not natural.', 2, 12, 0, 100, 1, 0, 37330, 73195, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70799, 3, 0, 'My champions will go. They will see what can be done, and rescue who they can.', 2, 12, 0, 100, 1, 0, 37331, 73196, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70799, 4, 0, 'It is heartening to see you here, Moira. You have my thanks.', 2, 12, 0, 100, 2, 0, 37332, 73197, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70799, 5, 0, 'Hrmmm. Fare well, Queen Regent.', 2, 12, 0, 100, 66, 0, 37333, 73199, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70799, 6, 0, 'Well fought. I see the Dark Irons are as formidable as ever.', 2, 12, 0, 100, 1, 0, 37334, 73461, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70799, 7, 0, 'Hah! For that, I\'m glad. The Bronzebeards and Wildhammers will regret not having been here to share in your glory.', 2, 12, 0, 100, 1, 0, 37335, 73462, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70799, 8, 0, 'Well, you\'ve done just that. Come; let us return to Ironforge, and announce your victory to the Council.', 2, 12, 0, 100, 1, 0, 37336, 73463, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70801, 0, 0, 'The nearest is just ahead. If ye can save him, he may know a thing or two.', 2, 12, 0, 100, 0, 0, 37263, 73219, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70801, 1, 0, 'We\'ll cover ye from the brush: Point out a target, and we\'ll open fire.', 2, 12, 0, 100, 396, 0, 0, 74081, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70824, 0, 0, 'Fiery Anger is about to |cFFFF2222|Hspell:141456|h[Detonate]|h|r!', 0, 41, 0, 100, 0, 0, 0, 73320, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70956, 0, 0, 'Thank the laws of physics you\'re here!', 0, 12, 0, 100, 5, 0, 37164, 73242, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70956, 1, 0, 'We found somethin\' down there, somethin\' HUGE, and all of a sudden the rocks started fightin\' back and everyone went crazy. Bad business!', 0, 12, 0, 100, 0, 0, 37165, 73118, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70956, 2, 0, 'Please, just make it all stop!', 0, 12, 0, 100, 20, 0, 37166, 73119, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70956, 3, 0, 'Just what in the name of all that is unholy was that thing?', 0, 12, 0, 100, 6, 0, 37158, 73583, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70956, 4, 0, 'Oh don\'t you worry none, I don\'t think anyone wants to touch that thing again.  I\'ll collect our money and we are outta here.', 0, 12, 0, 100, 1, 0, 37159, 73584, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70956, 5, 0, 'Hey hey, what is this, huh?  This is barely a fifth of the promised payment!', 0, 12, 0, 100, 5, 0, 37160, 73585, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70956, 6, 0, 'Naw naw naw, see there\'s a contract.  Payment in full is req-', 0, 12, 0, 100, 274, 0, 37161, 73586, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70956, 7, 0, 'Yeah, we\'ll see who\'s got Garrosh\'s back when it counts.', 0, 12, 0, 100, 1, 0, 37162, 73587, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70956, 8, 0, 'Oh yeah, hey no nothing your, uh, your hugeness.  Uh, for the Horde, and all that...  Bye!', 0, 12, 0, 100, 1, 0, 37163, 73588, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70959, 0, 0, 'Minions, destroy these insects!', 0, 14, 0, 100, 0, 0, 0, 73933, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (70959, 1, 0, '|cFFFF0000|Hspell:141387|h[Urtharges\' Shield]|h|r has been broken!', 0, 41, 0, 100, 0, 0, 0, 73685, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71030, 0, 0, 'Proto-Drake Whelps hatch from the nearby eggs!', 2, 41, 0, 100, 0, 0, 0, 74028, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71030, 1, 0, 'Embercores wake from the lava!', 2, 41, 0, 100, 0, 0, 0, 74118, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71030, 2, 0, 'You will never escape these caverns!', 2, 14, 0, 100, 0, 0, 0, 73679, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71030, 3, 0, 'The true Horde, the pure Horde, cannot be stopped!', 2, 14, 0, 100, 0, 0, 0, 73680, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71030, 4, 0, 'Destroy them! FOR THE HORDE!', 2, 14, 0, 100, 0, 0, 0, 73682, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71030, 5, 0, '|TInterface\\Icons\\inv_misc_pocketwatch_01:20|t Bonus Objective Started: Defeat all enemies before time runs out!', 2, 41, 0, 100, 0, 0, 0, 74193, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71030, 6, 0, 'Fools! The true Horde cannot be stopped by the likes of you.', 2, 14, 0, 100, 0, 0, 0, 73838, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71095, 0, 0, 'Lei Shi\'s reflection ripples ominously.', 0, 41, 0, 100, 0, 0, 0, 62713, 'Leishi - Intro 1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71095, 1, 0, 'Her reflection ripples a second time.', 0, 41, 0, 100, 0, 0, 0, 62714, 'leishi - Intro 2');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71095, 2, 0, '%s appears!', 0, 41, 0, 100, 0, 0, 0, 62715, 'Leishi - Intro Done');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71107, 0, 0, 'Ah, the young black dragon graces my temple. The deeds of you and your champion are quickly becoming Pandaren legend.', 0, 12, 0, 100, 1, 0, 37347, 73478, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71107, 1, 0, 'Tell me, young prince: What is the nature of strength?', 0, 12, 0, 100, 1, 0, 37348, 73481, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71107, 2, 0, 'You speak only in terms of one\'s enemies.', 0, 12, 0, 100, 1, 0, 37349, 73483, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71107, 3, 0, 'How so?', 0, 12, 0, 100, 1, 0, 37350, 73485, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71107, 4, 0, 'A black dragon, through and through. Very well, I will give you my blessing, but also some advice:', 0, 12, 0, 100, 1, 0, 37351, 73487, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71107, 5, 0, 'Strength used in the service of others is twice as powerful as strength spent on our foes.', 0, 12, 0, 100, 1, 0, 37352, 73488, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71107, 6, 0, 'Now. My challenge, should you accept it, will test your ability to stand right in the face of danger and persevere.', 0, 12, 0, 100, 1, 0, 37353, 73489, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71107, 7, 0, 'Let the challenge begin! Hero, you must defeat the Black Prince in close-quarters combat.', 0, 12, 0, 100, 1, 0, 37343, 73508, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71107, 8, 0, '', 0, 12, 0, 100, 2, 0, 37371, 73766, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71107, 9, 0, 'Black Prince, you are very strong. But what good is strength without the vision to guide it?', 0, 12, 0, 100, 1, 0, 37345, 73510, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71108, 0, 0, 'I am looking forward to this: The White Tiger is the embodiment of strength in Pandaria!', 0, 12, 0, 100, 1, 0, 37385, 73470, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71108, 1, 0, 'Great White Tiger! Hear our plea.', 0, 12, 0, 100, 5, 0, 37434, 73477, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71108, 2, 0, 'My champion and I seek the blessing of your strength.', 0, 12, 0, 100, 1, 0, 37467, 73479, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71108, 3, 0, 'I take great pride in the company I keep.', 0, 12, 0, 100, 1, 0, 37435, 73480, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71108, 4, 0, 'The power to crush one\'s foes. That is strength.', 0, 12, 0, 100, 1, 0, 37436, 73482, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71108, 5, 0, 'Certainly, strength can benefit one\'s allies.', 0, 12, 0, 100, 273, 0, 37437, 73484, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71108, 6, 0, '...by using it to crush their enemies!', 0, 12, 0, 100, 5, 0, 37438, 73486, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71108, 7, 0, 'Wait - that\'s absurd. I would annihilate my own champion!', 0, 12, 0, 100, 274, 0, 37388, 73512, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71108, 8, 0, '... interesting. But the outcome is unlikely to change.', 0, 12, 0, 100, 6, 0, 37389, 73513, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71108, 9, 0, 'Very well. I am sorry, champion - But I will not be the one learning a lesson today.', 0, 12, 0, 100, 1, 0, 37390, 73514, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71108, 10, 0, 'A blind dragon. Ridiculous!', 0, 12, 0, 100, 1, 0, 37391, 73517, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71123, 0, 0, 'My blood hungers.', 0, 14, 0, 100, 0, 0, 37142, 73594, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71123, 1, 0, 'Your class cooldowns have been reset!', 0, 41, 0, 100, 0, 0, 0, 72391, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71123, 2, 0, 'I only sleep.', 0, 14, 0, 100, 0, 0, 37143, 73600, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71142, 0, 0, 'Here we are, the home of Niuzao, the Black Ox. He is the patron spirit of Pandaren fortitude, the only celestial who chooses to live beyond the wall.', 0, 12, 0, 100, 1, 0, 37387, 73524, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71142, 1, 0, 'Mighty Black Ox! Hear our plea.', 0, 12, 0, 100, 1, 0, 37409, 73525, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71142, 2, 0, 'My champion and I seek your blessing of fortitude.', 0, 12, 0, 100, 1, 0, 37410, 73526, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71142, 3, 0, 'The strength to overcome any hardship! This is fortitude.', 0, 12, 0, 100, 1, 0, 37411, 73527, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71142, 4, 0, 'You mean to say - physical strength is developed without, but fortitude comes from within?', 0, 12, 0, 100, 1, 0, 37412, 73528, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71142, 5, 0, '...Sure. I knew that.', 0, 12, 0, 100, 1, 0, 37413, 73529, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71144, 0, 0, 'So enters the dragon, and his mortal champion. You are welcome here.', 0, 12, 0, 100, 402, 0, 37274, 73530, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71144, 1, 0, 'Tell me, Black Prince: What is the nature of fortitude?', 0, 12, 0, 100, 402, 0, 37275, 73531, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71144, 2, 0, 'You confuse strength with fortitude, young dragon. But power is worthless without spirit!', 0, 12, 0, 100, 402, 0, 37276, 73532, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71144, 3, 0, 'I have seen humble slaves endure unimaginable torture, only to rise up and overthrow their masters.', 0, 12, 0, 100, 402, 0, 37277, 73533, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71144, 4, 0, 'And I have seen the mightiest of emperors laid low by the perseverance of the smallest of enemies.', 0, 12, 0, 100, 402, 0, 37278, 73534, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71144, 5, 0, 'Precisely!', 0, 12, 0, 100, 1, 0, 37279, 73535, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71144, 6, 0, 'Of course you did. We will put your understanding to the test.', 0, 12, 0, 100, 1, 0, 37280, 73536, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71144, 7, 0, 'My challenge, if you accept it, will test your champion\'s ability to defend and protect while enduring terrible hardship.', 0, 12, 0, 100, 402, 0, 37281, 73537, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71152, 0, 0, 'And then the Old One will remake this world for the Mantid!', 0, 14, 0, 100, 0, 0, 38677, 75072, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71152, 1, 0, 'Avenge me, brothers!', 0, 14, 0, 100, 0, 0, 38675, 75077, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71153, 0, 0, 'Come children of the Titans, you face the Paragons.', 0, 14, 0, 100, 0, 0, 38609, 75116, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71153, 1, 0, 'The hunter... becomes... the hunted....', 0, 14, 0, 100, 0, 0, 38607, 75122, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71154, 0, 0, 'We will slay you all as easily as we slew the Pandaren of old.', 0, 14, 0, 100, 0, 0, 38637, 75079, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71154, 1, 0, 'You move too slowly, how does your kind catch any prey?', 0, 14, 0, 100, 0, 0, 38634, 75080, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71154, 2, 0, 'Curse this chitinous shell, I was not fast enough!', 0, 14, 0, 100, 0, 0, 38635, 75088, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71155, 0, 0, 'We have endured the Usurpers, their children, the loss of the Old One, and soon... you.', 0, 14, 0, 100, 0, 0, 38670, 75099, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71155, 1, 0, 'You now face the Prime. Bow down to your superior!', 0, 14, 0, 100, 0, 0, 38667, 75100, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71155, 2, 0, 'Return me to the amber...', 0, 14, 0, 100, 0, 0, 38668, 75106, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71156, 0, 0, 'It matters not, the Old One will not suffer their intrusion.', 0, 14, 0, 100, 0, 0, 38646, 75107, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71156, 1, 0, 'Come little Kunchongs, it\'s feeding time!', 0, 14, 0, 100, 0, 0, 38790, 75108, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71156, 2, 0, 'Kovok... come to me... I need...', 0, 14, 0, 100, 0, 0, 38643, 75114, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71157, 0, 0, 'We Mantid serve a power far greater than they or their gods.', 0, 14, 0, 100, 0, 0, 38707, 75123, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71157, 1, 0, 'Have you come to try my new concoctions, Wakener?  I assure you these are nothing like you\'ve tasted before.', 0, 14, 0, 100, 0, 0, 38704, 75124, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71157, 2, 0, 'So many wonderful varieties, which concoction shall I give you?', 0, 14, 0, 100, 0, 0, 38709, 75125, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71157, 3, 0, 'I never discovered... how to put a stopper... in death...', 0, 14, 0, 100, 0, 0, 38705, 75129, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71158, 0, 0, 'They made it this far? I told you the others needed some micro-sonic genetic alterations.', 0, 14, 0, 100, 0, 0, 38916, 75136, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71158, 1, 0, 'Yes... new test subjects!', 0, 14, 0, 100, 0, 0, 38913, 75137, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71158, 2, 0, 'Perhaps I could have used... some enhancements... of my own...', 0, 14, 0, 100, 0, 0, 38914, 75142, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71160, 0, 0, 'How illogical, did they not hear your warning Kil\'ruk?', 0, 14, 0, 100, 0, 0, 38621, 75090, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71160, 1, 0, 'Chaos is order unrecognized by a lesser mind!', 0, 14, 0, 100, 0, 0, 38618, 75091, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71160, 2, 0, 'How about a game?', 0, 14, 0, 100, 0, 0, 38623, 75092, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71160, 3, 0, 'My calculations, incorrect? Perhaps another 800 years...', 0, 14, 0, 100, 0, 0, 38619, 75098, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71161, 0, 0, 'Look brothers, the Wakener is here!', 0, 14, 0, 100, 0, 0, 38657, 75130, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71161, 1, 0, 'I will carry you one last time Wakener... to your doom!', 0, 14, 0, 100, 0, 0, 38654, 75131, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71161, 2, 0, 'Well fought Wakener.  We will meet again...', 0, 14, 0, 100, 0, 0, 38655, 75135, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71166, 0, 0, 'The Red Crane of Hope has inspired the citizens of Pandaria from the very dawn of history. I very much want to speak with him.', 0, 12, 0, 100, 1, 0, 37386, 73550, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71166, 1, 0, 'Great Red Crane! Hear our plea.', 0, 12, 0, 100, 1, 0, 37392, 73551, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71166, 2, 0, 'My champion and I seek your blessing of hope.', 0, 12, 0, 100, 1, 0, 37393, 73552, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71166, 3, 0, 'Hope is... a belief in a better tomorrow.', 0, 12, 0, 100, 1, 0, 37394, 73553, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71166, 4, 0, '...Great Crane. You have not seen what I have seen.', 0, 12, 0, 100, 0, 0, 37395, 73554, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71166, 5, 0, '... the fires that once burned the sky will return. It is inevitable. The Burning Legion WILL find Azeroth.', 0, 12, 0, 100, 0, 0, 37396, 73555, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71166, 6, 0, 'Seas of blood, cities in ruin! Who are we - one divided world - to stand against a legion?', 0, 12, 0, 100, 0, 0, 37397, 73556, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71166, 7, 0, 'You speak of hope. Believe me, the thinnest sliver of belief that we might somehow survive the coming devastation is all that sustains me.', 0, 12, 0, 100, 0, 0, 37398, 73557, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71166, 8, 0, '... thank you, Great Crane.', 0, 12, 0, 100, 1, 0, 37399, 73558, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71169, 0, 0, 'My my, the child of the Worldbreaker, proof that none are beyond redemption. I am honored by your visit.', 0, 12, 0, 100, 1, 0, 37249, 73559, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71169, 1, 0, 'Tell me, son of the Earth-Warder: What is the nature of hope?', 0, 12, 0, 100, 1, 0, 37250, 73560, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71169, 2, 0, 'You speak, but doubt your own words.', 0, 12, 0, 100, 1, 0, 37251, 73561, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71169, 3, 0, 'You underestimate me.', 0, 12, 0, 100, 1, 0, 37252, 73562, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71169, 4, 0, 'Rise, son of Deathwing. I will give you my blessing, for you need it more than any I have ever met.', 0, 12, 0, 100, 1, 0, 37253, 73563, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71169, 5, 0, 'I challenge you not to think of hope as a vague and unimaginable future.', 0, 12, 0, 100, 1, 0, 37254, 73564, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71169, 6, 0, 'Live EVERY day with hope in your heart. In doing so, you CREATE the future you dream of.', 0, 12, 0, 100, 1, 0, 37255, 73565, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71169, 7, 0, 'My challenge, if you accept it, tasks you with healing the most grievous of wounds.', 0, 12, 0, 100, 1, 0, 37256, 73566, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71177, 0, 0, 'Step aside goblin.  This artifact is exactly what the warchief hoped to find here.  I am taking it under Kor\'kron protection.', 0, 12, 0, 100, 5, 0, 37182, 73601, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71177, 1, 0, 'You are paid for all workers present, foreman.  The warchief does not compensate the dead.', 0, 12, 0, 100, 1, 0, 37183, 73602, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71177, 2, 0, 'THIS.  This is what has diluted the vitality of our precious Horde!', 0, 12, 0, 100, 0, 0, 37184, 73603, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71177, 3, 0, 'The warchief graciously permits the families of the dead to live in his city.  To die in the service of the Horde should be a great honor, but you see it as another opportunity to line your filthy pockets!', 0, 12, 0, 100, 1, 0, 37185, 73604, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71177, 4, 0, 'Your kind DISGUSTS me.  Small.  Weak.  Honorless as thieves.  Be thankful the warchief still tolerates your presence at all.', 0, 12, 0, 100, 5, 0, 37186, 73605, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71177, 5, 0, 'What was that, goblin?', 0, 12, 0, 100, 547, 0, 37187, 73606, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71246, 0, 0, 'Containment failure.  Active defense matrix enabled.', 0, 14, 0, 100, 0, 0, 37205, 73589, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71246, 1, 0, 'Power units activated.', 0, 14, 0, 100, 0, 0, 37206, 73591, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71327, 0, 0, 'Avast, these Horde scallywags have made their way onto our ship.  Take care of them!', 2, 14, 0, 100, 0, 0, 0, 73954, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71327, 1, 0, 'Ah, so it\'s a duel you want?  Then a duel you shall have!', 2, 14, 0, 100, 0, 0, 0, 73186, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71327, 2, 0, 'FIRE!', 2, 14, 0, 100, 0, 0, 0, 73947, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71327, 3, 0, 'The Rapier has fallen to the ground.  Pick it up to engage Admiral Hagman!', 2, 41, 0, 100, 0, 0, 0, 73616, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71328, 0, 0, 'Watch out!', 2, 14, 0, 100, 0, 0, 0, 73950, '');
