INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71330, 2, 0, '%s begins to drink a |cFFFF2222|Hspell:141327|h[Volatile Concoction]|h|r.', 2, 41, 0, 100, 0, 0, 0, 73492, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71330, 3, 0, 'Let\'s see how this works!', 2, 14, 0, 100, 0, 0, 0, 73953, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71352, 0, 0, 'To do what is right, no matter the cost. That is wisdom.', 0, 12, 0, 100, 1, 0, 37450, 73746, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71352, 1, 0, 'When is it ever wise not to act for the greater good?', 0, 12, 0, 100, 1, 0, 37451, 73747, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71358, 0, 0, 'It\'s all clear then?', 0, 12, 0, 100, 0, 0, 0, 73958, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71358, 1, 0, 'No time to waste... let\'s blow this wall to pieces!', 0, 12, 0, 100, 0, 0, 0, 73965, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71358, 2, 0, 'That was some explosion!  Let\'s check it out.', 0, 12, 0, 100, 0, 0, 0, 73959, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71358, 3, 0, 'It looks like some sort of gigantic chest... probably full of gold.  And it\'s all ours!', 0, 12, 0, 100, 0, 0, 0, 73960, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71426, 0, 0, 'Welcome back, champion!', 0, 12, 0, 100, 1, 0, 37415, 74101, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71426, 1, 0, 'Excellent work with the celestials. Can you believe how chatty they were?', 0, 12, 0, 100, 1, 0, 37416, 74102, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71426, 2, 0, 'I just wanted them to hand us our reward, but they were just, "talk talk talk talk talk."', 0, 12, 0, 100, 1, 0, 37417, 74103, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71426, 3, 0, 'I\'m sorry - where were we? Oh yes! I have something for you:', 0, 12, 0, 100, 1, 0, 37418, 74104, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71426, 4, 0, 'The very spirits of Pandaria are supporting us in our grand crusade! Behold:', 0, 12, 0, 100, 1, 0, 37419, 74105, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71426, 5, 0, 'The strength of the White Tiger.', 0, 12, 0, 100, 25, 0, 37420, 74106, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71426, 6, 0, 'The wisdom of the Jade Serpent.', 0, 12, 0, 100, 1, 0, 37421, 74107, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71426, 7, 0, 'The fortitude of the Black Ox.', 0, 12, 0, 100, 1, 0, 37422, 74108, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71426, 8, 0, 'And from the Red Crane, a shining beacon of hope for a better world!', 0, 12, 0, 100, 1, 0, 37423, 74109, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71426, 9, 0, 'Our world.', 0, 12, 0, 100, 1, 0, 37424, 74110, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71426, 10, 0, 'There! It is ready.', 0, 12, 0, 100, 1, 0, 37425, 74111, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71436, 0, 0, 'These Alliance pigs are swinging aboard.  Fend them off!', 2, 14, 0, 100, 0, 0, 0, 73941, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71436, 1, 0, 'Victory is ours!  Get in the cannon and press the attack!', 2, 14, 0, 100, 0, 0, 0, 74129, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71445, 0, 0, 'Help us fend off these Alliance dogs!', 2, 14, 0, 100, 0, 0, 0, 74182, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71445, 1, 0, 'More are swinging over.  It looks like this is the last of \'em!', 2, 14, 0, 100, 0, 0, 0, 74183, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71445, 2, 0, 'We\'ve pushed them back to their ship.  Swing over and steal their explosives to blow it to smithereens!', 2, 14, 0, 100, 0, 0, 0, 73948, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71445, 3, 0, 'That\'s the last of them!  Get to the bottom of the ship and plant those explosives!', 2, 14, 0, 100, 0, 0, 0, 74135, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71445, 4, 0, 'The ship is about to explode.  Run to the top deck and swing away!', 2, 41, 0, 100, 0, 0, 0, 68909, '');
