INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71454, 0, 0, 'Nazgrim has failed us. Kor\'kron, destroy these intruders!', 0, 14, 0, 100, 0, 0, 38174, 75161, 'MALKOROK_INTRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71454, 1, 0, 'I will never fail the warchief!', 0, 14, 0, 100, 0, 0, 38172, 75162, 'MALKOROK_AGGRO');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71454, 2, 0, 'Garrosh gives me strength!', 0, 14, 0, 100, 0, 0, 38176, 75163, 'MALKOROK_ARCING_SMASH_3');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71454, 3, 0, 'Witness the might of the true Horde!', 0, 14, 0, 100, 0, 0, 38179, 75166, 'MALKOROK_BREATH_OF_YSHAARJ_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71454, 4, 0, 'The true Horde cannot be STOPPED!', 0, 14, 0, 100, 0, 0, 38181, 75168, 'MALKOROK_BLOOD_RAGE_1');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71454, 5, 0, 'The overwhelming power!', 0, 14, 0, 100, 0, 0, 38183, 75171, 'MALKOROK_BERSERK');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71454, 6, 0, 'Deploy the gear! Establish a perimeter!', 0, 14, 0, 100, 0, 0, 38184, 75544, 'MALKOROK_WIPE_WRONG');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71454, 7, 0, 'To die... for the warchief... is... an... honor...', 0, 14, 0, 100, 0, 0, 38173, 75170, 'MALKOROK_DEATH');
