INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71504, 1, 0, '|TInterface\\Icons\\inv_misc_sawblade_01.blp:20|t Siegecrafter Blackfuse faces you and readies |cFFFF0000|Hspell:143266|h[Launch Sawblade]|h|r!', 0, 42, 0, 100, 0, 0, 0, 74553, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71515, 0, 0, 'No alliance hand has ever struck me down. Do you think you are seasoned enough?', 0, 14, 0, 100, 0, 0, 38204, 75923, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71515, 1, 0, 'I am Nazgrim, fist of the Warchief and General of the Horde Army. If I must die today, I will die with honor, in battle, for the horde!', 0, 14, 0, 100, 0, 0, 38191, 75924, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71515, 2, 0, '|TInterface\\Icons\\ability_warrior_offensivestance.blp:20|t General Nazgrim assumes a |cFFFF0000|Hspell:143589|h[Battle Stance]|h|r!', 0, 41, 0, 100, 0, 0, 0, 74348, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71515, 3, 0, 'All Kor\'kron... under my command... kill them... NOW!', 0, 14, 0, 100, 0, 0, 38203, 74510, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71515, 4, 0, '|TInterface\\Icons\\ability_racial_avatar.blp:20|t General Nazgrim assumes a |cFFFF0000|Hspell:143594|h[Berserker Stance]|h|r!', 0, 41, 0, 100, 0, 0, 0, 74350, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71515, 5, 0, 'I die... with honor... Lok\'tar Ogar...', 0, 12, 0, 100, 0, 0, 38193, 75926, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71515, 6, 0, 'So, it has come to this. Together, we have learned and grown over the years, and now we find ourselves face to face on the battlefield. Do not think I will go easy on you, nor do I expect any quarter. What we do now, we do for the horde, both of us.', 0, 14, 0, 100, 0, 0, 38208, 75931, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71515, 7, 0, 'It is an honor to finally face you in combat. Lok\'tar Ogar, for Honor, and for the Horde!', 0, 14, 0, 100, 0, 0, 38192, 75932, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71515, 8, 0, 'Ah... you have learned much... and learned well... an honorable battle.', 0, 12, 0, 100, 0, 0, 38196, 75928, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71515, 9, 0, 'Kor\'kron, at my side!', 0, 14, 0, 100, 0, 0, 38198, 74505, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71515, 9, 1, 'Next squad, to the front!', 0, 14, 0, 100, 0, 0, 38200, 74507, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71515, 10, 0, '|TInterface\\Icons\\ability_warrior_defensivestance.blp:20|t General Nazgrim assumes a |cFFFF0000|Hspell:143593|h[Defensive Stance]|h|r!', 0, 41, 0, 100, 0, 0, 0, 74349, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71515, 11, 0, 'In the end, I stood by the warchief, because it was my duty, and I am glad that it was you who struck me down. May your strength... lead the horde... into a new era of prosperity...', 2, 12, 0, 100, 0, 0, 38197, 75929, '');
