INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 2, 1, 'We will cleanse this world in steel and fire!', 0, 14, 0, 100, 0, 0, 38070, 74708, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 2, 2, 'For the True Horde!', 0, 14, 0, 100, 0, 0, 38073, 74713, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 3, 0, 'Farseers, mend our wounds!', 0, 14, 0, 100, 0, 0, 38072, 74710, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 4, 0, 'Anger. Hatred. Fear!! They are weapons of war - the tools of a Warchief!', 0, 14, 0, 100, 0, 0, 38048, 74610, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 5, 0, 'Yes... yes I can see now...I can see the future of this world...a world ruled by the horde...MY horde.', 0, 14, 0, 100, 0, 0, 38051, 74616, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 6, 0, 'The Heart will be your end!', 0, 14, 0, 100, 0, 0, 38053, 74721, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 7, 0, 'The power flows through me!', 0, 14, 0, 100, 0, 0, 38076, 74714, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 8, 0, '|TInterface\\Icons\\ability_garrosh_whirling_corruption.blp:20|t%s begins to cast |cFFFF0000|Hspell:144985|h[Whirling Corruption]|h|r!', 0, 41, 0, 100, 0, 0, 0, 75753, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 1, 0, '|TInterface\\Icons\\ability_mage_firestarter.blp:20|t|cFFFF0000Siege Engineers|r appear in the wings and begin to cast |cFFFF0000|Hspell:144616|h[Power Iron Star]|h|r!', 0, 41, 0, 100, 0, 0, 0, 74586, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 10, 0, 'The power to crush your world, and everything on it!', 0, 14, 0, 100, 0, 0, 38077, 74715, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 11, 0, 'The True Horde WILL come to pass.  I have SEEN it.  IT HAS SHOWN ME. I HAVE SEEN MOUNTAINS OF SKULLS AND RIVERS OF BLOOD.  AND I WILL...HAVE...MY...WORLD!', 0, 14, 0, 100, 0, 0, 38056, 74724, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 12, 0, 'Ha! Do you remember nothing of Honor? Of glory on the battlefield?  You who would parlay with the humans, who allowed warlocks to practice their dark magics right under our feet.  You are weak.', 0, 14, 0, 100, 0, 0, 38059, 74701, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 13, 0, 'We are the Orcish Horde, the True Horde.  We die, bloody and thrashing on the field of battle, like true orcs SHOULD.', 0, 14, 0, 100, 0, 0, 38060, 74702, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 14, 0, 'You are an Orc no longer, and speak for none but yourself. You betrayed our people to forge your fragile alliances, and I will take great pleasure in tearing them apart.', 0, 14, 0, 100, 0, 0, 38061, 74703, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 15, 0, 'Fool. My Dark Shaman have twisted and tortured the elements for miles around. They cannot hear you now. Once again you prove too weak and powerless to do anything.', 0, 14, 0, 100, 0, 0, 38062, 74704, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 16, 0, 'So, you wish to face off against a real Orc Warchief.  So be it.', 0, 14, 0, 100, 0, 0, 38063, 74705, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 17, 0, 'You think you have WON?  You are BLIND.  I WILL FORCE YOUR EYES OPEN.', 0, 14, 0, 100, 0, 0, 38057, 75820, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 18, 0, 'BEHOLD, MY GLORIOUS DESTINY!', 0, 14, 0, 100, 0, 0, 38058, 75821, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 19, 0, '|TInterface\\Icons\\ability_warlock_eradication.blp:20|t%s casts |cFFFF0000|Hspell:147209|h[Malice]|h|r on |cFFFF0000$n|r!', 0, 41, 0, 100, 0, 0, 0, 75843, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 20, 0, 'THERE IS NO PLACE FOR YOU IN MY WORLD.', 0, 14, 0, 100, 0, 0, 38049, 75822, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 21, 0, '|TInterface\\Icons\\ability_garrosh_siege_engine.blp:20|t The Fleet Captain detected a cluster! Firing |cFFFF0000|Hspell:147047|h[Unstable Iron Star]|h|r!', 0, 41, 0, 100, 0, 0, 0, 75799, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 22, 0, '|TInterface\\Icons\\ability_vehicle_demolisherflamecatapult.blp:20|tThe Horde Fleet begins their |cFFFF0000|Hspell:147088|h[Bombardment]|h|r!', 0, 41, 0, 100, 0, 0, 0, 75795, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71865, 23, 0, 'I WILL DESTROY EVERYTHING YOU HAVE EVER LOVED.', 0, 14, 0, 100, 0, 0, 38050, 75823, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71869, 0, 0, 'Good deal. Welcome to the spirit world. Kinda. Let\'s check out the chests for any goodies, and see if you can find my body somewhere!', 0, 12, 0, 100, 0, 0, 0, 74489, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71869, 1, 0, 'I thought about askin\' her out but I didn\'t have the guts.', 0, 12, 0, 100, 0, 0, 0, 74486, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71869, 1, 1, 'What kind of spirit haunts a chicken coop? A poultry geist!', 0, 12, 0, 100, 0, 0, 0, 74467, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71869, 1, 2, 'I\'m not THAT snarky, am I?', 0, 12, 0, 100, 0, 0, 0, 74464, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71869, 1, 3, 'Want to hear somethin\' funny? Coccyx.', 0, 12, 0, 100, 0, 0, 0, 74476, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71869, 1, 4, 'Sorry for ribbin\' ya so much.', 0, 12, 0, 100, 0, 0, 0, 74473, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71869, 1, 5, 'Sticks and stones may break my bones...', 0, 12, 0, 100, 0, 0, 0, 74485, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71869, 1, 6, 'Who just called me a numbskull?', 0, 12, 0, 100, 0, 0, 0, 74471, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71869, 1, 7, 'I love you wit\' every bone in my body!', 0, 12, 0, 100, 0, 0, 0, 74482, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71869, 1, 8, 'I\'m not sure I have the stomach for this...', 0, 12, 0, 100, 0, 0, 0, 74487, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71869, 1, 9, 'I know my jokes ain\'t so good... I lost my funny bone!', 0, 12, 0, 100, 0, 0, 0, 74468, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71876, 0, 0, 'Hey, meat sack! If you got five minutes, come talk to me! I got no body!', 0, 12, 0, 100, 0, 0, 0, 74488, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71889, 0, 0, 'Activating GB-11010 "Armageddon"-class defense systems. Hey, does anybody know if we\'re zoned for this?', 0, 14, 0, 100, 0, 0, 38482, 75754, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71889, 1, 0, '"Armageddon"-class defense systems fully on-line. Here comes the explosions!', 0, 14, 0, 100, 0, 0, 38484, 75766, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71889, 2, 0, 'Module 1\'s all prepared for system reset.', 0, 14, 0, 100, 0, 0, 38474, 75757, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71889, 3, 0, 'Module 2\'s all prepared for system reset.', 0, 14, 0, 100, 0, 0, 38476, 75759, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71889, 4, 0, 'Security system deactivated.  Welcome back Siegecrafter Blackfuse.', 0, 14, 0, 100, 0, 0, 38478, 75761, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71889, 5, 0, 'Hey, we recording?  Yeah?  Okay.  Goblin-Titan control module starting up, please stand back.', 0, 14, 0, 100, 0, 0, 38479, 75750, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71889, 6, 0, 'Checking out the datas, hold on a second will ya.', 0, 14, 0, 100, 0, 0, 38480, 75751, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71889, 7, 0, 'Oh, now you did it, that was really really really not the correct input code.', 0, 14, 0, 100, 0, 0, 38481, 75752, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71952, 0, 0, 'Then let us begin.', 2, 14, 0, 100, 0, 0, 38024, 75898, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71952, 1, 0, 'When faced with challenges the like you have never seen, what do you hope for? What is the future you seek?', 2, 14, 0, 100, 0, 0, 38026, 75897, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71952, 2, 0, 'Your hope shines brightly, and even more brightly when you work together to overcome. It will ever light your way in even the darkest of places.', 2, 14, 0, 100, 0, 0, 38025, 75903, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71952, 3, 0, 'Do not give up on yourself!', 2, 14, 0, 100, 0, 0, 38027, 75902, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71952, 4, 0, 'Believe in one another, and let others carry hope for you!', 2, 14, 0, 100, 0, 0, 38028, 75899, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71952, 5, 0, 'Without hope, there is no brightness in the future!', 2, 14, 0, 100, 0, 0, 38029, 75900, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71952, 6, 0, 'Create the destiny you seek!', 2, 14, 0, 100, 0, 0, 38030, 75901, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71953, 0, 0, 'Ha ha! The trial commences!', 2, 14, 0, 100, 0, 0, 38211, 75912, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71953, 1, 0, 'Strength. It is far more than simple physical prowess. When you are truly tested, will you be able to tell the difference between strength and power?', 2, 14, 0, 100, 0, 0, 38213, 75911, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71953, 2, 0, 'You are strong, stronger even than you realize. Carry this thought with you into the darkness ahead, and let it shield you.', 2, 14, 0, 100, 0, 0, 38212, 75917, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71953, 3, 0, 'Return twice as powerful!', 2, 14, 0, 100, 0, 0, 38214, 75916, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71953, 4, 0, 'Believe in your strength!', 2, 14, 0, 100, 0, 0, 38215, 75913, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71953, 5, 0, 'You have the power to change your destiny!', 2, 14, 0, 100, 0, 0, 38216, 75914, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71953, 6, 0, 'Do not mistake the power that darkness offers for true strength!', 2, 14, 0, 100, 0, 0, 38217, 75915, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71954, 0, 0, 'Today, you will face many trainers, who have honed their skills and proven themselves.  Will your pets tell the same story about you?  We shall see!  Good luck!', 2, 14, 0, 100, 0, 0, 38032, 75311, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71954, 1, 0, 'Can you stand on the tallest peak, winds and sleet buffeting your skin, until the trees wither and the mountains fall into the sea?', 2, 14, 0, 100, 0, 0, 38757, 75890, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71954, 2, 0, 'Though you will be surrounded by foes greater than you can imagine, your fortitude shall allow you to endure. Remember this in the times ahead.', 2, 14, 0, 100, 0, 0, 38756, 75896, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71954, 3, 0, 'You must persevere!', 2, 14, 0, 100, 0, 0, 38758, 75895, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71954, 4, 0, 'The winds may be strong, and the sleet may sting.', 2, 14, 0, 100, 0, 0, 38759, 75892, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71954, 5, 0, 'You are the mountain, unmovable by all but time.', 2, 14, 0, 100, 0, 0, 38760, 75893, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71954, 6, 0, 'Be vigilent in your stand, or you will never achieve your goals.', 2, 14, 0, 100, 0, 0, 38761, 75894, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71955, 0, 0, 'The trial begins!', 2, 14, 0, 100, 0, 0, 38222, 75905, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71955, 1, 0, 'The lesson of true wisdom lies within you dear heroes, and in the choices you make. When faced with overwhelming darkness, will you make the right decisions?', 2, 14, 0, 100, 0, 0, 38224, 75904, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71955, 2, 0, 'Your wisdom has seen you through this trial. May it ever light your way out of dark places.', 2, 14, 0, 100, 0, 0, 38223, 75910, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71955, 3, 0, 'Learn from your mistakes!', 2, 14, 0, 100, 0, 0, 38225, 75909, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71955, 4, 0, 'Listen to your inner voice, and seek out the truth!', 2, 14, 0, 100, 0, 0, 38226, 75906, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71955, 5, 0, 'Do not let your judgement be clouded in trying times!', 2, 14, 0, 100, 0, 0, 38227, 75907, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71955, 6, 0, 'Always consider the consequences of your actions!', 2, 14, 0, 100, 0, 0, 38228, 75908, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71965, 13, 0, 'The light will not suffer your taint.', 0, 14, 0, 100, 0, 0, 38881, 75040, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71965, 0, 0, 'Halt!', 0, 14, 0, 100, 0, 0, 38883, 75026, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71965, 1, 0, 'No further corruption will enter the heart chamber!', 0, 14, 0, 100, 0, 0, 38884, 75027, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71965, 2, 0, 'You wish to purge the corruption?', 0, 14, 0, 100, 0, 0, 38885, 75028, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71965, 3, 0, 'Should you pass this door, at this time, you would fail!  You, all of you, are corrupted with the insiduous plague known as Pride.', 0, 14, 0, 100, 0, 0, 38886, 75029, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71965, 4, 0, 'You stand tall and proud atop your accomplishments, and this will be your downfall.', 0, 14, 0, 100, 0, 0, 0, 75159, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71965, 5, 0, 'Should you wish to defeat the corruption, you will first need to purify the corruption within yourselves.', 0, 14, 0, 100, 0, 0, 0, 75160, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71965, 6, 0, 'Speak to me again when you are prepared to face your inner demons.', 0, 14, 0, 100, 0, 0, 38887, 75030, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71965, 7, 0, 'Very well, I will create a field to keep your corruption quarantined.', 0, 14, 0, 100, 0, 0, 38888, 75031, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71965, 8, 0, 'Prove yourselves worthy and I will let you pass.', 0, 14, 0, 100, 0, 0, 38889, 75032, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71965, 9, 0, 'The light cleanses; but it is not gentle.  Gird yourselves and be purified!', 0, 14, 0, 100, 0, 0, 38880, 75034, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71965, 10, 0, 'You have been judged, and proven yourselves worthy. But be warned, what lies beyond will try your souls to their utmost.  When you are fully prepared, you may enter the chamber.', 0, 14, 0, 100, 0, 0, 38882, 74958, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71965, 11, 0, 'The corruption is amplifying.  The fragments must be purged before it becomes too great.', 0, 14, 0, 100, 0, 0, 38903, 74962, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71965, 12, 0, 'It did not matter. It comes.  Steel your hearts and prepare your souls.', 0, 14, 0, 100, 0, 0, 38904, 74963, '');
INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71967, 0, 0, 'You have been judged, and proven yourselves worthy. But be warned, what lies beyond will try your souls to their utmost.  When you are fully prepared, you may enter the chamber.', 0, 14, 0, 100, 0, 0, 38882, 74958, '');
