INSERT INTO `creature_text` (`CreatureID`, `groupid`, `id`, `text`, `TextRange`, `type`, `language`, `probability`, `emote`, `duration`, `sound`, `BroadcastTextId`, `comment`) VALUES (71967, 3, 0, 'You... must... contain... it...', 0, 14, 0, 100, 0, 0, 38902, 74964, '');
